import React from 'react';

/**
 * TextInput - Input tự động viết hoa chữ cái đầu
 * 
 * Props:
 * - value: string
 * - onChange: function(value)
 * - capitalize: 'none' | 'first' | 'sentence' | 'word' | 'all'
 *   + none: không tự động (mặc định)
 *   + first: Chữ cái đầu tiên
 *   + sentence: Chữ cái đầu mỗi câu (sau dấu .)
 *   + word: Chữ Cái Đầu Mỗi Từ
 *   + all: TẤT CẢ CHỮ HOA
 * - ...rest: các props khác của input
 * 
 * TẮT TẠM THỜI: Giữ phím Shift khi gõ
 */

const TextInput = ({ 
  value, 
  onChange, 
  capitalize = 'none',
  className = '',
  onKeyDown,
  ...rest 
}) => {
  const [tempDisable, setTempDisable] = React.useState(false);

  const handleChange = (e) => {
    let newValue = e.target.value;

    // Nếu đang giữ Shift → tạm tắt auto-capitalize
    if (capitalize !== 'none' && !tempDisable) {
      newValue = applyCapitalization(newValue, capitalize);
    }

    onChange(newValue);
  };

  const handleKeyDown = (e) => {
    // Giữ Shift → tắt tạm thời
    if (e.shiftKey && e.key !== 'Shift') {
      setTempDisable(true);
    } else {
      setTempDisable(false);
    }

    if (onKeyDown) onKeyDown(e);
  };

  const applyCapitalization = (text, mode) => {
    if (!text) return text;

    switch (mode) {
      case 'first':
        // Chữ cái đầu tiên viết hoa
        return text.charAt(0).toUpperCase() + text.slice(1);

      case 'sentence':
        // Chữ cái đầu mỗi câu (sau dấu .)
        return text.replace(/(^\w|[.!?]\s+\w)/g, (match) => match.toUpperCase());

      case 'word':
        // Chữ cái đầu mỗi từ
        return text.replace(/\b\w/g, (char) => char.toUpperCase());

      case 'all':
        // Tất cả chữ hoa
        return text.toUpperCase();

      default:
        return text;
    }
  };

  return (
    <input
      type="text"
      value={value}
      onChange={handleChange}
      onKeyDown={handleKeyDown}
      className={className}
      title={capitalize !== 'none' ? 'Giữ Shift khi gõ để tắt tự động viết hoa' : ''}
      {...rest}
    />
  );
};

export default TextInput;
