import React, { useState, useEffect } from 'react';
import { Save, Edit, Trash2, Plus, X, FileText, ShoppingCart } from 'lucide-react';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';

// ============================================================================
// MAIN COMPONENT
// ============================================================================

function SalesOrder() {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  // Form state
  const [form, setForm] = useState({
    so_don_hang: '',
    khach_hang_id: '',
    ngay_dat: new Date().toISOString().split('T')[0],
    ngay_giao_du_kien: '',
    chi_tiet: [],
    ghi_chu: ''
  });

  // Product selection modal
  const [isProductModalOpen, setIsProductModalOpen] = useState(false);
  const [productSearchTerm, setProductSearchTerm] = useState('');

  // ============================================================================
  // LOAD DATA
  // ============================================================================

  useEffect(() => {
    loadOrders();
    loadProducts();
    loadCustomers();
  }, []);

  const loadOrders = () => {
    const saved = localStorage.getItem('salesOrders');
    if (saved) {
      setOrders(JSON.parse(saved));
    }
  };

  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    if (saved) {
      setProducts(JSON.parse(saved));
    }
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    if (saved) {
      setCustomers(JSON.parse(saved));
    }
  };

  const saveOrders = (data) => {
    localStorage.setItem('salesOrders', JSON.stringify(data));
    setOrders(data);
  };

  // ============================================================================
  // FORM HANDLERS
  // ============================================================================

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.so_don_hang.trim()) {
      alert('Vui lòng nhập Số đơn hàng');
      return;
    }
    if (!form.khach_hang_id) {
      alert('Vui lòng chọn Khách hàng');
      return;
    }
    if (form.chi_tiet.length === 0) {
      alert('Vui lòng thêm ít nhất 1 sản phẩm');
      return;
    }

    const orderData = {
      ...form,
      id: editingId || Date.now(),
      tong_tien: form.chi_tiet.reduce((sum, item) => sum + item.thanh_tien, 0)
    };

    if (editingId) {
      saveOrders(orders.map(o => o.id === editingId ? orderData : o));
    } else {
      saveOrders([...orders, orderData]);
    }

    handleCloseForm();
  };

  const handleAddProduct = (product) => {
    const ktpo = `${product.po_dai}×${product.po_rong}×${product.po_cao}`;
    const ktsx = product.sx_dai 
      ? `${product.sx_dai}×${product.sx_rong}×${product.sx_cao}`
      : ktpo;

    const newItem = {
      id: Date.now(),
      thanh_pham_id: product.id,
      ma_hang: product.ma_hang,
      ten_san_pham: product.ten_san_pham,
      ktsx: ktsx,
      ktpo: ktpo,
      song: product.song,
      dvt: product.dvt,
      kieu: product.kieu,
      so_luong: 0,
      don_gia: product.don_gia,
      thanh_tien: 0,

      // Copy 13 công đoạn
      cong_doan: { ...product.cong_doan },

      la_thanh_phan_con: false,
      thanh_pham_me_id: null
    };

    setForm({
      ...form,
      chi_tiet: [...form.chi_tiet, newItem]
    });

    setIsProductModalOpen(false);
    setProductSearchTerm('');
  };

  const handleUpdateQuantity = (itemId, quantity) => {
    const qty = parseFloat(quantity) || 0;

    setForm({
      ...form,
      chi_tiet: form.chi_tiet.map(item => {
        if (item.id === itemId) {
          return {
            ...item,
            so_luong: qty,
            thanh_tien: qty * item.don_gia
          };
        }
        return item;
      })
    });
  };

  const handleRemoveItem = (itemId) => {
    setForm({
      ...form,
      chi_tiet: form.chi_tiet.filter(item => item.id !== itemId)
    });
  };

  const handleEdit = (order) => {
    setForm(order);
    setEditingId(order.id);
    setIsFormOpen(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Bạn có chắc muốn xóa đơn hàng này?')) {
      saveOrders(orders.filter(o => o.id !== id));
    }
  };

  const handleCloseForm = () => {
    setForm({
      so_don_hang: '',
      khach_hang_id: '',
      ngay_dat: new Date().toISOString().split('T')[0],
      ngay_giao_du_kien: '',
      chi_tiet: [],
      ghi_chu: ''
    });
    setEditingId(null);
    setIsFormOpen(false);
  };

  // ============================================================================
  // FILTER
  // ============================================================================

  const filteredOrders = orders.filter(o => {
    const searchLower = searchTerm.toLowerCase();
    const customer = customers.find(c => c.id === o.khach_hang_id);
    return (
      o.so_don_hang.toLowerCase().includes(searchLower) ||
      (customer?.ten_khach_hang || '').toLowerCase().includes(searchLower)
    );
  });

  const filteredProducts = products.filter(p => {
    const searchLower = productSearchTerm.toLowerCase();
    const customer = customers.find(c => c.id === p.khach_hang_id);

    // Filter by selected customer if exists
    if (form.khach_hang_id && p.khach_hang_id !== form.khach_hang_id) {
      return false;
    }

    return (
      p.ma_hang.toLowerCase().includes(searchLower) ||
      p.ten_san_pham.toLowerCase().includes(searchLower) ||
      (customer?.ten_khach_hang || '').toLowerCase().includes(searchLower)
    );
  });

  // ============================================================================
  // RENDER
  // ============================================================================

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">Quản lý Đơn hàng Xuất</h1>
        <button
          onClick={() => setIsFormOpen(true)}
          className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
        >
          <Plus className="w-5 h-5" />
          Tạo đơn hàng mới
        </button>
      </div>

      {/* Search */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Tìm kiếm theo số đơn hàng, khách hàng..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg"
        />
      </div>

      {/* Orders List */}
      <div className="space-y-4">
        {filteredOrders.map(order => {
          const customer = customers.find(c => c.id === order.khach_hang_id);

          return (
            <div key={order.id} className="bg-white rounded-lg shadow overflow-hidden">
              {/* Order Header */}
              <div className="p-4 bg-gray-50 border-b flex justify-between items-center">
                <div className="flex items-center gap-4">
                  <ShoppingCart className="w-6 h-6 text-green-600" />
                  <div>
                    <h3 className="font-bold text-lg text-gray-800">{order.so_don_hang}</h3>
                    <p className="text-sm text-gray-600">
                      Khách hàng: {customer?.ten_khach_hang || 'N/A'} | 
                      Ngày đặt: {new Date(order.ngay_dat).toLocaleDateString('vi-VN')} |
                      Giao dự kiến: {new Date(order.ngay_giao_du_kien).toLocaleDateString('vi-VN')}
                    </p>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <div className="text-right">
                    <p className="text-sm text-gray-600">Tổng tiền</p>
                    <p className="text-xl font-bold text-green-600">
                      {order.tong_tien.toLocaleString('vi-VN')}đ
                    </p>
                  </div>
                  <button
                    onClick={() => handleEdit(order)}
                    className="p-2 text-blue-600 hover:bg-blue-50 rounded"
                  >
                    <Edit className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => handleDelete(order.id)}
                    className="p-2 text-red-600 hover:bg-red-50 rounded"
                  >
                    <Trash2 className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Order Items - 22 COLUMNS TABLE */}
              <div className="overflow-x-auto">
                <table className="w-full text-sm">
                  <thead className="bg-gray-100 border-b">
                    <tr>
                      {/* 9 CỘT CƠ BẢN */}
                      <th className="sticky left-0 bg-gray-100 px-3 py-2 text-left font-semibold text-gray-700 border-r">Mã hàng</th>
                      <th className="px-3 py-2 text-left font-semibold text-gray-700">Tên SP</th>
                      <th className="px-3 py-2 text-left font-semibold text-gray-700">KTSX</th>
                      <th className="px-3 py-2 text-left font-semibold text-gray-700">KTPO</th>
                      <th className="px-3 py-2 text-left font-semibold text-gray-700">Sóng</th>
                      <th className="px-3 py-2 text-left font-semibold text-gray-700">Kiểu</th>
                      <th className="px-3 py-2 text-right font-semibold text-gray-700">SL</th>
                      <th className="px-3 py-2 text-right font-semibold text-gray-700">Đơn giá</th>
                      <th className="px-3 py-2 text-right font-semibold text-gray-700 border-r">Thành tiền</th>

                      {/* 13 CỘT CÔNG ĐOẠN */}
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Xả</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Mã phim</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Số màu</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">In</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Bồi</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Cán màng</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Mã khuôn</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Bế</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Chạp</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Đóng</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Dán</th>
                      <th className="px-3 py-2 text-center font-semibold text-gray-700 bg-blue-50">Khác</th>
                      <th className="px-3 py-2 text-left font-semibold text-gray-700 bg-blue-50">Ghi chú Khác</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y">
                    {order.chi_tiet.map(item => (
                      <tr key={item.id} className="hover:bg-gray-50">
                        {/* 9 CỘT CƠ BẢN */}
                        <td className="sticky left-0 bg-white px-3 py-2 font-medium text-blue-600 border-r">
                          {item.ma_hang}
                        </td>
                        <td className="px-3 py-2 text-gray-700">{item.ten_san_pham}</td>
                        <td className="px-3 py-2 text-gray-600 whitespace-nowrap">{item.ktsx}</td>
                        <td className="px-3 py-2 text-gray-600 whitespace-nowrap">{item.ktpo}</td>
                        <td className="px-3 py-2">
                          <span className="px-2 py-1 bg-gray-100 rounded text-xs">
                            {item.song}
                          </span>
                        </td>
                        <td className="px-3 py-2">
                          <span className="px-2 py-1 bg-blue-100 text-blue-700 rounded text-xs">
                            {item.kieu}
                          </span>
                        </td>
                        <td className="px-3 py-2 text-right font-medium">
                          {item.so_luong.toLocaleString('vi-VN')}
                        </td>
                        <td className="px-3 py-2 text-right text-gray-600">
                          {item.don_gia.toLocaleString('vi-VN')}
                        </td>
                        <td className="px-3 py-2 text-right font-semibold text-green-600 border-r">
                          {item.thanh_tien.toLocaleString('vi-VN')}
                        </td>

                        {/* 13 CỘT CÔNG ĐOẠN */}
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.xa || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.ma_phim ? (
                            <span className="px-2 py-1 bg-purple-100 text-purple-700 rounded text-xs font-medium">
                              {item.cong_doan.ma_phim}
                            </span>
                          ) : '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.so_mau || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.in || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.boi || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.can_mang || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.ma_khuon ? (
                            <span className="px-2 py-1 bg-indigo-100 text-indigo-700 rounded text-xs font-medium">
                              {item.cong_doan.ma_khuon}
                            </span>
                          ) : '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.be || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.chap || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.dong || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.dan || '-'}
                        </td>
                        <td className="px-3 py-2 text-center bg-blue-50">
                          {item.cong_doan.khac || '-'}
                        </td>
                        <td className="px-3 py-2 text-left bg-blue-50 text-gray-600 text-xs">
                          {item.cong_doan.khac_ghi_chu || '-'}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {order.ghi_chu && (
                <div className="p-4 bg-gray-50 border-t">
                  <p className="text-sm text-gray-600">
                    <span className="font-semibold">Ghi chú:</span> {order.ghi_chu}
                  </p>
                </div>
              )}
            </div>
          );
        })}

        {filteredOrders.length === 0 && (
          <div className="text-center py-12 text-gray-500 bg-white rounded-lg shadow">
            Chưa có đơn hàng nào
          </div>
        )}
      </div>

      {/* Form Modal */}
      {isFormOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-start justify-center p-4 overflow-y-auto z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-6xl w-full my-8">
            {/* Form Header */}
            <div className="flex justify-between items-center p-6 border-b">
              <h2 className="text-2xl font-bold text-gray-800">
                {editingId ? 'Sửa đơn hàng' : 'Tạo đơn hàng mới'}
              </h2>
              <button onClick={handleCloseForm} className="text-gray-400 hover:text-gray-600">
                <X className="w-6 h-6" />
              </button>
            </div>

            {/* Form Body */}
            <form onSubmit={handleSubmit} className="p-6 space-y-6">
              {/* Thông tin đơn hàng */}
              <div className="grid grid-cols-2 gap-4">
                <TextInput
                  label="Số đơn hàng *"
                  value={form.so_don_hang}
                  onChange={(value) => setForm({ ...form, so_don_hang: value })}
                  placeholder="VD: DH2512001"
                />

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Khách hàng *
                  </label>
                  <select
                    value={form.khach_hang_id}
                    onChange={(e) => setForm({ ...form, khach_hang_id: e.target.value })}
                    className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-green-500"
                    required
                  >
                    <option value="">-- Chọn khách hàng --</option>
                    {customers.map(c => (
                      <option key={c.id} value={c.id}>{c.ten_khach_hang}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Ngày đặt</label>
                  <input
                    type="date"
                    value={form.ngay_dat}
                    onChange={(e) => setForm({ ...form, ngay_dat: e.target.value })}
                    className="w-full px-3 py-2 border rounded-lg"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Ngày giao dự kiến</label>
                  <input
                    type="date"
                    value={form.ngay_giao_du_kien}
                    onChange={(e) => setForm({ ...form, ngay_giao_du_kien: e.target.value })}
                    className="w-full px-3 py-2 border rounded-lg"
                  />
                </div>
              </div>

              {/* Chi tiết sản phẩm */}
              <div className="border-t pt-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-gray-700">Chi tiết sản phẩm</h3>
                  <button
                    type="button"
                    onClick={() => setIsProductModalOpen(true)}
                    className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                    disabled={!form.khach_hang_id}
                  >
                    <Plus className="w-4 h-4" />
                    Thêm sản phẩm
                  </button>
                </div>

                {!form.khach_hang_id && (
                  <p className="text-sm text-amber-600 mb-4">
                    * Vui lòng chọn khách hàng trước khi thêm sản phẩm
                  </p>
                )}

                {/* Products Table */}
                {form.chi_tiet.length > 0 && (
                  <div className="overflow-x-auto border rounded-lg">
                    <table className="w-full text-sm">
                      <thead className="bg-gray-100">
                        <tr>
                          <th className="px-3 py-2 text-left font-semibold">Mã hàng</th>
                          <th className="px-3 py-2 text-left font-semibold">Tên SP</th>
                          <th className="px-3 py-2 text-left font-semibold">KTSX</th>
                          <th className="px-3 py-2 text-left font-semibold">Sóng</th>
                          <th className="px-3 py-2 text-left font-semibold">Kiểu</th>
                          <th className="px-3 py-2 text-right font-semibold">SL</th>
                          <th className="px-3 py-2 text-right font-semibold">Đơn giá</th>
                          <th className="px-3 py-2 text-right font-semibold">Thành tiền</th>
                          <th className="px-3 py-2 text-center font-semibold"></th>
                        </tr>
                      </thead>
                      <tbody className="divide-y">
                        {form.chi_tiet.map(item => (
                          <tr key={item.id} className="hover:bg-gray-50">
                            <td className="px-3 py-2 font-medium text-blue-600">{item.ma_hang}</td>
                            <td className="px-3 py-2">{item.ten_san_pham}</td>
                            <td className="px-3 py-2 text-gray-600">{item.ktsx}</td>
                            <td className="px-3 py-2">
                              <span className="px-2 py-1 bg-gray-100 rounded text-xs">
                                {item.song}
                              </span>
                            </td>
                            <td className="px-3 py-2">
                              <span className="px-2 py-1 bg-blue-100 text-blue-700 rounded text-xs">
                                {item.kieu}
                              </span>
                            </td>
                            <td className="px-3 py-2">
                              <input
                                type="number"
                                value={item.so_luong}
                                onChange={(e) => handleUpdateQuantity(item.id, e.target.value)}
                                className="w-24 px-2 py-1 border rounded text-right"
                                min="0"
                              />
                            </td>
                            <td className="px-3 py-2 text-right text-gray-600">
                              {item.don_gia.toLocaleString('vi-VN')}
                            </td>
                            <td className="px-3 py-2 text-right font-semibold text-green-600">
                              {item.thanh_tien.toLocaleString('vi-VN')}
                            </td>
                            <td className="px-3 py-2 text-center">
                              <button
                                type="button"
                                onClick={() => handleRemoveItem(item.id)}
                                className="p-1 text-red-600 hover:bg-red-50 rounded"
                              >
                                <X className="w-4 h-4" />
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                      <tfoot className="bg-gray-50 border-t-2">
                        <tr>
                          <td colSpan="7" className="px-3 py-2 text-right font-bold">
                            Tổng tiền:
                          </td>
                          <td className="px-3 py-2 text-right font-bold text-green-600 text-lg">
                            {form.chi_tiet.reduce((sum, item) => sum + item.thanh_tien, 0).toLocaleString('vi-VN')}đ
                          </td>
                          <td></td>
                        </tr>
                      </tfoot>
                    </table>
                  </div>
                )}

                {form.chi_tiet.length === 0 && (
                  <div className="text-center py-8 text-gray-500 border rounded-lg">
                    Chưa có sản phẩm nào. Nhấn "Thêm sản phẩm" để bắt đầu.
                  </div>
                )}
              </div>

              {/* Ghi chú */}
              <TextArea
                label="Ghi chú"
                value={form.ghi_chu}
                onChange={(value) => setForm({ ...form, ghi_chu: value })}
                placeholder="Ghi chú về đơn hàng..."
                rows={3}
              />

              {/* Form Footer */}
              <div className="flex justify-end gap-3 pt-4 border-t">
                <button
                  type="button"
                  onClick={handleCloseForm}
                  className="px-6 py-2 border rounded-lg hover:bg-gray-50"
                >
                  Hủy
                </button>
                <button
                  type="submit"
                  className="flex items-center gap-2 px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                >
                  <Save className="w-5 h-5" />
                  {editingId ? 'Cập nhật' : 'Lưu đơn hàng'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Product Selection Modal */}
      {isProductModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[80vh] overflow-hidden">
            <div className="flex justify-between items-center p-4 border-b">
              <h3 className="text-xl font-bold text-gray-800">Chọn sản phẩm</h3>
              <button
                onClick={() => {
                  setIsProductModalOpen(false);
                  setProductSearchTerm('');
                }}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="w-6 h-6" />
              </button>
            </div>

            <div className="p-4">
              <input
                type="text"
                placeholder="Tìm kiếm sản phẩm..."
                value={productSearchTerm}
                onChange={(e) => setProductSearchTerm(e.target.value)}
                className="w-full px-4 py-2 border rounded-lg mb-4"
                autoFocus
              />

              <div className="overflow-y-auto max-h-96">
                <table className="w-full text-sm">
                  <thead className="bg-gray-50 sticky top-0">
                    <tr>
                      <th className="px-3 py-2 text-left font-semibold">Mã hàng</th>
                      <th className="px-3 py-2 text-left font-semibold">Tên SP</th>
                      <th className="px-3 py-2 text-left font-semibold">KTSX</th>
                      <th className="px-3 py-2 text-left font-semibold">Sóng</th>
                      <th className="px-3 py-2 text-left font-semibold">Kiểu</th>
                      <th className="px-3 py-2 text-right font-semibold">Đơn giá</th>
                      <th className="px-3 py-2 text-center font-semibold"></th>
                    </tr>
                  </thead>
                  <tbody className="divide-y">
                    {filteredProducts.map(product => {
                      const ktsx = product.sx_dai 
                        ? `${product.sx_dai}×${product.sx_rong}×${product.sx_cao}`
                        : `${product.po_dai}×${product.po_rong}×${product.po_cao}`;

                      const alreadyAdded = form.chi_tiet.some(item => item.thanh_pham_id === product.id);

                      return (
                        <tr key={product.id} className="hover:bg-gray-50">
                          <td className="px-3 py-2 font-medium text-blue-600">{product.ma_hang}</td>
                          <td className="px-3 py-2">{product.ten_san_pham}</td>
                          <td className="px-3 py-2 text-gray-600">{ktsx}</td>
                          <td className="px-3 py-2">
                            <span className="px-2 py-1 bg-gray-100 rounded text-xs">
                              {product.song}
                            </span>
                          </td>
                          <td className="px-3 py-2">
                            <span className="px-2 py-1 bg-blue-100 text-blue-700 rounded text-xs">
                              {product.kieu}
                            </span>
                          </td>
                          <td className="px-3 py-2 text-right">
                            {product.don_gia.toLocaleString('vi-VN')}đ
                          </td>
                          <td className="px-3 py-2 text-center">
                            <button
                              onClick={() => handleAddProduct(product)}
                              disabled={alreadyAdded}
                              className={`px-3 py-1 rounded text-sm ${
                                alreadyAdded
                                  ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                                  : 'bg-blue-600 text-white hover:bg-blue-700'
                              }`}
                            >
                              {alreadyAdded ? 'Đã thêm' : 'Chọn'}
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>

                {filteredProducts.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    Không tìm thấy sản phẩm phù hợp
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SalesOrder;
