import React, { useState, useEffect } from 'react';
import { X, Save, Edit, Package } from 'lucide-react';

/**
 * ProductDetailModal - MODAL XEM/SỬA THÀNH PHẨM
 * 
 * Mở từ SalesOrder để xem/sửa product
 * Không cần chuyển trang
 */

const ProductDetailModal = ({ 
  productId, 
  isOpen, 
  onClose,
  onSave 
}) => {
  const [product, setProduct] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [form, setForm] = useState({
    ma_hang: '',
    ten_san_pham: '',
    don_gia: '',
    song: '',
    dvt: '',
    sx_dai: '',
    sx_rong: '',
    sx_cao: '',
    ghi_chu: ''
  });

  // Load product data
  useEffect(() => {
    if (isOpen && productId) {
      const products = JSON.parse(localStorage.getItem('products') || '[]');
      const found = products.find(p => 
        p.id === productId || 
        p.id === parseInt(productId) ||
        p.ma_hang === productId
      );

      if (found) {
        setProduct(found);
        setForm({
          ma_hang: found.ma_hang || '',
          ten_san_pham: found.ten_san_pham || '',
          don_gia: found.don_gia || '',
          song: found.song || '',
          dvt: found.dvt || '',
          sx_dai: found.sx_dai || found.po_dai || '',
          sx_rong: found.sx_rong || found.po_rong || '',
          sx_cao: found.sx_cao || found.po_cao || '',
          ghi_chu: found.ghi_chu || ''
        });
      }
    }
  }, [isOpen, productId]);

  // Handle save
  const handleSave = () => {
    if (!product) return;

    const products = JSON.parse(localStorage.getItem('products') || '[]');
    const updated = products.map(p => 
      p.id === product.id 
        ? { 
            ...p,
            ma_hang: form.ma_hang,
            ten_san_pham: form.ten_san_pham,
            don_gia: parseFloat(form.don_gia) || 0,
            song: form.song,
            dvt: form.dvt,
            sx_dai: parseFloat(form.sx_dai) || 0,
            sx_rong: parseFloat(form.sx_rong) || 0,
            sx_cao: parseFloat(form.sx_cao) || 0,
            ghi_chu: form.ghi_chu
          }
        : p
    );

    localStorage.setItem('products', JSON.stringify(updated));

    if (onSave) {
      onSave(updated.find(p => p.id === product.id));
    }

    setIsEditing(false);
    alert('Đã cập nhật thành phẩm!');
  };

  // Handle close
  const handleClose = () => {
    setIsEditing(false);
    if (onClose) onClose();
  };

  if (!isOpen || !product) return null;

  const ktsx = `${form.sx_dai || 0}×${form.sx_rong || 0}${form.sx_cao ? `×${form.sx_cao}` : ''}`;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="sticky top-0 bg-white border-b px-6 py-4 flex items-center justify-between z-10">
          <div>
            <h2 className="text-xl font-bold text-blue-900 flex items-center gap-2">
              <Package className="w-6 h-6" />
              Chi tiết thành phẩm
            </h2>
            <p className="text-sm text-gray-600">Mã: {product.ma_hang}</p>
          </div>
          <div className="flex gap-2">
            {!isEditing && (
              <button
                onClick={() => setIsEditing(true)}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
              >
                <Edit className="w-4 h-4" />
                Sửa
              </button>
            )}
            <button
              onClick={handleClose}
              className="p-2 hover:bg-gray-100 rounded-lg"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="p-6 space-y-4">
          {/* Mã hàng */}
          <div>
            <label className="block text-sm font-medium mb-2">Mã hàng</label>
            {isEditing ? (
              <input
                type="text"
                value={form.ma_hang}
                onChange={(e) => setForm({ ...form, ma_hang: e.target.value.toUpperCase() })}
                className="w-full px-4 py-2 border rounded-lg uppercase"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg font-medium">
                {product.ma_hang}
              </div>
            )}
          </div>

          {/* Tên sản phẩm */}
          <div>
            <label className="block text-sm font-medium mb-2">Tên sản phẩm *</label>
            {isEditing ? (
              <input
                type="text"
                value={form.ten_san_pham}
                onChange={(e) => setForm({ ...form, ten_san_pham: e.target.value })}
                className="w-full px-4 py-2 border rounded-lg"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg font-medium">
                {product.ten_san_pham}
              </div>
            )}
          </div>

          {/* KTSX */}
          <div>
            <label className="block text-sm font-medium mb-2">Kích thước sản xuất (KTSX)</label>
            {isEditing ? (
              <div className="grid grid-cols-3 gap-2">
                <div>
                  <label className="text-xs text-gray-600">Dài</label>
                  <input
                    type="number"
                    value={form.sx_dai}
                    onChange={(e) => setForm({ ...form, sx_dai: e.target.value })}
                    className="w-full px-3 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="text-xs text-gray-600">Rộng</label>
                  <input
                    type="number"
                    value={form.sx_rong}
                    onChange={(e) => setForm({ ...form, sx_rong: e.target.value })}
                    className="w-full px-3 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="text-xs text-gray-600">Cao</label>
                  <input
                    type="number"
                    value={form.sx_cao}
                    onChange={(e) => setForm({ ...form, sx_cao: e.target.value })}
                    className="w-full px-3 py-2 border rounded-lg"
                  />
                </div>
              </div>
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg">
                {ktsx}
              </div>
            )}
          </div>

          {/* Đơn giá & Sóng & ĐVT */}
          <div className="grid grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2">Đơn giá</label>
              {isEditing ? (
                <input
                  type="number"
                  value={form.don_gia}
                  onChange={(e) => setForm({ ...form, don_gia: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                />
              ) : (
                <div className="px-4 py-2 bg-gray-50 rounded-lg">
                  {(product.don_gia || 0).toLocaleString('vi-VN')}đ
                </div>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium mb-2">Sóng</label>
              {isEditing ? (
                <select
                  value={form.song}
                  onChange={(e) => setForm({ ...form, song: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                >
                  <option value="">--</option>
                  <option value="B">B</option>
                  <option value="BC">BC</option>
                  <option value="E">E</option>
                  <option value="BE">BE</option>
                  <option value="C">C</option>
                </select>
              ) : (
                <div className="px-4 py-2 bg-gray-50 rounded-lg">
                  {product.song || '-'}
                </div>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium mb-2">Đơn vị</label>
              {isEditing ? (
                <select
                  value={form.dvt}
                  onChange={(e) => setForm({ ...form, dvt: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                >
                  <option value="">--</option>
                  <option value="Cái">Cái</option>
                  <option value="Bộ">Bộ</option>
                  <option value="Hộp">Hộp</option>
                  <option value="Tờ">Tờ</option>
                </select>
              ) : (
                <div className="px-4 py-2 bg-gray-50 rounded-lg">
                  {product.dvt || '-'}
                </div>
              )}
            </div>
          </div>

          {/* Ghi chú */}
          <div>
            <label className="block text-sm font-medium mb-2">Ghi chú</label>
            {isEditing ? (
              <textarea
                value={form.ghi_chu}
                onChange={(e) => setForm({ ...form, ghi_chu: e.target.value })}
                className="w-full px-4 py-2 border rounded-lg"
                rows="3"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg min-h-[80px]">
                {product.ghi_chu || '-'}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="border-t px-6 py-4 flex justify-end gap-2">
          {isEditing ? (
            <>
              <button
                onClick={() => {
                  setIsEditing(false);
                  // Reset form
                  setForm({
                    ma_hang: product.ma_hang || '',
                    ten_san_pham: product.ten_san_pham || '',
                    don_gia: product.don_gia || '',
                    song: product.song || '',
                    dvt: product.dvt || '',
                    sx_dai: product.sx_dai || product.po_dai || '',
                    sx_rong: product.sx_rong || product.po_rong || '',
                    sx_cao: product.sx_cao || product.po_cao || '',
                    ghi_chu: product.ghi_chu || ''
                  });
                }}
                className="px-4 py-2 border rounded-lg hover:bg-gray-50"
              >
                Hủy
              </button>
              <button
                onClick={handleSave}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
              >
                <Save className="w-4 h-4" />
                Lưu
              </button>
            </>
          ) : (
            <button
              onClick={handleClose}
              className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700"
            >
              Đóng
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetailModal;
