import React, { useState, useEffect, useRef } from 'react';
import { Plus, Search, Edit, Trash2, Eye, X, ShoppingCart } from 'lucide-react';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';

// ============================================================================
// HELPER FUNCTIONS
// ============================================================================

// Tạo số đơn hàng tự động: DHXyyyymmxxx
const generateSoDonHang = (existingOrders) => {
  const now = new Date();
  const year = now.getFullYear();
  const month = String(now.getMonth() + 1).padStart(2, '0');
  const prefix = `DHX${year}${month}`;

  // Tìm số thứ tự lớn nhất trong tháng hiện tại
  const currentMonthOrders = existingOrders.filter(o => 
    o.so_don_hang?.startsWith(prefix)
  );

  let maxNum = 0;
  currentMonthOrders.forEach(o => {
    const num = parseInt(o.so_don_hang.slice(-3));
    if (!isNaN(num) && num > maxNum) {
      maxNum = num;
    }
  });

  const newNum = String(maxNum + 1).padStart(3, '0');
  return `${prefix}${newNum}`;
};

// Lấy tên khách hàng
const getCustomerName = (customerId, customers) => {
  const customer = customers.find(c => c.id === customerId);
  return customer ? customer.ten_khach_hang : '';
};

// Lấy mã khách hàng
const getCustomerCode = (customerId, customers) => {
  const customer = customers.find(c => c.id === customerId);
  return customer ? customer.ma_khach_hang : '';
};

// ============================================================================
// MAIN COMPONENT
// ============================================================================

function SalesOrder() {
  const loadOrders = () => {
    const saved = localStorage.getItem('salesOrders');
    return saved ? JSON.parse(saved) : [];
  };

  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    return saved ? JSON.parse(saved) : [];
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    return saved ? JSON.parse(saved) : [];
  };

  const [orders, setOrders] = useState(loadOrders);
  const [products, setProducts] = useState(loadProducts);
  const [customers, setCustomers] = useState(loadCustomers);
  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingOrder, setEditingOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showProductModal, setShowProductModal] = useState(false);
  const [productSearchTerm, setProductSearchTerm] = useState('');

  // Ref cho auto focus số lượng
  const quantityInputRefs = useRef({});

  const [form, setForm] = useState({
    so_don_hang: '',
    khach_hang_id: '',
    ngay_dat: new Date().toISOString().split('T')[0],
    ngay_giao_du_kien: '',
    chi_tiet: [],
    ghi_chu: ''
  });

  useEffect(() => {
    localStorage.setItem('salesOrders', JSON.stringify(orders));
  }, [orders]);

  useEffect(() => {
    // Reload products và customers khi component mount
    setProducts(loadProducts());
    setCustomers(loadCustomers());
  }, []);

  // ============================================================================
  // FORM HANDLERS
  // ============================================================================

  const handleSubmit = () => {
    if (!form.so_don_hang.trim() || !form.khach_hang_id) {
      alert('Vui lòng điền đầy đủ Số đơn hàng và Khách hàng!');
      return;
    }

    if (form.chi_tiet.length === 0) {
      alert('Vui lòng thêm ít nhất 1 sản phẩm!');
      return;
    }

    // Check số lượng
    const hasInvalidQty = form.chi_tiet.some(item => !item.so_luong || item.so_luong <= 0);
    if (hasInvalidQty) {
      alert('Vui lòng nhập số lượng cho tất cả sản phẩm!');
      return;
    }

    const orderData = {
      so_don_hang: form.so_don_hang.trim(),
      khach_hang_id: form.khach_hang_id,
      ngay_dat: form.ngay_dat,
      ngay_giao_du_kien: form.ngay_giao_du_kien,
      chi_tiet: form.chi_tiet.map(item => ({
        ...item,
        so_luong: parseFloat(item.so_luong),
        thanh_tien: parseFloat(item.so_luong) * parseFloat(item.don_gia)
      })),
      tong_tien: form.chi_tiet.reduce((sum, item) => 
        sum + (parseFloat(item.so_luong) * parseFloat(item.don_gia)), 0
      ),
      ghi_chu: form.ghi_chu.trim()
    };

    if (editingOrder) {
      setOrders(orders.map(o =>
        o.id === editingOrder.id ? { ...o, ...orderData } : o
      ));
      alert('Đã cập nhật đơn hàng!');
    } else {
      const newOrder = {
        id: Date.now(),
        ...orderData,
        ngay_tao: new Date().toISOString()
      };
      setOrders([...orders, newOrder]);
      alert('Đã tạo đơn hàng mới!');
    }

    resetForm();
    setShowModal(false);
  };

  const resetForm = () => {
    setForm({
      so_don_hang: generateSoDonHang(orders),
      khach_hang_id: '',
      ngay_dat: new Date().toISOString().split('T')[0],
      ngay_giao_du_kien: '',
      chi_tiet: [],
      ghi_chu: ''
    });
    setEditingOrder(null);
    quantityInputRefs.current = {};
  };

  const handleEdit = (order) => {
    setForm({
      ...order,
      chi_tiet: order.chi_tiet.map(item => ({
        ...item,
        so_luong: item.so_luong.toString()
      }))
    });
    setEditingOrder(order);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Bạn có chắc muốn xóa đơn hàng này?')) {
      setOrders(orders.filter(o => o.id !== id));
      alert('Đã xóa đơn hàng!');
    }
  };

  // ============================================================================
  // PRODUCT HANDLERS
  // ============================================================================

  const handleAddProduct = (product, isChild = false, parentProduct = null) => {
    // Tạo item mới
    const ktpo = product.po_cao 
      ? `${product.po_dai}×${product.po_rong}×${product.po_cao}`
      : `${product.po_dai}×${product.po_rong}`;

    const ktsx = (product.sx_dai || product.sx_rong || product.sx_cao)
      ? `${product.sx_dai || product.po_dai}×${product.sx_rong || product.po_rong}${product.sx_cao || product.po_cao ? '×' + (product.sx_cao || product.po_cao) : ''}`
      : ktpo;

    const newItem = {
      id: Date.now() + Math.random(), // Unique ID
      thanh_pham_id: product.id,
      ma_hang: product.ma_hang,
      ten_san_pham: product.ten_san_pham || product.ten || 'N/A',
      ktsx: ktsx,
      ktpo: ktpo,
      song: product.song || 'N/A',
      dvt: product.dvt || 'Cái',
      kieu: product.kieu || '',
      so_luong: '',
      don_gia: product.don_gia || 0,
      thanh_tien: 0,
      cong_doan: product.cong_doan || {},
      la_thanh_phan_con: isChild,
      thanh_pham_me_id: isChild ? parentProduct?.id : null
    };

    setForm({
      ...form,
      chi_tiet: [...form.chi_tiet, newItem]
    });

    setShowProductModal(false);
    setProductSearchTerm('');

    // Auto focus vào input số lượng sau khi thêm
    setTimeout(() => {
      const input = quantityInputRefs.current[newItem.id];
      if (input) {
        input.focus();
        input.select();
      }
    }, 100);
  };

  const handleUpdateQuantity = (itemId, value) => {
    setForm({
      ...form,
      chi_tiet: form.chi_tiet.map(item => {
        if (item.id === itemId) {
          const qty = parseFloat(value) || 0;
          return {
            ...item,
            so_luong: value,
            thanh_tien: qty * parseFloat(item.don_gia)
          };
        }
        return item;
      })
    });
  };

  const handleRemoveItem = (itemId) => {
    setForm({
      ...form,
      chi_tiet: form.chi_tiet.filter(item => item.id !== itemId)
    });
  };

  // ============================================================================
  // PRODUCT SELECTION - Cả mẹ + con
  // ============================================================================

  const getAllAvailableProducts = () => {
    const allProducts = [];

    products.forEach(product => {
      // Thêm sản phẩm mẹ
      allProducts.push({
        ...product,
        isParent: true,
        isChild: false,
        displayName: `${product.ma_hang} - ${product.ten_san_pham}`
      });

      // Thêm các thành phần con (nếu có)
      if (product.co_thanh_phan_con && product.thanh_phan_con) {
        product.thanh_phan_con.forEach(con => {
          allProducts.push({
            ...con,
            id: `${product.id}_${con.ma_hang_con}`,
            ma_hang: con.ma_hang_con,
            ten_san_pham: con.ten,
            isParent: false,
            isChild: true,
            parentProduct: product,
            displayName: `${con.ma_hang_con} - ${con.ten} (con của ${product.ma_hang})`
          });
        });
      }
    });

    return allProducts;
  };

  const filteredProducts = getAllAvailableProducts().filter(p => {
    const searchLower = productSearchTerm.toLowerCase();
    return (
      p.ma_hang.toLowerCase().includes(searchLower) ||
      (p.ten_san_pham || '').toLowerCase().includes(searchLower) ||
      p.displayName.toLowerCase().includes(searchLower)
    );
  });

  // ============================================================================
  // FILTERS
  // ============================================================================

  const filteredOrders = orders.filter(o => {
    const searchLower = searchTerm.toLowerCase();
    const customerName = getCustomerName(o.khach_hang_id, customers);
    const customerCode = getCustomerCode(o.khach_hang_id, customers);

    return (
      o.so_don_hang.toLowerCase().includes(searchLower) ||
      customerName.toLowerCase().includes(searchLower) ||
      customerCode.toLowerCase().includes(searchLower)
    );
  });

  // ============================================================================
  // RENDER
  // ============================================================================

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Đơn hàng Xuất</h1>
          <p className="text-gray-600">Quản lý đơn hàng xuất kho</p>
        </div>
        <button
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
        >
          <Plus className="w-5 h-5" />Tạo đơn hàng
        </button>
      </div>

      {/* Search */}
      <div className="bg-white rounded-xl shadow-sm border p-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Tìm kiếm theo số đơn hàng, khách hàng..."
            className="w-full pl-10 pr-4 py-2 border rounded-lg"
          />
        </div>
      </div>

      {/* Orders List */}
      <div className="space-y-4">
        {filteredOrders.map(order => {
          const customerName = getCustomerName(order.khach_hang_id, customers);
          const customerCode = getCustomerCode(order.khach_hang_id, customers);

          return (
            <div key={order.id} className="bg-white rounded-xl shadow-sm border overflow-hidden">
              {/* Order Header */}
              <div className="bg-gradient-to-r from-blue-50 to-purple-50 px-6 py-4 border-b flex justify-between items-center">
                <div className="flex items-center gap-4">
                  <ShoppingCart className="w-6 h-6 text-blue-600" />
                  <div>
                    <h3 className="font-bold text-lg text-blue-900">{order.so_don_hang}</h3>
                    <div className="flex items-center gap-3 text-sm text-gray-600 mt-1">
                      <span>📦 KH: <span className="font-medium text-gray-900">{customerCode} - {customerName}</span></span>
                      <span>•</span>
                      <span>📅 Đặt: {new Date(order.ngay_dat).toLocaleDateString('vi-VN')}</span>
                      <span>•</span>
                      <span>🚚 Giao: {new Date(order.ngay_giao_du_kien).toLocaleDateString('vi-VN')}</span>
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <div className="text-right">
                    <p className="text-sm text-gray-600">Tổng tiền</p>
                    <p className="text-2xl font-bold text-green-600">
                      {order.tong_tien.toLocaleString('vi-VN')}đ
                    </p>
                  </div>
                  <button
                    onClick={() => {
                      setSelectedOrder(order);
                      setShowDetailModal(true);
                    }}
                    className="p-2 text-purple-600 hover:bg-purple-50 rounded-lg"
                    title="Xem chi tiết"
                  >
                    <Eye className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => handleEdit(order)}
                    className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                    title="Sửa"
                  >
                    <Edit className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => handleDelete(order.id)}
                    className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                    title="Xóa"
                  >
                    <Trash2 className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Order Summary */}
              <div className="px-6 py-4">
                <div className="text-sm text-gray-600 mb-2">
                  <span className="font-medium">Sản phẩm:</span> {order.chi_tiet.length} mặt hàng
                </div>
                {order.ghi_chu && (
                  <div className="text-sm">
                    <span className="font-medium text-gray-700">Ghi chú:</span>
                    <span className="text-gray-600 ml-2">{order.ghi_chu}</span>
                  </div>
                )}
              </div>
            </div>
          );
        })}

        {filteredOrders.length === 0 && (
          <div className="bg-white rounded-xl shadow-sm border p-12 text-center text-gray-500">
            Chưa có đơn hàng nào
          </div>
        )}
      </div>

      {/* Add/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4 overflow-y-auto">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-6xl my-8">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10 rounded-t-xl">
              <div className="flex justify-between items-center">
                <h2 className="text-xl font-bold">
                  {editingOrder ? 'Sửa đơn hàng' : 'Tạo đơn hàng mới'}
                </h2>
                <button
                  onClick={() => {
                    setShowModal(false);
                    resetForm();
                  }}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="w-6 h-6" />
                </button>
              </div>
            </div>

            <div className="p-6 space-y-6 max-h-[calc(100vh-200px)] overflow-y-auto">
              {/* Thông tin đơn hàng */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Số đơn hàng * <span className="text-xs text-gray-500">(Tự động)</span>
                  </label>
                  <input
                    type="text"
                    value={form.so_don_hang}
                    readOnly
                    className="w-full px-4 py-2 border rounded-lg bg-gray-50 font-mono font-bold text-blue-600"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Khách hàng *</label>
                  <select
                    value={form.khach_hang_id}
                    onChange={(e) => setForm({ ...form, khach_hang_id: e.target.value })}
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">-- Chọn mã khách hàng --</option>
                    {customers.map(c => (
                      <option key={c.id} value={c.id}>
                        {c.ma_khach_hang}
                      </option>
                    ))}
                  </select>
                  {form.khach_hang_id && (
                    <p className="text-sm text-gray-600 mt-1">
                      → {getCustomerName(form.khach_hang_id, customers)}
                    </p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Ngày đặt</label>
                  <input
                    type="date"
                    value={form.ngay_dat}
                    onChange={(e) => setForm({ ...form, ngay_dat: e.target.value })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-2">Ngày giao dự kiến</label>
                  <input
                    type="date"
                    value={form.ngay_giao_du_kien}
                    onChange={(e) => setForm({ ...form, ngay_giao_du_kien: e.target.value })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
              </div>

              {/* Chi tiết sản phẩm */}
              <div className="border rounded-lg p-4 bg-gray-50">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="font-bold text-lg">Chi tiết sản phẩm</h3>
                  <button
                    onClick={() => setShowProductModal(true)}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
                  >
                    <Plus className="w-4 h-4" />Thêm sản phẩm
                  </button>
                </div>

                {form.chi_tiet.length > 0 ? (
                  <div className="overflow-x-auto border rounded-lg bg-white">
                    <table className="w-full text-sm">
                      <thead className="bg-gray-100">
                        <tr>
                          <th className="px-3 py-2 text-left font-semibold">Mã hàng</th>
                          <th className="px-3 py-2 text-left font-semibold">Tên SP</th>
                          <th className="px-3 py-2 text-center font-semibold">KTSX</th>
                          <th className="px-3 py-2 text-center font-semibold">Sóng</th>
                          <th className="px-3 py-2 text-center font-semibold">Số lượng</th>
                          <th className="px-3 py-2 text-right font-semibold">Đơn giá</th>
                          <th className="px-3 py-2 text-right font-semibold">Thành tiền</th>
                          <th className="px-3 py-2 text-center font-semibold"></th>
                        </tr>
                      </thead>
                      <tbody className="divide-y">
                        {form.chi_tiet.map(item => (
                          <tr key={item.id} className={item.la_thanh_phan_con ? 'bg-purple-50' : ''}>
                            <td className="px-3 py-2">
                              <span className={`font-medium ${item.la_thanh_phan_con ? 'text-purple-700' : 'text-blue-600'}`}>
                                {item.ma_hang}
                              </span>
                            </td>
                            <td className="px-3 py-2">{item.ten_san_pham}</td>
                            <td className="px-3 py-2 text-center text-xs">{item.ktsx}</td>
                            <td className="px-3 py-2 text-center">
                              <span className="px-2 py-1 bg-gray-100 rounded text-xs">
                                {item.song}
                              </span>
                            </td>
                            <td className="px-3 py-2">
                              <input
                                ref={el => quantityInputRefs.current[item.id] = el}
                                type="number"
                                value={item.so_luong}
                                onChange={(e) => handleUpdateQuantity(item.id, e.target.value)}
                                className="w-24 px-2 py-1 border rounded text-right"
                                min="0"
                                placeholder="0"
                              />
                            </td>
                            <td className="px-3 py-2 text-right">
                              {parseFloat(item.don_gia).toLocaleString('vi-VN')}
                            </td>
                            <td className="px-3 py-2 text-right font-semibold text-green-600">
                              {item.thanh_tien.toLocaleString('vi-VN')}
                            </td>
                            <td className="px-3 py-2 text-center">
                              <button
                                onClick={() => handleRemoveItem(item.id)}
                                className="p-1 text-red-600 hover:bg-red-50 rounded"
                              >
                                <X className="w-4 h-4" />
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                      <tfoot className="bg-gray-50 border-t-2">
                        <tr>
                          <td colSpan="6" className="px-3 py-3 text-right font-bold">
                            Tổng tiền:
                          </td>
                          <td className="px-3 py-3 text-right font-bold text-green-600 text-lg">
                            {form.chi_tiet.reduce((sum, item) => sum + item.thanh_tien, 0).toLocaleString('vi-VN')}đ
                          </td>
                          <td></td>
                        </tr>
                      </tfoot>
                    </table>
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500 bg-white border rounded-lg">
                    Chưa có sản phẩm. Nhấn "Thêm sản phẩm" để bắt đầu.
                  </div>
                )}
              </div>

              {/* Ghi chú */}
              <div>
                <label className="block text-sm font-medium mb-2">Ghi chú</label>
                <TextArea
                  value={form.ghi_chu}
                  onChange={(value) => setForm({ ...form, ghi_chu: value })}
                  placeholder="Ghi chú về đơn hàng..."
                  rows={3}
                  className="w-full px-4 py-2 border rounded-lg"
                />
              </div>
            </div>

            {/* Footer */}
            <div className="border-t px-6 py-4 flex justify-end gap-3 sticky bottom-0 bg-white">
              <button
                onClick={() => {
                  setShowModal(false);
                  resetForm();
                }}
                className="px-6 py-2 border rounded-lg hover:bg-gray-50"
              >
                Hủy
              </button>
              <button
                onClick={handleSubmit}
                className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
              >
                <ShoppingCart className="w-5 h-5" />
                {editingOrder ? 'Cập nhật' : 'Tạo đơn hàng'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Product Selection Modal */}
      {showProductModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-5xl max-h-[90vh] overflow-hidden">
            <div className="border-b px-6 py-4">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-bold">Chọn sản phẩm (Mẹ + Con)</h3>
                <button
                  onClick={() => {
                    setShowProductModal(false);
                    setProductSearchTerm('');
                  }}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="w-6 h-6" />
                </button>
              </div>
            </div>

            <div className="p-6 space-y-4">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                <input
                  type="text"
                  value={productSearchTerm}
                  onChange={(e) => setProductSearchTerm(e.target.value)}
                  placeholder="Tìm kiếm mã hàng, tên sản phẩm..."
                  className="w-full pl-10 pr-4 py-2 border rounded-lg"
                  autoFocus
                />
              </div>

              {/* Products List */}
              <div className="overflow-y-auto max-h-96 border rounded-lg">
                <table className="w-full text-sm">
                  <thead className="bg-gray-50 sticky top-0">
                    <tr>
                      <th className="px-3 py-2 text-left font-semibold">Mã hàng</th>
                      <th className="px-3 py-2 text-left font-semibold">Tên sản phẩm</th>
                      <th className="px-3 py-2 text-center font-semibold">KTSX</th>
                      <th className="px-3 py-2 text-center font-semibold">Sóng</th>
                      <th className="px-3 py-2 text-right font-semibold">Đơn giá</th>
                      <th className="px-3 py-2 text-center font-semibold"></th>
                    </tr>
                  </thead>
                  <tbody className="divide-y">
                    {filteredProducts.map(product => {
                      const alreadyAdded = form.chi_tiet.some(item => 
                        item.thanh_pham_id === product.id || 
                        item.ma_hang === product.ma_hang
                      );

                      const ktsx = product.ktsx || (product.sx_dai 
                        ? `${product.sx_dai}×${product.sx_rong}${product.sx_cao ? '×' + product.sx_cao : ''}`
                        : (product.po_cao ? `${product.po_dai}×${product.po_rong}×${product.po_cao}` : `${product.po_dai}×${product.po_rong}`));

                      return (
                        <tr 
                          key={product.id} 
                          className={`hover:bg-gray-50 ${product.isChild ? 'bg-purple-50' : ''}`}
                        >
                          <td className="px-3 py-2">
                            <span className={`font-medium ${product.isChild ? 'text-purple-700' : 'text-blue-600'}`}>
                              {product.ma_hang}
                            </span>
                          </td>
                          <td className="px-3 py-2">
                            <div>
                              {product.ten_san_pham}
                              {product.isChild && (
                                <span className="text-xs text-purple-600 ml-2">
                                  (con của {product.parentProduct?.ma_hang})
                                </span>
                              )}
                            </div>
                          </td>
                          <td className="px-3 py-2 text-center text-xs">{ktsx}</td>
                          <td className="px-3 py-2 text-center">
                            <span className="px-2 py-1 bg-gray-100 rounded text-xs">
                              {product.song || 'N/A'}
                            </span>
                          </td>
                          <td className="px-3 py-2 text-right">
                            {(product.don_gia || 0).toLocaleString('vi-VN')}đ
                          </td>
                          <td className="px-3 py-2 text-center">
                            <button
                              onClick={() => handleAddProduct(
                                product, 
                                product.isChild, 
                                product.parentProduct
                              )}
                              disabled={alreadyAdded}
                              className={`px-3 py-1 rounded text-sm ${
                                alreadyAdded
                                  ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                                  : 'bg-blue-600 text-white hover:bg-blue-700'
                              }`}
                            >
                              {alreadyAdded ? 'Đã thêm' : 'Chọn'}
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>

                {filteredProducts.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    Không tìm thấy sản phẩm
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Detail Modal */}
      {showDetailModal && selectedOrder && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-bold">Chi tiết đơn hàng: {selectedOrder.so_don_hang}</h3>
                <button
                  onClick={() => {
                    setShowDetailModal(false);
                    setSelectedOrder(null);
                  }}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="w-6 h-6" />
                </button>
              </div>
            </div>

            <div className="p-6">
              <p className="text-gray-600">Khách hàng: <span className="font-bold">{getCustomerName(selectedOrder.khach_hang_id, customers)}</span></p>
              <p className="text-gray-600 mt-2">Tổng tiền: <span className="font-bold text-green-600">{selectedOrder.tong_tien.toLocaleString('vi-VN')}đ</span></p>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}

export default SalesOrder;
