                    </label>
                  </div>
                  {form.co_thanh_phan_con && (
                    <button
                      onClick={addThanhPhanCon}
                      className="text-sm bg-purple-600 text-white px-3 py-1 rounded-lg hover:bg-purple-700 flex items-center gap-1"
