import React from 'react';
import { ExternalLink } from 'lucide-react';

/**
 * LinkedField - Clickable field để xem cross-module
 * 
 * Props:
 * - value: Giá trị hiển thị
 * - module: Module đích ('products', 'customers', 'salesOrders')
 * - id: ID của record để xem
 * - onClick: Callback tùy chỉnh (nếu không dùng module)
 * - className: Custom CSS classes
 */
function LinkedField({ 
  value, 
  module, 
  id, 
  onClick,
  className = '' 
}) {

  const handleClick = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (onClick) {
      onClick(id);
      return;
    }

    // Default behavior: navigate to module detail
    if (module && id) {
      // In a real app, this would navigate to the detail page
      // For now, we'll show an alert with module info
      console.log(`Navigate to ${module} with ID: ${id}`);

      // You can implement actual navigation here
      // window.location.href = `/${module}/${id}`;
      // or use React Router: navigate(`/${module}/${id}`);

      alert(`Xem chi tiết ${getModuleName(module)}: ${value}\nID: ${id}`);
    }
  };

  const getModuleName = (mod) => {
    const names = {
      'products': 'Thành phẩm',
      'customers': 'Khách hàng',
      'salesOrders': 'Đơn hàng xuất'
    };
    return names[mod] || mod;
  };

  return (
    <button
      onClick={handleClick}
      className={`
        inline-flex items-center gap-1 
        text-blue-600 hover:text-blue-800 
        hover:underline 
        font-medium
        transition-colors
        ${className}
      `}
      title={`Xem chi tiết ${value}`}
    >
      <span>{value}</span>
      <ExternalLink className="w-3 h-3" />
    </button>
  );
}

export default LinkedField;
