import React, { useState, useEffect } from 'react';
import { Plus, Search, Edit, Trash2, Copy, Eye, Download, ChevronRight, TrendingUp, History } from 'lucide-react';
import * as XLSX from 'xlsx';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';
import FileUploadCompact from '../components/FileUploadCompact';
import FilterBox from '../components/FilterBox';
import ExcelActions from '../components/ExcelActions';
import LinkedField from '../components/LinkedField';

const removeVietnameseTones = (str) => {
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/đ/g, 'd')
    .replace(/Đ/g, 'D');
};

// Danh sách công đoạn cố định
const CONG_DOAN_MAC_DINH = [
  { ma: 'ma_phim', ten: 'Mã phim', type: 'text', placeholder: 'Mã hóa phim in' },
  { ma: 'so_mau', ten: 'Số màu', type: 'number', placeholder: 'Số màu in' },
  { ma: 'in', ten: 'In', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'boi', ten: 'Bồi', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'can_mang', ten: 'Cán màng', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'ma_khuon', ten: 'Mã khuôn', type: 'text', placeholder: 'Mã khuôn bế' },
  { ma: 'be', ten: 'Bế', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'chap', ten: 'Chạp', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'dong', ten: 'Đóng', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'dan', ten: 'Dán', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'khac', ten: 'Khác', type: 'number', placeholder: 'Định mức (cái/h)' }
];

function Product() {
  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    return saved ? JSON.parse(saved) : [];
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    return saved ? JSON.parse(saved) : [];
  };

  const loadPriceHistory = () => {
    const saved = localStorage.getItem('product_price_history');
    return saved ? JSON.parse(saved) : [];
  };

  const [products, setProducts] = useState(loadProducts);
  const [customers] = useState(loadCustomers);
  const [priceHistory, setPriceHistory] = useState(loadPriceHistory);
  const [searchTerm, setSearchTerm] = useState('');
  const [advancedFilters, setAdvancedFilters] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showPriceModal, setShowPriceModal] = useState(false);
  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const [priceForm, setPriceForm] = useState({
    gia_moi: '',
    ngay_ap_dung: new Date().toISOString().slice(0, 10),
    ly_do: ''
  });

  const [form, setForm] = useState({
    ma_hang: '',
    ten_san_pham: '',
    khach_hang_id: '',
    po_dai: '',
    po_rong: '',
    po_cao: '',
    sx_dai: '',
    sx_rong: '',
    sx_cao: '',
    song: 'B',
    dvt: 'Cái',
    don_gia: '',
    hoa_hong_co_dinh: '',
    hoa_hong_phan_tram: '',
    cho_phep_sai_lech: false,
    sai_lech_so_luong: '',
    sai_lech_phan_tram: '',
    cong_doan: {
      ma_phim: '',
      ma_phim_file: null,
      so_mau: '',
      in: '',
      boi: '',
      can_mang: '',
      ma_khuon: '',
      ma_khuon_file: null,
      be: '',
      chap: '',
      dong: '',
      dan: '',
      khac: ''
    },
    co_thanh_phan_con: false,
    thanh_phan_con: [],
    kieu: '',
    ghi_chu: ''
  });

  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);

  useEffect(() => {
    localStorage.setItem('product_price_history', JSON.stringify(priceHistory));
  }, [priceHistory]);

  // ============================================
  // FILTER & EXCEL CONFIGURATION
  // ============================================

  // FilterBox columns configuration
  const filterColumns = [
    { 
      key: 'ma_hang', 
      label: 'Mã hàng', 
      type: 'text' 
    },
    { 
      key: 'ten_san_pham', 
      label: 'Tên sản phẩm', 
      type: 'text' 
    },
    { 
      key: 'don_gia', 
      label: 'Đơn giá', 
      type: 'range' 
    },
    { 
      key: 'song', 
      label: 'Sóng', 
      type: 'select',
      options: ['B', 'BC', 'E', 'BE', 'C']
    },
    { 
      key: 'dvt', 
      label: 'Đơn vị tính', 
      type: 'select',
      options: ['Cái', 'Bộ', 'Hộp', 'Tờ']
    }
  ];

  // ExcelActions columns configuration
  const excelColumns = [
    { 
      key: 'ma_hang', 
      label: 'Mã hàng',
      example: 'NN01'
    },
    { 
      key: 'ten_san_pham', 
      label: 'Tên sản phẩm',
      example: 'Nắp nấm'
    },
    { 
      key: 'khach_hang_id', 
      label: 'Khách hàng',
      example: 'ABC Company',
      formatter: (value) => {
        const customer = customers.find(c => c.id === value);
        return customer ? customer.name : '';
      },
      parser: (value) => {
        const customer = customers.find(c => c.name === value);
        return customer ? customer.id : '';
      }
    },
    { 
      key: 'ktsx', 
      label: 'KTSX',
      example: '60×40×30',
      formatter: (value, row) => {
        const ktsx = getKTSXThucTe(row);
        return ktsx.display;
      }
    },
    { 
      key: 'don_gia', 
      label: 'Đơn giá',
      example: '22000',
      formatter: (value) => value?.toLocaleString('vi-VN') || '',
      parser: (value) => parseFloat(String(value).replace(/[^\d]/g, '')) || 0
    },
    { 
      key: 'song', 
      label: 'Sóng',
      example: 'B'
    },
    { 
      key: 'dvt', 
      label: 'Đơn vị',
      example: 'Cái'
    },
    { 
      key: 'po_dai', 
      label: 'PO Dài',
      example: '60'
    },
    { 
      key: 'po_rong', 
      label: 'PO Rộng',
      example: '40'
    },
    { 
      key: 'po_cao', 
      label: 'PO Cao',
      example: '30'
    }
  ];

  // Handle advanced filter
  const handleAdvancedFilter = (filters) => {
    setAdvancedFilters(filters);
  };

  // Handle Excel import
  const handleExcelImport = (importedData) => {
    try {
      // Validate imported data
      const validated = importedData.filter(row => {
        if (!row.ma_hang || !row.ten_san_pham) {
          console.warn('Thiếu thông tin bắt buộc:', row);
          return false;
        }
        return true;
      });

      if (validated.length === 0) {
        alert('Không có dữ liệu hợp lệ để import!');
        return;
      }

      // Convert to product format
      const newProducts = validated.map(row => ({
        id: Date.now() + Math.random(),
        ma_hang: row.ma_hang?.toUpperCase().trim() || '',
        ten_san_pham: row.ten_san_pham?.trim() || '',
        khach_hang_id: row.khach_hang_id || '',
        po_dai: row.po_dai || '',
        po_rong: row.po_rong || '',
        po_cao: row.po_cao || '',
        sx_dai: row.sx_dai || row.po_dai || '',
        sx_rong: row.sx_rong || row.po_rong || '',
        sx_cao: row.sx_cao || row.po_cao || '',
        song: row.song || 'B',
        dvt: row.dvt || 'Cái',
        don_gia: row.don_gia || 0,
        hoa_hong_co_dinh: row.hoa_hong_co_dinh || 0,
        hoa_hong_phan_tram: row.hoa_hong_phan_tram || 0,
        cho_phep_sai_lech: false,
        sai_lech_so_luong: '',
        sai_lech_phan_tram: '',
        cong_doan: {},
        co_thanh_phan_con: false,
        thanh_phan_con: [],
        kieu: row.kieu || '',
        ghi_chu: row.ghi_chu || ''
      }));

      // Check for duplicates
      const duplicates = [];
      const toAdd = [];

      newProducts.forEach(newP => {
        const exists = products.find(p => 
          p.ma_hang.toUpperCase() === newP.ma_hang.toUpperCase()
        );
        if (exists) {
          duplicates.push(newP.ma_hang);
        } else {
          toAdd.push(newP);
        }
      });

      if (duplicates.length > 0) {
        const proceed = window.confirm(
          `Có ${duplicates.length} mã hàng bị trùng:\n${duplicates.join(', ')}\n\n` +
          `Bỏ qua và chỉ import ${toAdd.length} mã hàng mới?`
        );
        if (!proceed) return;
      }

      if (toAdd.length > 0) {
        setProducts([...products, ...toAdd]);
        alert(`Đã import thành công ${toAdd.length} sản phẩm!`);
      } else {
        alert('Không có sản phẩm mới nào được import (tất cả đều trùng).');
      }

    } catch (error) {
      console.error('Import error:', error);
      alert('Lỗi khi import dữ liệu!');
    }
  };

  // ============================================
  // END FILTER & EXCEL CONFIGURATION
  // ============================================


  // File upload handlers
  const handleMaPhimFileChange = (fileData) => {
    setForm({
      ...form,
      cong_doan: {
        ...form.cong_doan,
        ma_phim_file: fileData
      }
    });
  };

  const handleMaPhimFileRemove = () => {
    setForm({
      ...form,
      cong_doan: {
        ...form.cong_doan,
        ma_phim_file: null
      }
    });
  };

  const handleMaKhuonFileChange = (fileData) => {
    setForm({
      ...form,
      cong_doan: {
        ...form.cong_doan,
        ma_khuon_file: fileData
      }
    });
  };

  const handleMaKhuonFileRemove = () => {
    setForm({
      ...form,
      cong_doan: {
        ...form.cong_doan,
        ma_khuon_file: null
      }
    });
  };

  const getKTSXThucTe = (product) => {
    const dai = product.sx_dai || product.po_dai;
    const rong = product.sx_rong || product.po_rong;
    const cao = product.sx_cao || product.po_cao;

    return {
      dai,
      rong,
      cao,
      display: cao ? `${dai}×${rong}×${cao}` : `${dai}×${rong}`
    };
  };

  const generateMaHangCon = (maME, index) => {
    const letters = 'abcdefghijklmnopqrstuvwxyz';
    return `${maME}-${letters[index]}`;
  };

  const handleSubmit = () => {
    if (!form.ma_hang.trim() || !form.ten_san_pham.trim() || !form.khach_hang_id) {
      alert('Vui lòng điền đầy đủ thông tin bắt buộc!');
      return;
    }

    if (!form.po_dai || !form.po_rong) {
      alert('Vui lòng nhập ít nhất Dài và Rộng!');
      return;
    }

    if (form.co_thanh_phan_con && form.thanh_phan_con.length === 0) {
      alert('Vui lòng thêm ít nhất 1 thành phần con!');
      return;
    }

    // Validate thành phần con
    if (form.co_thanh_phan_con) {
      for (let i = 0; i < form.thanh_phan_con.length; i++) {
        const con = form.thanh_phan_con[i];
        if (!con.ten || !con.so_luong || !con.po_dai || !con.po_rong) {
          alert(`Thành phần con ${i + 1}: Vui lòng điền Tên, SL, Dài, Rộng!`);
          return;
        }
      }
    }

    const existingCode = products.find(p => 
      p.ma_hang.toUpperCase() === form.ma_hang.toUpperCase().trim() && 
      (!editingProduct || p.id !== editingProduct.id)
    );

    if (existingCode) {
      alert('Mã hàng đã tồn tại!');
      return;
    }

    const productData = {
      ma_hang: form.ma_hang.toUpperCase().trim(),
      ten_san_pham: form.ten_san_pham.trim(),
      khach_hang_id: form.khach_hang_id,
      po_dai: parseFloat(form.po_dai),
      po_rong: parseFloat(form.po_rong),
      po_cao: parseFloat(form.po_cao),
      sx_dai: form.sx_dai ? parseFloat(form.sx_dai) : null,
      sx_rong: form.sx_rong ? parseFloat(form.sx_rong) : null,
      sx_cao: form.sx_cao ? parseFloat(form.sx_cao) : null,
      song: form.song,
      dvt: form.dvt,
      don_gia: parseFloat(form.don_gia) || 0,
      hoa_hong_co_dinh: parseFloat(form.hoa_hong_co_dinh) || 0,
      hoa_hong_phan_tram: parseFloat(form.hoa_hong_phan_tram) || 0,
      cho_phep_sai_lech: form.cho_phep_sai_lech,
      sai_lech_so_luong: parseFloat(form.sai_lech_so_luong) || 0,
      sai_lech_phan_tram: parseFloat(form.sai_lech_phan_tram) || 0,
      cong_doan: form.cong_doan,
      co_thanh_phan_con: form.co_thanh_phan_con,
      thanh_phan_con: form.co_thanh_phan_con ? form.thanh_phan_con.map((con, index) => ({
        ma_hang_con: generateMaHangCon(form.ma_hang.toUpperCase().trim(), index),
        ten: con.ten.trim(),
        so_luong: parseInt(con.so_luong),
        po_dai: parseFloat(con.po_dai),
        po_rong: parseFloat(con.po_rong),
        po_cao: parseFloat(con.po_cao),
        sx_dai: con.sx_dai ? parseFloat(con.sx_dai) : null,
        sx_rong: con.sx_rong ? parseFloat(con.sx_rong) : null,
        sx_cao: con.sx_cao ? parseFloat(con.sx_cao) : null,
        cong_doan: con.cong_doan || {}
      })) : [],
      kieu: form.kieu,
      ghi_chu: form.ghi_chu.trim()
    };

    if (editingProduct) {
      setProducts(products.map(p =>
        p.id === editingProduct.id ? { ...p, ...productData } : p
      ));
      alert('Đã cập nhật thành phẩm!');
    } else {
      const newProduct = {
        id: Date.now(),
        ...productData,
        trang_thai: 'active',
        ngay_tao: new Date().toISOString()
      };
      setProducts([...products, newProduct]);
      alert('Đã thêm thành phẩm!');
    }

    setShowModal(false);
    setEditingProduct(null);
    resetForm();
  };

  const resetForm = () => {
    setForm({
      ma_hang: '',
      ten_san_pham: '',
      khach_hang_id: '',
      po_dai: '',
      po_rong: '',
      po_cao: '',
      sx_dai: '',
      sx_rong: '',
      sx_cao: '',
      song: 'B',
      dvt: 'Cái',
      don_gia: '',
      hoa_hong_co_dinh: '',
      hoa_hong_phan_tram: '',
      cho_phep_sai_lech: false,
      sai_lech_so_luong: '',
      sai_lech_phan_tram: '',
      cong_doan: {
        ma_phim: '',
        ma_phim_file: null,
        so_mau: '',
        in: '',
        boi: '',
        can_mang: '',
        ma_khuon: '',
        ma_khuon_file: null,
        be: '',
        chap: '',
        dong: '',
        dan: '',
        khac: ''
      },
      co_thanh_phan_con: false,
      thanh_phan_con: [],
      kieu: '',
      ghi_chu: ''
    });
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setForm({
      ma_hang: product.ma_hang,
      ten_san_pham: product.ten_san_pham,
      khach_hang_id: product.khach_hang_id,
      po_dai: product.po_dai || '',
      po_rong: product.po_rong || '',
      po_cao: product.po_cao || '',
      sx_dai: product.sx_dai || '',
      sx_rong: product.sx_rong || '',
      sx_cao: product.sx_cao || '',
      song: product.song,
      dvt: product.dvt,
      don_gia: product.don_gia,
      hoa_hong_co_dinh: product.hoa_hong_co_dinh,
      hoa_hong_phan_tram: product.hoa_hong_phan_tram,
      cho_phep_sai_lech: product.cho_phep_sai_lech,
      sai_lech_so_luong: product.sai_lech_so_luong || '',
      sai_lech_phan_tram: product.sai_lech_phan_tram || '',
      cong_doan: product.cong_doan || {
        ma_phim: '',
        ma_phim_file: null,
        so_mau: '',
        in: '',
        boi: '',
        can_mang: '',
        ma_khuon: '',
        ma_khuon_file: null,
        be: '',
        chap: '',
        dong: '',
        dan: '',
        khac: ''
      },
      co_thanh_phan_con: product.co_thanh_phan_con || false,
      thanh_phan_con: product.thanh_phan_con || [],
      kieu: product.kieu || '',
      ghi_chu: product.ghi_chu || ''
    });
    setShowModal(true);
  };

  const handleDelete = (product) => {
    if (window.confirm(`Xóa thành phẩm "${product.ten_san_pham}"?`)) {
      setProducts(products.filter(p => p.id !== product.id));
    }
  };

  // ============================================
  // PRICE UPDATE HANDLERS (Material-style)
  // ============================================

  const handleUpdatePrice = () => {
    if (!selectedProduct || !priceForm.gia_moi) {
      alert('Vui lòng điền đầy đủ thông tin!');
      return;
    }

    const gia_moi = parseFloat(priceForm.gia_moi);
    if (gia_moi <= 0) {
      alert('Giá phải lớn hơn 0!');
      return;
    }

    const gia_cu = selectedProduct.don_gia;

    if (gia_moi === gia_cu) {
      alert('Giá mới phải khác giá cũ!');
      return;
    }

    // Update product
    setProducts(products.map(p =>
      p.id === selectedProduct.id
        ? { ...p, don_gia: gia_moi }
        : p
    ));

    // Save history
    const historyRecord = {
      id: Date.now(),
      san_pham_id: selectedProduct.id,
      san_pham_ma: selectedProduct.ma_hang,
      san_pham_ten: selectedProduct.ten_san_pham,
      gia_cu: gia_cu,
      gia_moi: gia_moi,
      ngay_ap_dung: priceForm.ngay_ap_dung,
      ly_do: priceForm.ly_do.trim() || 'Cập nhật giá',
      nguoi_tao: 'Admin',
      ngay_tao: new Date().toISOString()
    };
    setPriceHistory([...priceHistory, historyRecord]);

    setShowPriceModal(false);
    setSelectedProduct(null);
    setPriceForm({
      gia_moi: '',
      ngay_ap_dung: new Date().toISOString().slice(0, 10),
      ly_do: ''
    });
    alert('Đã cập nhật giá!');
  };

  const handleViewHistory = (product) => {
    setSelectedProduct(product);
    setShowHistoryModal(true);
  };

  const getProductHistory = (productId) => {
    return priceHistory
      .filter(h => h.san_pham_id === productId)
      .sort((a, b) => new Date(b.ngay_ap_dung) - new Date(a.ngay_ap_dung));
  };

  // ============================================
  // END PRICE UPDATE HANDLERS
  // ============================================


  const handleDuplicate = (product) => {
    setEditingProduct(null);
    setForm({
      ma_hang: product.ma_hang + '-COPY',
      ten_san_pham: product.ten_san_pham + ' (Copy)',
      khach_hang_id: product.khach_hang_id,
      po_dai: product.po_dai || '',
      po_rong: product.po_rong || '',
      po_cao: product.po_cao || '',
      sx_dai: product.sx_dai || '',
      sx_rong: product.sx_rong || '',
      sx_cao: product.sx_cao || '',
      song: product.song,
      dvt: product.dvt,
      don_gia: product.don_gia,
      hoa_hong_co_dinh: product.hoa_hong_co_dinh,
      hoa_hong_phan_tram: product.hoa_hong_phan_tram,
      cho_phep_sai_lech: product.cho_phep_sai_lech,
      sai_lech_so_luong: product.sai_lech_so_luong || '',
      sai_lech_phan_tram: product.sai_lech_phan_tram || '',
