  const handleExportExcel = () => {
    if (products.length === 0) {
      alert('Không có dữ liệu để xuất!');
      return;
    }

    const data = products.map(p => {
      const ktsx = getKTSXThucTe(p);
      const customer = customers.find(c => c.id === p.khach_hang_id);
      return {
        'Mã hàng': p.ma_hang,
        'Tên sản phẩm': p.ten_san_pham,
        'Khách hàng': customer ? customer.name : '',
        'Có con': p.co_thanh_phan_con ? 'Có' : 'Không',
        'Số con': p.thanh_phan_con?.length || 0,
        'KTSX': ktsx.display,
        'Đơn giá': p.don_gia,
        'Ghi chú': p.ghi_chu
      };
    });

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Thành phẩm');
    XLSX.writeFile(wb, 'Danh-muc-thanh-pham.xlsx');
  };

  const filteredProducts = products.filter(p => {
    const customer = customers.find(c => c.id === p.khach_hang_id);
    const ktsx = getKTSXThucTe(p);

    // Apply search term filter
    if (searchTerm) {
      const searchLower = removeVietnameseTones(searchTerm.toLowerCase());

      // SEARCH ALL - Tìm trong TẤT CẢ fields
      const searchableFields = [
        p.ma_hang,
        p.ten_san_pham,
        customer ? customer.name : '',
        p.don_gia?.toString() || '',
        ktsx.display,
        p.song || '',
        p.dvt || '',
        p.kieu || '',
        p.ghi_chu || '',
        // Công đoạn
        p.cong_doan?.ma_phim || '',
        p.cong_doan?.ma_khuon || '',
        p.cong_doan?.so_mau?.toString() || '',
        p.cong_doan?.in?.toString() || '',
        p.cong_doan?.boi?.toString() || '',
        p.cong_doan?.can_mang?.toString() || '',
        p.cong_doan?.be?.toString() || '',
        p.cong_doan?.chap?.toString() || '',
        p.cong_doan?.dong?.toString() || '',
        p.cong_doan?.dan?.toString() || '',
        p.cong_doan?.khac?.toString() || '',
        // Kích thước PO
        p.po_dai?.toString() || '',
        p.po_rong?.toString() || '',
        p.po_cao?.toString() || '',
        // Kích thước SX
        p.sx_dai?.toString() || '',
        p.sx_rong?.toString() || '',
        p.sx_cao?.toString() || '',
        // Hoa hồng
        p.hoa_hong_co_dinh?.toString() || '',
        p.hoa_hong_phan_tram?.toString() || '',
        // Thành phần con
        p.co_thanh_phan_con ? 'có con' : '',
        ...(p.thanh_phan_con || []).map(con => con.ten || ''),
        ...(p.thanh_phan_con || []).map(con => con.ma_hang_con || '')
      ];

      const matchSearch = searchableFields.some(field =>
        removeVietnameseTones(field.toLowerCase()).includes(searchLower)
      );

      if (!matchSearch) return false;
    }

    // Apply advanced filters
    if (Object.keys(advancedFilters).length > 0) {
      // Text filters
      if (advancedFilters.ma_hang && !p.ma_hang.toLowerCase().includes(advancedFilters.ma_hang.toLowerCase())) {
        return false;
      }
      if (advancedFilters.ten_san_pham && !p.ten_san_pham.toLowerCase().includes(advancedFilters.ten_san_pham.toLowerCase())) {
        return false;
      }

      // Range filter - Đơn giá
      if (advancedFilters.don_gia_min && p.don_gia < parseFloat(advancedFilters.don_gia_min)) {
        return false;
      }
      if (advancedFilters.don_gia_max && p.don_gia > parseFloat(advancedFilters.don_gia_max)) {
        return false;
      }

      // Select filters
      if (advancedFilters.song && p.song !== advancedFilters.song) {
        return false;
      }
      if (advancedFilters.dvt && p.dvt !== advancedFilters.dvt) {
        return false;
      }
    }

    return true;
  }).sort((a, b) => a.ma_hang.localeCompare(b.ma_hang));

  const getCustomerName = (customerId) => {
    const customer = customers.find(c => c.id === customerId);
    return customer ? customer.name : 'N/A';
  };

  const addThanhPhanCon = () => {
    setForm({
