import React, { useState, useEffect } from 'react';
import { Plus, Search, Edit2, Trash2, X } from 'lucide-react';

function Customers() {
  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    if (saved) return JSON.parse(saved);
    return [
      { id: 1, code: 'KH001', name: 'Công ty TNHH ABC', phone: '0901234567', address: 'TP.HCM', email: 'abc@company.com', taxCode: '0123456789', note: '', debt: 5000000 },
      { id: 2, code: 'KH002', name: 'Công ty CP XYZ', phone: '0907654321', address: 'Hà Nội', email: 'xyz@company.com', taxCode: '0987654321', note: '', debt: 0 },
      { id: 3, code: 'KH003', name: 'Công ty TNHH DEF', phone: '0903456789', address: 'Đà Nẵng', email: 'def@company.com', taxCode: '0111222333', note: 'Khách VIP', debt: 2500000 },
    ];
  };

  const [customers, setCustomers] = useState(loadCustomers);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterDebt, setFilterDebt] = useState('all');
  const [showModal, setShowModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [editingCustomer, setEditingCustomer] = useState(null);
  const [deletingCustomer, setDeletingCustomer] = useState(null);
  const [formData, setFormData] = useState({ name: '', phone: '', address: '', email: '', taxCode: '', note: '' });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    localStorage.setItem('customers', JSON.stringify(customers));
  }, [customers]);

  const generateCustomerCode = () => {
    if (customers.length === 0) return 'KH001';
    const maxId = Math.max(...customers.map(c => parseInt(c.code.substring(2))));
    return `KH${String(maxId + 1).padStart(3, '0')}`;
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Tên khách hàng là bắt buộc';
    if (!formData.phone.trim()) newErrors.phone = 'Số điện thoại là bắt buộc';
    else if (!/^0\d{9}$/.test(formData.phone)) newErrors.phone = 'SĐT phải có 10 số và bắt đầu bằng 0';
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) newErrors.email = 'Email không hợp lệ';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleAdd = () => {
    setEditingCustomer(null);
    setFormData({ name: '', phone: '', address: '', email: '', taxCode: '', note: '' });
    setErrors({});
    setShowModal(true);
  };

  const handleEdit = (customer) => {
    setEditingCustomer(customer);
    setFormData({ name: customer.name, phone: customer.phone, address: customer.address || '', email: customer.email || '', taxCode: customer.taxCode || '', note: customer.note || '' });
    setErrors({});
    setShowModal(true);
  };

  const handleSave = () => {
    if (!validateForm()) return;
    if (editingCustomer) {
      setCustomers(customers.map(c => c.id === editingCustomer.id ? { ...c, ...formData } : c));
    } else {
      setCustomers([...customers, { id: Date.now(), code: generateCustomerCode(), ...formData, debt: 0 }]);
    }
    setShowModal(false);
  };

  const handleDeleteClick = (customer) => {
    setDeletingCustomer(customer);
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = () => {
    setCustomers(customers.filter(c => c.id !== deletingCustomer.id));
    setShowDeleteModal(false);
  };

  const filteredCustomers = customers.filter(c => {
    const search = c.name.toLowerCase().includes(searchTerm.toLowerCase()) || c.code.toLowerCase().includes(searchTerm.toLowerCase()) || c.phone.includes(searchTerm);
    const debt = filterDebt === 'all' || (filterDebt === 'debt' && c.debt > 0) || (filterDebt === 'no-debt' && c.debt === 0);
    return search && debt;
  });

  return (
    <div className="p-6 space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Khách hàng</h1>
          <p className="text-gray-600">Quản lý danh sách khách hàng</p>
        </div>
        <button onClick={handleAdd} className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2">
          <Plus className="w-5 h-5" />Thêm khách hàng
        </button>
      </div>

      <div className="bg-white rounded-xl shadow-sm border p-4">
        <div className="flex gap-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} placeholder="Tìm kiếm..." className="w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500" />
          </div>
          <select value={filterDebt} onChange={(e) => setFilterDebt(e.target.value)} className="px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500">
            <option value="all">Tất cả</option>
            <option value="debt">Có công nợ</option>
            <option value="no-debt">Không công nợ</option>
          </select>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-sm border overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Mã KH</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Tên</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">SĐT</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Địa chỉ</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Công nợ</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase">Thao tác</th>
            </tr>
          </thead>
          <tbody className="divide-y">
            {filteredCustomers.map(c => (
              <tr key={c.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 text-sm font-medium">{c.code}</td>
                <td className="px-6 py-4 text-sm">{c.name}</td>
                <td className="px-6 py-4 text-sm">{c.phone}</td>
                <td className="px-6 py-4 text-sm">{c.address}</td>
                <td className="px-6 py-4 text-sm">
                  <span className={c.debt > 0 ? 'text-red-600 font-medium' : 'text-green-600'}>
                    {c.debt.toLocaleString('vi-VN')} đ
                  </span>
                </td>
                <td className="px-6 py-4">
                  <div className="flex justify-center gap-2">
                    <button onClick={() => handleEdit(c)} className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg">
                      <Edit2 className="w-4 h-4" />
                    </button>
                    <button onClick={() => handleDeleteClick(c)} className="p-2 text-red-600 hover:bg-red-50 rounded-lg">
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {filteredCustomers.length === 0 && <div className="text-center py-12 text-gray-500">Không tìm thấy</div>}
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 flex justify-between items-center">
              <h2 className="text-xl font-bold">{editingCustomer ? 'Sửa khách hàng' : 'Thêm khách hàng'}</h2>
              <button onClick={() => setShowModal(false)} className="p-2 hover:bg-gray-100 rounded-lg"><X className="w-5 h-5" /></button>
            </div>
            <div className="p-6 space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Tên <span className="text-red-500">*</span></label>
                  <input type="text" value={formData.name} onChange={(e) => setFormData({...formData, name: e.target.value})} className={`w-full px-4 py-2 border rounded-lg ${errors.name ? 'border-red-500' : ''}`} />
                  {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name}</p>}
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">SĐT <span className="text-red-500">*</span></label>
                  <input type="text" value={formData.phone} onChange={(e) => setFormData({...formData, phone: e.target.value})} className={`w-full px-4 py-2 border rounded-lg ${errors.phone ? 'border-red-500' : ''}`} />
                  {errors.phone && <p className="text-red-500 text-sm mt-1">{errors.phone}</p>}
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Email</label>
                  <input type="email" value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} className={`w-full px-4 py-2 border rounded-lg ${errors.email ? 'border-red-500' : ''}`} />
                  {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Mã số thuế</label>
                  <input type="text" value={formData.taxCode} onChange={(e) => setFormData({...formData, taxCode: e.target.value})} className="w-full px-4 py-2 border rounded-lg" />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Địa chỉ</label>
                <input type="text" value={formData.address} onChange={(e) => setFormData({...formData, address: e.target.value})} className="w-full px-4 py-2 border rounded-lg" />
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Ghi chú</label>
                <textarea value={formData.note} onChange={(e) => setFormData({...formData, note: e.target.value})} className="w-full px-4 py-2 border rounded-lg" rows="3" />
              </div>
            </div>
            <div className="border-t px-6 py-4 flex justify-end gap-3">
              <button onClick={() => setShowModal(false)} className="px-4 py-2 border rounded-lg hover:bg-gray-50">Hủy</button>
              <button onClick={handleSave} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">{editingCustomer ? 'Cập nhật' : 'Thêm'}</button>
            </div>
          </div>
        </div>
      )}

      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-md">
            <div className="p-6">
              <h3 className="text-lg font-bold mb-2">Xác nhận xóa</h3>
              <p className="text-gray-600 mb-4">Bạn có chắc muốn xóa <strong>{deletingCustomer?.name}</strong>?</p>
              <p className="text-sm text-red-600">Hành động này không thể hoàn tác!</p>
            </div>
            <div className="border-t px-6 py-4 flex justify-end gap-3">
              <button onClick={() => setShowDeleteModal(false)} className="px-4 py-2 border rounded-lg hover:bg-gray-50">Hủy</button>
              <button onClick={handleDeleteConfirm} className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700">Xóa</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Customers;
