import React, { useState } from 'react';
import { Filter, X, ChevronDown } from 'lucide-react';

/**
 * FilterBox Component - Bộ lọc dữ liệu chung
 * 
 * @param {Array} columns - Danh sách cột có thể lọc
 *   [{ key: 'ma_hang', label: 'Mã hàng', type: 'text' }, ...]
 *   Types: 'text', 'number', 'range', 'select', 'date'
 * 
 * @param {Function} onFilter - Callback khi apply filter
 *   onFilter(filters) => filters = { ma_hang: 'NN01', don_gia_min: 20000, ... }
 * 
 * @param {Object} initialFilters - Giá trị filter ban đầu (optional)
 */

const FilterBox = ({ columns = [], onFilter, initialFilters = {} }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [filters, setFilters] = useState(initialFilters);
  const [activeCount, setActiveCount] = useState(0);

  // Cập nhật filter
  const handleFilterChange = (key, value) => {
    const newFilters = { ...filters, [key]: value };

    // Remove empty filters
    Object.keys(newFilters).forEach(k => {
      if (newFilters[k] === '' || newFilters[k] === null || newFilters[k] === undefined) {
        delete newFilters[k];
      }
    });

    setFilters(newFilters);
    setActiveCount(Object.keys(newFilters).length);
  };

  // Apply filters
  const handleApply = () => {
    onFilter(filters);
    setIsOpen(false);
  };

  // Reset filters
  const handleReset = () => {
    setFilters({});
    setActiveCount(0);
    onFilter({});
  };

  // Render filter input by type
  const renderFilterInput = (column) => {
    const value = filters[column.key] || '';

    switch (column.type) {
      case 'text':
        return (
          <input
            type="text"
            value={value}
            onChange={(e) => handleFilterChange(column.key, e.target.value)}
            placeholder={`Nhập ${column.label.toLowerCase()}...`}
            className="w-full px-3 py-2 border rounded-lg text-sm"
          />
        );

      case 'number':
        return (
          <input
            type="number"
            value={value}
            onChange={(e) => handleFilterChange(column.key, e.target.value)}
            placeholder={`Nhập ${column.label.toLowerCase()}...`}
            className="w-full px-3 py-2 border rounded-lg text-sm"
          />
        );

      case 'range':
        return (
          <div className="grid grid-cols-2 gap-2">
            <input
              type="number"
              value={filters[`${column.key}_min`] || ''}
              onChange={(e) => handleFilterChange(`${column.key}_min`, e.target.value)}
              placeholder="Từ"
              className="w-full px-3 py-2 border rounded-lg text-sm"
            />
            <input
              type="number"
              value={filters[`${column.key}_max`] || ''}
              onChange={(e) => handleFilterChange(`${column.key}_max`, e.target.value)}
              placeholder="Đến"
              className="w-full px-3 py-2 border rounded-lg text-sm"
            />
          </div>
        );

      case 'select':
        return (
          <select
            value={value}
            onChange={(e) => handleFilterChange(column.key, e.target.value)}
            className="w-full px-3 py-2 border rounded-lg text-sm"
          >
            <option value="">Tất cả</option>
            {column.options?.map(opt => (
              <option key={opt} value={opt}>{opt}</option>
            ))}
          </select>
        );

      case 'date':
        return (
          <input
            type="date"
            value={value}
            onChange={(e) => handleFilterChange(column.key, e.target.value)}
            className="w-full px-3 py-2 border rounded-lg text-sm"
          />
        );

      default:
        return null;
    }
  };

  return (
    <div className="relative">
      {/* Filter Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className={`flex items-center gap-2 px-4 py-2 border rounded-lg font-medium transition-colors ${
          activeCount > 0 
            ? 'bg-blue-600 text-white border-blue-600 hover:bg-blue-700' 
            : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
        }`}
      >
        <Filter className="w-4 h-4" />
        <span>Bộ lọc</span>
        {activeCount > 0 && (
          <span className="bg-white text-blue-600 px-2 py-0.5 rounded-full text-xs font-bold">
            {activeCount}
          </span>
        )}
        <ChevronDown className={`w-4 h-4 transition-transform ${isOpen ? 'rotate-180' : ''}`} />
      </button>

      {/* Filter Panel */}
      {isOpen && (
        <>
          {/* Backdrop */}
          <div 
            className="fixed inset-0 z-40"
            onClick={() => setIsOpen(false)}
          />

          {/* Panel */}
          <div className="absolute top-full right-0 mt-2 w-96 bg-white border rounded-lg shadow-xl z-50 max-h-96 overflow-y-auto">
            <div className="p-4">
              <div className="flex items-center justify-between mb-4">
                <h3 className="font-bold text-lg">Bộ lọc nâng cao</h3>
                <button
                  onClick={() => setIsOpen(false)}
                  className="p-1 hover:bg-gray-100 rounded"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>

              {/* Filter Fields */}
              <div className="space-y-3">
                {columns.map(column => (
                  <div key={column.key}>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      {column.label}
                    </label>
                    {renderFilterInput(column)}
                  </div>
                ))}
              </div>

              {/* Actions */}
              <div className="flex gap-2 mt-4 pt-4 border-t">
                <button
                  onClick={handleReset}
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 font-medium"
                >
                  Xóa bộ lọc
                </button>
                <button
                  onClick={handleApply}
                  className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-medium"
                >
                  Áp dụng
                </button>
              </div>

              {/* Active Filters Summary */}
              {activeCount > 0 && (
                <div className="mt-3 pt-3 border-t">
                  <p className="text-xs text-gray-500 mb-2">Đang lọc theo:</p>
                  <div className="flex flex-wrap gap-2">
                    {Object.keys(filters).map(key => {
                      const column = columns.find(c => 
                        c.key === key || 
                        c.key === key.replace('_min', '') || 
                        c.key === key.replace('_max', '')
                      );
                      if (!column) return null;

                      return (
                        <span 
                          key={key}
                          className="inline-flex items-center gap-1 px-2 py-1 bg-blue-100 text-blue-700 rounded text-xs"
                        >
                          {column.label}: {filters[key]}
                          <button
                            onClick={() => handleFilterChange(key, '')}
                            className="hover:bg-blue-200 rounded-full p-0.5"
                          >
                            <X className="w-3 h-3" />
                          </button>
                        </span>
                      );
                    })}
                  </div>
                </div>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default FilterBox;
