import React, { useRef } from 'react';

/**
 * TextArea - Tự động viết hoa chữ cái đầu dòng và sau dấu chấm
 * Có thể sửa lại thành chữ thường nếu muốn
 */

const TextArea = ({ 
  value, 
  onChange, 
  className = '',
  ...rest 
}) => {
  const lastValueRef = useRef(value);

  const handleChange = (e) => {
    const newValue = e.target.value;
    const cursorPosition = e.target.selectionStart;

    // Chỉ auto-capitalize khi đang thêm ký tự (không phải xóa hoặc sửa)
    if (newValue.length > lastValueRef.current.length) {
      const processedValue = autoCapitalize(newValue);
      lastValueRef.current = processedValue;
      onChange(processedValue);

      // Giữ vị trí con trỏ
      setTimeout(() => {
        e.target.setSelectionRange(cursorPosition, cursorPosition);
      }, 0);
    } else {
      // Người dùng đang xóa hoặc sửa → không auto-capitalize
      lastValueRef.current = newValue;
      onChange(newValue);
    }
  };

  const autoCapitalize = (text) => {
    if (!text) return text;

    // Viết hoa ký tự đầu tiên
    let result = text.charAt(0).toUpperCase() + text.slice(1);

    // Viết hoa ký tự sau dấu chấm (. ! ?) + khoảng trắng
    result = result.replace(/([.!?]\s+)([a-z])/g, (match, punctuation, letter) => {
      return punctuation + letter.toUpperCase();
    });

    // Viết hoa đầu dòng mới (sau Enter)
    result = result.replace(/(^|\n)([a-z])/g, (match, newline, letter) => {
      return newline + letter.toUpperCase();
    });

    return result;
  };

  return (
    <textarea
      value={value}
      onChange={handleChange}
      className={className}
      {...rest}
    />
  );
};

export default TextArea;
