import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ExternalLink, Package, ShoppingCart, User, Users, Box, FileText, Truck } from 'lucide-react';

/**
 * LinkedField Component - COMPLETE VERSION
 * 
 * Tự động navigate hoặc mở modal tùy mode
 * 
 * @param {String} type - Loại: 'product', 'customer', 'material', 'supplier', 'order', 'employee'
 * @param {String} value - Giá trị hiển thị (VD: 'ABC', 'NN01')
 * @param {String|Number} id - ID để navigate/modal
 * @param {Object} data - Dữ liệu đầy đủ (optional, cho tooltip)
 * @param {Function} onClick - Custom onClick (optional)
 * @param {String} className - Custom className
 * @param {Boolean} showIcon - Hiển thị icon (default: true)
 * @param {Boolean} disabled - Disable link
 * @param {String} mode - 'navigate' hoặc 'modal' (default: 'modal')
 */

const LinkedField = ({ 
  type = 'product',
  value,
  id,
  data,
  onClick,
  className = '',
  showIcon = true,
  disabled = false,
  mode = 'modal'  // ← THÊM MODE
}) => {
  const navigate = useNavigate();
  const [showTooltip, setShowTooltip] = useState(false);

  // Map type → route path
  const getRoutePath = () => {
    const routes = {
      'product': '/products',
      'customer': '/customers',
      'material': '/materials',
      'supplier': '/suppliers',
      'order': '/sales-orders',
      'employee': '/employees',
      'advance': '/advance',
      'transaction': '/cashbook',
      'bank': '/banks'
    };
    return routes[type] || '/dashboard';
  };

  // Icon theo loại
  const getIcon = () => {
    const icons = {
      'product': <Package className="w-3 h-3" />,
      'order': <ShoppingCart className="w-3 h-3" />,
      'customer': <User className="w-3 h-3" />,
      'supplier': <Truck className="w-3 h-3" />,
      'material': <Box className="w-3 h-3" />,
      'employee': <Users className="w-3 h-3" />,
      'document': <FileText className="w-3 h-3" />
    };
    return icons[type] || <ExternalLink className="w-3 h-3" />;
  };

  // Màu theo loại
  const getColor = () => {
    const colors = {
      'product': 'text-blue-600 hover:text-blue-800 hover:bg-blue-50',
      'order': 'text-green-600 hover:text-green-800 hover:bg-green-50',
      'customer': 'text-purple-600 hover:text-purple-800 hover:bg-purple-50',
      'supplier': 'text-orange-600 hover:text-orange-800 hover:bg-orange-50',
      'material': 'text-teal-600 hover:text-teal-800 hover:bg-teal-50',
      'employee': 'text-indigo-600 hover:text-indigo-800 hover:bg-indigo-50',
      'document': 'text-gray-600 hover:text-gray-800 hover:bg-gray-50'
    };
    return colors[type] || 'text-blue-600 hover:text-blue-800 hover:bg-blue-50';
  };

  // Label theo loại
  const getTypeLabel = () => {
    const labels = {
      'product': 'Thành phẩm',
      'order': 'Đơn hàng',
      'customer': 'Khách hàng',
      'supplier': 'Nhà cung cấp',
      'material': 'Nguyên vật liệu',
      'employee': 'Nhân viên',
      'document': 'Tài liệu'
    };
    return labels[type] || 'Chi tiết';
  };

  // Handle click - Navigate hoặc Custom onClick
  const handleClick = (e) => {
    e.stopPropagation();

    if (disabled) return;

    if (onClick) {
      // Custom onClick handler (dùng cho modal)
      onClick(id || value, data, type);
    } else if (mode === 'navigate') {
      // Navigate mode: Chuyển trang với highlight
      const path = getRoutePath();
      const highlightId = id || value;
      navigate(`${path}?highlight=${highlightId}`);
    } else {
      // Default modal mode: Show alert (cần custom onClick để mở modal)
      alert(`Vui lòng truyền onClick handler để mở modal ${getTypeLabel()}`);
    }
  };

  // Tooltip content
  const renderTooltip = () => {
    if (!data) return null;

    return (
      <div className="absolute z-50 bottom-full left-0 mb-2 p-3 bg-gray-900 text-white text-xs rounded-lg shadow-xl min-w-64 max-w-sm pointer-events-none">
        <div className="space-y-1">
          <p className="font-bold text-sm">{getTypeLabel()}</p>
          <p className="text-gray-200">{value}</p>
          {data.name && <p className="text-gray-300">{data.name}</p>}
          {data.ten_san_pham && <p className="text-gray-300">{data.ten_san_pham}</p>}
          {data.sdt && <p className="text-gray-300">SĐT: {data.sdt}</p>}
          {data.don_gia && <p className="text-gray-300">Giá: {data.don_gia.toLocaleString('vi-VN')} đ</p>}
        </div>
        {/* Arrow */}
        <div className="absolute top-full left-4 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900" />
      </div>
    );
  };

  if (!value) return <span className="text-gray-400">-</span>;

  return (
    <div className="relative inline-block">
      <button
        onClick={handleClick}
        onMouseEnter={() => data && setShowTooltip(true)}
        onMouseLeave={() => setShowTooltip(false)}
        disabled={disabled}
        className={`
          inline-flex items-center gap-1 font-medium 
          ${disabled ? 'cursor-not-allowed opacity-50' : 'cursor-pointer underline decoration-dotted'}
          transition-all rounded px-1 py-0.5 
          ${disabled ? 'text-gray-400' : getColor()} 
          ${className}
        `}
        title={disabled ? value : `Click để xem ${getTypeLabel()}: ${value}`}
      >
        {showIcon && getIcon()}
        <span>{value}</span>
        {!disabled && <ExternalLink className="w-3 h-3 opacity-50" />}
      </button>

      {/* Tooltip */}
      {showTooltip && renderTooltip()}
    </div>
  );
};

export default LinkedField;

/**
 * ========================================
 * USAGE EXAMPLES - CÁCH DÙNG
 * ========================================
 * 
 * 1. Trong Product → Link đến Customer:
 * 
 * <LinkedField 
 *   type="customer" 
 *   value="ABC"              // Mã khách hàng
 *   id={product.khach_hang_id}  // ID khách hàng
 *   data={customerData}      // Data cho tooltip
 * />
 * 
 * Click → Navigate to /customers?highlight=123
 * 
 * ---
 * 
 * 2. Trong Order → Link đến Product:
 * 
 * <LinkedField 
 *   type="product" 
 *   value="NN01" 
 *   id={item.product_id}
 * />
 * 
 * Click → Navigate to /products?highlight=456
 * 
 * ---
 * 
 * 3. Trong Material → Link đến Supplier:
 * 
 * <LinkedField 
 *   type="supplier" 
 *   value="NCC-001" 
 *   id={material.supplier_id}
 *   data={supplier}
 * />
 * 
 * Click → Navigate to /suppliers?highlight=789
 * 
 * ---
 * 
 * 4. Custom onClick:
 * 
 * <LinkedField 
 *   type="customer" 
 *   value="ABC"
 *   onClick={(id) => {
 *     // Custom logic
 *     console.log('Clicked', id);
 *   }}
 * />
 * 
 * ---
 * 
 * 5. Disabled (chỉ hiển thị, không click được):
 * 
 * <LinkedField 
 *   type="product" 
 *   value="NN01"
 *   disabled={true}
 * />
 * 
 * ========================================
 * TỰ ĐỘNG HOẠT ĐỘNG CHO:
 * ========================================
 * 
 * ✅ Product → Customer
 * ✅ Product → Material
 * ✅ Order → Product
 * ✅ Order → Customer
 * ✅ Material → Supplier
 * ✅ Cashbook → Customer/Supplier
 * ✅ Advance → Employee
 * ✅ BẤT KỲ MODULE NÀO KHÁC
 * 
 * Chỉ cần:
 * 1. Import LinkedField
 * 2. Truyền type, value, id
 * 3. Xong!
 */
