import React, { useState, useEffect, useRef } from 'react';
import { Plus, Search, Edit, Trash2, Eye, Download, ShoppingCart, Package, X } from 'lucide-react';
import * as XLSX from 'xlsx';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';
import LinkedField from '../components/LinkedField';
import CustomerDetailModal from '../components/CustomerDetailModal';
import ProductDetailModal from '../components/ProductDetailModal';
import FilterBox from '../components/FilterBox';
import ExcelActions from '../components/ExcelActions';

const removeVietnameseTones = (str) => {
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/Ä‘/g, 'd')
    .replace(/Ä/g, 'D');
};

function SalesOrder() {
  const loadOrders = () => {
    const saved = localStorage.getItem('salesOrders');
    return saved ? JSON.parse(saved) : [];
  };

  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    return saved ? JSON.parse(saved) : [];
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    const parsed = saved ? JSON.parse(saved) : [];
    console.log('ðŸ“¦ Load customers:', parsed.length, 'items');
    return parsed;
  };

  const [orders, setOrders] = useState(loadOrders);
  const [products, setProducts] = useState(loadProducts);
  const [customers, setCustomers] = useState(loadCustomers);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [editingOrder, setEditingOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  const [showCustomerModal, setShowCustomerModal] = useState(false);
  const [selectedCustomerId, setSelectedCustomerId] = useState(null);
  const [showProductModal, setShowProductModal] = useState(false);
  const [selectedProductIdModal, setSelectedProductIdModal] = useState(null);

  const [form, setForm] = useState({
    so_don_hang: '',
    khach_hang_id: '',
    ngay_dat: new Date().toISOString().split('T')[0],
    ngay_giao_du_kien: '',
    chi_tiet: [],
    ghi_chu: ''
  });

  const [selectedProductIdForAdd, setSelectedProductIdForAdd] = useState('');
  const firstQuantityInputRef = useRef(null);

  useEffect(() => {
    if (showModal) {
      const reloaded = loadCustomers();
      setCustomers(reloaded);
    }
  }, [showModal]);

  useEffect(() => {
    localStorage.setItem('salesOrders', JSON.stringify(orders));
  }, [orders]);

  const getCustomerName = (khach_hang_id) => {
    const customer = customers.find(c => c.id === khach_hang_id);
    return customer?.ten_cong_ty || customer?.ten || 'N/A';
  };

  const getCustomerCode = (khach_hang_id) => {
    const customer = customers.find(c => c.id === khach_hang_id);
    return customer?.ma_khach_hang || 'N/A';
  };

  const generateOrderNumber = () => {
    const today = new Date();
    const year = today.getFullYear().toString().slice(-2);
    const month = String(today.getMonth() + 1).padStart(2, '0');
