import React, { useState, useEffect } from 'react';
import { X, Save, Edit } from 'lucide-react';

/**
 * CustomerDetailModal - MODAL XEM/SỬA KHÁCH HÀNG
 * 
 * Mở từ bất kỳ module nào để xem/sửa customer
 * Không cần chuyển trang
 */

const CustomerDetailModal = ({ 
  customerId, 
  isOpen, 
  onClose,
  onSave 
}) => {
  const [customer, setCustomer] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [form, setForm] = useState({
    ma: '',
    name: '',
    sdt: '',
    dia_chi: '',
    mst: '',
    email: '',
    ghi_chu: ''
  });

  // Load customer data
  useEffect(() => {
    if (isOpen && customerId) {
      const customers = JSON.parse(localStorage.getItem('customers') || '[]');
      const found = customers.find(c => 
        c.id === customerId || 
        c.id === parseInt(customerId) ||
        c.ma === customerId ||
        c.code === customerId
      );

      if (found) {
        setCustomer(found);
        setForm({
          ma: found.ma || found.code || '',
          name: found.name || '',
          sdt: found.sdt || '',
          dia_chi: found.dia_chi || '',
          mst: found.mst || '',
          email: found.email || '',
          ghi_chu: found.ghi_chu || ''
        });
      }
    }
  }, [isOpen, customerId]);

  // Handle save
  const handleSave = () => {
    if (!customer) return;

    const customers = JSON.parse(localStorage.getItem('customers') || '[]');
    const updated = customers.map(c => 
      c.id === customer.id 
        ? { 
            ...c, 
            ma: form.ma,
            code: form.ma, // Sync both
            name: form.name,
            sdt: form.sdt,
            dia_chi: form.dia_chi,
            mst: form.mst,
            email: form.email,
            ghi_chu: form.ghi_chu
          }
        : c
    );

    localStorage.setItem('customers', JSON.stringify(updated));

    if (onSave) {
      onSave(updated.find(c => c.id === customer.id));
    }

    setIsEditing(false);
    alert('Đã cập nhật khách hàng!');
  };

  // Handle close
  const handleClose = () => {
    setIsEditing(false);
    if (onClose) onClose();
  };

  if (!isOpen || !customer) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="sticky top-0 bg-white border-b px-6 py-4 flex items-center justify-between z-10">
          <div>
            <h2 className="text-xl font-bold text-purple-900">
              Chi tiết khách hàng
            </h2>
            <p className="text-sm text-gray-600">Mã: {customer.ma || customer.code}</p>
          </div>
          <div className="flex gap-2">
            {!isEditing && (
              <button
                onClick={() => setIsEditing(true)}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
              >
                <Edit className="w-4 h-4" />
                Sửa
              </button>
            )}
            <button
              onClick={handleClose}
              className="p-2 hover:bg-gray-100 rounded-lg"
            >
              <X className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="p-6 space-y-4">
          {/* Mã khách hàng */}
          <div>
            <label className="block text-sm font-medium mb-2">Mã khách hàng</label>
            {isEditing ? (
              <input
                type="text"
                value={form.ma}
                onChange={(e) => setForm({ ...form, ma: e.target.value.toUpperCase() })}
                className="w-full px-4 py-2 border rounded-lg uppercase"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg font-medium">
                {customer.ma || customer.code}
              </div>
            )}
          </div>

          {/* Tên khách hàng */}
          <div>
            <label className="block text-sm font-medium mb-2">Tên khách hàng *</label>
            {isEditing ? (
              <input
                type="text"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                className="w-full px-4 py-2 border rounded-lg"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg font-medium">
                {customer.name}
              </div>
            )}
          </div>

          {/* SĐT & Email */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2">Số điện thoại</label>
              {isEditing ? (
                <input
                  type="text"
                  value={form.sdt}
                  onChange={(e) => setForm({ ...form, sdt: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                />
              ) : (
                <div className="px-4 py-2 bg-gray-50 rounded-lg">
                  {customer.sdt || '-'}
                </div>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium mb-2">Email</label>
              {isEditing ? (
                <input
                  type="email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                />
              ) : (
                <div className="px-4 py-2 bg-gray-50 rounded-lg">
                  {customer.email || '-'}
                </div>
              )}
            </div>
          </div>

          {/* Địa chỉ */}
          <div>
            <label className="block text-sm font-medium mb-2">Địa chỉ</label>
            {isEditing ? (
              <input
                type="text"
                value={form.dia_chi}
                onChange={(e) => setForm({ ...form, dia_chi: e.target.value })}
                className="w-full px-4 py-2 border rounded-lg"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg">
                {customer.dia_chi || '-'}
              </div>
            )}
          </div>

          {/* MST */}
          <div>
            <label className="block text-sm font-medium mb-2">Mã số thuế</label>
            {isEditing ? (
              <input
                type="text"
                value={form.mst}
                onChange={(e) => setForm({ ...form, mst: e.target.value })}
                className="w-full px-4 py-2 border rounded-lg"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg">
                {customer.mst || '-'}
              </div>
            )}
          </div>

          {/* Ghi chú */}
          <div>
            <label className="block text-sm font-medium mb-2">Ghi chú</label>
            {isEditing ? (
              <textarea
                value={form.ghi_chu}
                onChange={(e) => setForm({ ...form, ghi_chu: e.target.value })}
                className="w-full px-4 py-2 border rounded-lg"
                rows="3"
              />
            ) : (
              <div className="px-4 py-2 bg-gray-50 rounded-lg min-h-[80px]">
                {customer.ghi_chu || '-'}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="border-t px-6 py-4 flex justify-end gap-2">
          {isEditing ? (
            <>
              <button
                onClick={() => {
                  setIsEditing(false);
                  // Reset form
                  setForm({
                    ma: customer.ma || customer.code || '',
                    name: customer.name || '',
                    sdt: customer.sdt || '',
                    dia_chi: customer.dia_chi || '',
                    mst: customer.mst || '',
                    email: customer.email || '',
                    ghi_chu: customer.ghi_chu || ''
                  });
                }}
                className="px-4 py-2 border rounded-lg hover:bg-gray-50"
              >
                Hủy
              </button>
              <button
                onClick={handleSave}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
              >
                <Save className="w-4 h-4" />
                Lưu
              </button>
            </>
          ) : (
            <button
              onClick={handleClose}
              className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700"
            >
              Đóng
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

