import React, { useState, useEffect } from 'react';
import { Plus, Search, Edit, Trash2, Copy, Eye, Download, Upload } from 'lucide-react';
import * as XLSX from 'xlsx';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';

const removeVietnameseTones = (str) => {
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/đ/g, 'd')
    .replace(/Đ/g, 'D');
};

// Danh sách công đoạn cố định
const CONG_DOAN_MAC_DINH = [
  { ma: 'ma_phim', ten: 'Mã phim', type: 'text', placeholder: 'Mã hóa phim in' },
  { ma: 'so_mau', ten: 'Số màu', type: 'number', placeholder: 'Số màu in' },
  { ma: 'in', ten: 'In', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'boi', ten: 'Bồi', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'can_mang', ten: 'Cán màng', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'ma_khuon', ten: 'Mã khuôn', type: 'text', placeholder: 'Mã khuôn bế' },
  { ma: 'be', ten: 'Bế', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'chap', ten: 'Chạp', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'dong', ten: 'Dóng', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'dan', ten: 'Dán', type: 'number', placeholder: 'Định mức (cái/h)' },
  { ma: 'khac', ten: 'Khác', type: 'number', placeholder: 'Định mức (cái/h)' }
];

function Product() {
  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    return saved ? JSON.parse(saved) : [];
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    return saved ? JSON.parse(saved) : [];
  };

  const [products, setProducts] = useState(loadProducts);
  const [customers] = useState(loadCustomers);
  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const [form, setForm] = useState({
    ma_hang: '',
    ten_san_pham: '',
    khach_hang_id: '',

    // Loại sản phẩm
    loai: 'don',  // 'don' hoặc 'bo' - Mặc định đơn lẻ
    la_bo_san_pham: false,
    thanh_phan_bo: [],  // Array các thành phần

    // Kích thước PO
    po_dai: '',
    po_rong: '',
    po_cao: '',

    // Kích thước SX (để trống = giống PO)
    sx_dai: '',
    sx_rong: '',
    sx_cao: '',

    song: 'B',
    dvt: 'Cái',

    // Giá & Hoa hồng
    don_gia: '',
    hoa_hong_co_dinh: '',
    hoa_hong_phan_tram: '',

    // Giao đủ (mặc định trống)
    cho_phep_sai_lech: false,
    sai_lech_so_luong: '',
    sai_lech_phan_tram: '',

    // Công đoạn
    cong_doan: {},

    // Khác
    kieu: '',
    ghi_chu: ''
  });

  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);

  const getKTSXThucTe = (product) => {
    return {
      dai: product.sx_dai || product.po_dai,
      rong: product.sx_rong || product.po_rong,
      cao: product.sx_cao || product.po_cao
    };
  };

  const handleSubmit = () => {
    if (!form.ma_hang.trim() || !form.ten_san_pham.trim() || !form.khach_hang_id) {
      alert('Vui lòng điền đầy đủ thông tin bắt buộc!');
      return;
    }

    // Validate bộ sản phẩm
    if (form.loai === 'bo' && form.la_bo_san_pham) {
      if (form.thanh_phan_bo.length === 0) {
        alert('Bộ sản phẩm phải có ít nhất 1 thành phần!');
        return;
      }
    }

    // Sản phẩm đơn: phải có kích thước
    if (form.loai === 'don') {
      if (!form.po_dai || !form.po_rong || !form.po_cao) {
        alert('Vui lòng nhập kích thước PO!');
        return;
      }
    }

    // Check duplicate
    const existingCode = products.find(p => 
      p.ma_hang.toUpperCase() === form.ma_hang.toUpperCase().trim() && 
      (!editingProduct || p.id !== editingProduct.id)
    );

    if (existingCode) {
      alert('Mã hàng đã tồn tại!');
      return;
    }

    const productData = {
      ma_hang: form.ma_hang.toUpperCase().trim(),
      ten_san_pham: form.ten_san_pham.trim(),
      khach_hang_id: form.khach_hang_id,
      loai: form.loai,
      la_bo_san_pham: form.loai === 'bo' ? form.la_bo_san_pham : false,
      thanh_phan_bo: form.loai === 'bo' && form.la_bo_san_pham ? form.thanh_phan_bo : [],
      po_dai: parseFloat(form.po_dai) || null,
      po_rong: parseFloat(form.po_rong) || null,
      po_cao: parseFloat(form.po_cao) || null,
      sx_dai: form.sx_dai ? parseFloat(form.sx_dai) : null,
      sx_rong: form.sx_rong ? parseFloat(form.sx_rong) : null,
      sx_cao: form.sx_cao ? parseFloat(form.sx_cao) : null,
      song: form.song,
      dvt: form.dvt,
      don_gia: parseFloat(form.don_gia) || 0,
      hoa_hong_co_dinh: parseFloat(form.hoa_hong_co_dinh) || 0,
      hoa_hong_phan_tram: parseFloat(form.hoa_hong_phan_tram) || 0,
      cho_phep_sai_lech: form.cho_phep_sai_lech,
      sai_lech_so_luong: parseFloat(form.sai_lech_so_luong) || 0,
      sai_lech_phan_tram: parseFloat(form.sai_lech_phan_tram) || 0,
      cong_doan: form.cong_doan,
      kieu: form.kieu,
      ghi_chu: form.ghi_chu.trim()
    };

    if (editingProduct) {
      setProducts(products.map(p =>
        p.id === editingProduct.id ? { ...p, ...productData } : p
      ));
      alert('Đã cập nhật thành phẩm!');
    } else {
      const newProduct = {
        id: Date.now(),
        ...productData,
        trang_thai: 'active',
        ngay_tao: new Date().toISOString()
      };

      setProducts([...products, newProduct]);
      alert('Đã thêm thành phẩm!');
    }

    setShowModal(false);
    setEditingProduct(null);
    resetForm();
  };

  const resetForm = () => {
    setForm({
      ma_hang: '',
      ten_san_pham: '',
      khach_hang_id: '',
      loai: 'don',  // Mặc định đơn lẻ
      la_bo_san_pham: false,
      thanh_phan_bo: [],
      po_dai: '',
      po_rong: '',
      po_cao: '',
      sx_dai: '',
      sx_rong: '',
      sx_cao: '',
      song: 'B',
      dvt: 'Cái',
      don_gia: '',
      hoa_hong_co_dinh: '',
      hoa_hong_phan_tram: '',
      cho_phep_sai_lech: false,
      sai_lech_so_luong: '',
      sai_lech_phan_tram: '',
      cong_doan: {},
      kieu: '',
      ghi_chu: ''
    });
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setForm({
      ma_hang: product.ma_hang,
      ten_san_pham: product.ten_san_pham,
      khach_hang_id: product.khach_hang_id,
      loai: product.loai || 'don',
      la_bo_san_pham: product.la_bo_san_pham || false,
      thanh_phan_bo: product.thanh_phan_bo || [],
      po_dai: product.po_dai || '',
      po_rong: product.po_rong || '',
      po_cao: product.po_cao || '',
      sx_dai: product.sx_dai || '',
      sx_rong: product.sx_rong || '',
      sx_cao: product.sx_cao || '',
      song: product.song,
      dvt: product.dvt,
      don_gia: product.don_gia,
      hoa_hong_co_dinh: product.hoa_hong_co_dinh,
      hoa_hong_phan_tram: product.hoa_hong_phan_tram,
      cho_phep_sai_lech: product.cho_phep_sai_lech,
      sai_lech_so_luong: product.sai_lech_so_luong || '',
      sai_lech_phan_tram: product.sai_lech_phan_tram || '',
      cong_doan: product.cong_doan || {},
      kieu: product.kieu || '',
      ghi_chu: product.ghi_chu || ''
    });
    setShowModal(true);
  };

  const handleDelete = (product) => {
    if (window.confirm(`Xóa thành phẩm "${product.ten_san_pham}"?`)) {
      setProducts(products.filter(p => p.id !== product.id));
    }
  };

  const handleDuplicate = (product) => {
    setEditingProduct(null);
    setForm({
      ma_hang: product.ma_hang + '-COPY',
      ten_san_pham: product.ten_san_pham + ' (Copy)',
      khach_hang_id: product.khach_hang_id,
      loai: product.loai || 'don',
      la_bo_san_pham: product.la_bo_san_pham || false,
      thanh_phan_bo: product.thanh_phan_bo || [],
      po_dai: product.po_dai || '',
      po_rong: product.po_rong || '',
      po_cao: product.po_cao || '',
      sx_dai: product.sx_dai || '',
      sx_rong: product.sx_rong || '',
      sx_cao: product.sx_cao || '',
      song: product.song,
      dvt: product.dvt,
      don_gia: product.don_gia,
      hoa_hong_co_dinh: product.hoa_hong_co_dinh,
      hoa_hong_phan_tram: product.hoa_hong_phan_tram,
      cho_phep_sai_lech: product.cho_phep_sai_lech,
      sai_lech_so_luong: product.sai_lech_so_luong || '',
      sai_lech_phan_tram: product.sai_lech_phan_tram || '',
      cong_doan: product.cong_doan || {},
      kieu: product.kieu || '',
      ghi_chu: product.ghi_chu || ''
    });
    setShowModal(true);
  };

  const handleExportExcel = () => {
    if (products.length === 0) {
      alert('Không có dữ liệu để xuất!');
      return;
    }

    const data = products.map(p => {
      const ktsx = getKTSXThucTe(p);
      const customer = customers.find(c => c.id === p.khach_hang_id);
      return {
        'Mã hàng KH': p.ma_hang,
        'Tên sản phẩm': p.ten_san_pham,
        'Khách hàng': customer ? customer.name : '',
        'PO Dài': p.po_dai,
        'PO Rộng': p.po_rong,
        'PO Cao': p.po_cao,
        'SX Dài': ktsx.dai,
        'SX Rộng': ktsx.rong,
        'SX Cao': ktsx.cao,
        'Sóng': p.song,
        'ĐVT': p.dvt,
        'Đơn giá': p.don_gia,
        'HH cố định': p.hoa_hong_co_dinh,
        'HH %': p.hoa_hong_phan_tram,
        'Giao đủ ±SL': p.sai_lech_so_luong || '',
        'Giao đủ ±%': p.sai_lech_phan_tram || '',
        'Kiểu': p.kieu,
        'Ghi chú': p.ghi_chu
      };
    });

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Thành phẩm');
    XLSX.writeFile(wb, 'Danh-muc-thanh-pham.xlsx');
  };

  const filteredProducts = products.filter(p => {
    if (!searchTerm) return true;
    const searchLower = removeVietnameseTones(searchTerm.toLowerCase());
    const customer = customers.find(c => c.id === p.khach_hang_id);
    const searchableFields = [
      p.ma_hang,
      p.ten_san_pham,
      customer ? customer.name : '',
      p.ghi_chu || ''
    ];
    return searchableFields.some(field =>
      removeVietnameseTones(field.toLowerCase()).includes(searchLower)
    );
  }).sort((a, b) => a.ma_hang.localeCompare(b.ma_hang));

  const getCustomerName = (customerId) => {
    const customer = customers.find(c => c.id === customerId);
    return customer ? customer.name : 'N/A';
  };

  return (
    <div className="p-6 space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Thành phẩm</h1>
          <p className="text-gray-600">Quản lý danh mục sản phẩm khách hàng</p>
        </div>
        <div className="flex gap-2">
          <button
            onClick={handleExportExcel}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
          >
            <Download className="w-5 h-5" />Xuất Excel
          </button>
          <button
            onClick={() => {
              setEditingProduct(null);
              resetForm();
              setShowModal(true);
            }}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2"
          >
            <Plus className="w-5 h-5" />Thêm thành phẩm
          </button>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-sm border p-4">
        <div>
          <label className="block text-sm font-medium mb-2">Tìm kiếm</label>
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Mã hàng, tên sản phẩm, khách hàng..."
              className="w-full pl-10 pr-4 py-2 border rounded-lg"
            />
          </div>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-sm border overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Mã hàng KH</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Tên sản phẩm</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Khách hàng</th>
                <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase">Loại</th>
                <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase">KTSX</th>
                <th className="px-4 py-3 text-right text-xs font-medium text-gray-500 uppercase">Đơn giá</th>
                <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase">Giao đủ</th>
                <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase">Thao tác</th>
              </tr>
            </thead>
            <tbody className="divide-y">
              {filteredProducts.map(product => {
                const ktsx = getKTSXThucTe(product);
                return (
                  <tr key={product.id} className="hover:bg-gray-50">
                    <td className="px-4 py-4 text-sm font-bold text-blue-600">{product.ma_hang}</td>
                    <td className="px-4 py-4 text-sm font-medium">{product.ten_san_pham}</td>
                    <td className="px-4 py-4 text-sm">{getCustomerName(product.khach_hang_id)}</td>
                    <td className="px-4 py-4 text-center">
                      {product.loai === 'bo' ? (
                        <span className="text-xs bg-purple-100 text-purple-700 px-2 py-1 rounded font-medium">
                          Bộ ({product.thanh_phan_bo?.length || 0})
                        </span>
                      ) : (
                        <span className="text-xs bg-gray-100 text-gray-700 px-2 py-1 rounded">
                          Đơn lẻ
                        </span>
                      )}
                    </td>
                    <td className="px-4 py-4 text-sm text-center">
                      {product.loai === 'don' ? (
                        <span className="text-xs bg-blue-50 text-blue-700 px-2 py-1 rounded">
                          {ktsx.dai}×{ktsx.rong}×{ktsx.cao}
                        </span>
                      ) : (
                        <span className="text-xs text-gray-400">-</span>
                      )}
                    </td>
                    <td className="px-4 py-4 text-sm text-right font-medium text-green-600">
                      {product.don_gia.toLocaleString('vi-VN')}
                    </td>
                    <td className="px-4 py-4 text-center">
                      {product.cho_phep_sai_lech ? (
                        <span className="text-xs bg-orange-50 text-orange-700 px-2 py-1 rounded">
                          ±{product.sai_lech_so_luong || product.sai_lech_phan_tram + '%'}
                        </span>
                      ) : (
                        <span className="text-xs text-gray-400">-</span>
                      )}
                    </td>
                    <td className="px-4 py-4">
                      <div className="flex justify-center gap-2">
                        <button
                          onClick={() => {
                            setSelectedProduct(product);
                            setShowDetailModal(true);
                          }}
                          className="p-2 text-purple-600 hover:bg-purple-50 rounded-lg"
                          title="Xem chi tiết"
                        >
                          <Eye className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleDuplicate(product)}
                          className="p-2 text-green-600 hover:bg-green-50 rounded-lg"
                          title="Nhân bản"
                        >
                          <Copy className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleEdit(product)}
                          className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                          title="Sửa"
                        >
                          <Edit className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleDelete(product)}
                          className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                          title="Xóa"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        {filteredProducts.length === 0 && (
          <div className="text-center py-12 text-gray-500">Không có dữ liệu</div>
        )}
      </div>

      {/* Add/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-6xl max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10">
              <h2 className="text-xl font-bold">
                {editingProduct ? 'Sửa thành phẩm' : 'Thêm thành phẩm'}
              </h2>
            </div>
            <div className="p-6 space-y-6">
              {/* Thông tin cơ bản */}
              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Mã hàng KH *</label>
                  <input
                    type="text"
                    value={form.ma_hang}
                    onChange={(e) => setForm({ ...form, ma_hang: e.target.value.toUpperCase() })}
                    placeholder="VD: AD-T 1230-JETUNE"
                    className="w-full px-4 py-2 border rounded-lg uppercase"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Tên sản phẩm *</label>
                  <TextInput
                    value={form.ten_san_pham}
                    onChange={(value) => setForm({ ...form, ten_san_pham: value })}
                    placeholder="VD: Thùng carton"
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Khách hàng *</label>
                  <select
                    value={form.khach_hang_id}
                    onChange={(e) => setForm({ ...form, khach_hang_id: e.target.value })}
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">Chọn khách hàng</option>
                    {customers.map(c => (
                      <option key={c.id} value={c.id}>{c.code} - {c.name}</option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Loại sản phẩm */}
              <div className="border rounded-lg p-4 bg-yellow-50">
                <h3 className="font-bold mb-3">Loại sản phẩm</h3>
                <div className="flex items-center gap-6">
                  <label className="flex items-center gap-2 cursor-pointer">
                    <input
                      type="radio"
                      checked={form.loai === 'don'}
                      onChange={() => setForm({ ...form, loai: 'don', la_bo_san_pham: false, thanh_phan_bo: [] })}
                      className="w-4 h-4"
                    />
                    <span className="text-sm font-medium">Đơn lẻ</span>
                  </label>
                  <label className="flex items-center gap-2 cursor-pointer">
                    <input
                      type="radio"
                      checked={form.loai === 'bo'}
                      onChange={() => setForm({ ...form, loai: 'bo' })}
                      className="w-4 h-4"
                    />
                    <span className="text-sm font-medium">Bộ sản phẩm</span>
                  </label>
                </div>

                {/* Thành phần bộ */}
                {form.loai === 'bo' && (
                  <div className="mt-4 border-t pt-4">
                    <div className="flex items-center gap-2 mb-3">
                      <input
                        type="checkbox"
                        id="la_bo_san_pham"
                        checked={form.la_bo_san_pham}
                        onChange={(e) => setForm({ ...form, la_bo_san_pham: e.target.checked })}
                        className="w-4 h-4"
                      />
                      <label htmlFor="la_bo_san_pham" className="text-sm font-medium">
                        Cấu hình thành phần của bộ
                      </label>
                    </div>

                    {form.la_bo_san_pham && (
                      <div className="space-y-3">
                        {form.thanh_phan_bo.map((tp, index) => (
                          <div key={index} className="flex items-center gap-2 bg-white p-3 rounded-lg border">
                            <select
                              value={tp.thanh_pham_id || ''}
                              onChange={(e) => {
                                const selectedId = e.target.value;
                                const selectedProduct = products.find(p => p.id === parseInt(selectedId));
                                if (selectedProduct) {
                                  const newThanhPhan = [...form.thanh_phan_bo];
                                  newThanhPhan[index] = {
                                    ...newThanhPhan[index],
                                    thanh_pham_id: selectedProduct.id,
                                    ma_hang: selectedProduct.ma_hang,
                                    ten: selectedProduct.ten_san_pham
                                  };
                                  setForm({ ...form, thanh_phan_bo: newThanhPhan });
                                }
                              }}
                              className="flex-1 px-3 py-2 border rounded-lg text-sm"
                            >
                              <option value="">Chọn thành phẩm</option>
                              {products.filter(p => p.loai !== 'bo').map(p => (
                                <option key={p.id} value={p.id}>{p.ma_hang} - {p.ten_san_pham}</option>
                              ))}
                            </select>
                            <input
                              type="number"
                              value={tp.so_luong || 1}
                              onChange={(e) => {
                                const newThanhPhan = [...form.thanh_phan_bo];
                                newThanhPhan[index].so_luong = parseInt(e.target.value) || 1;
                                setForm({ ...form, thanh_phan_bo: newThanhPhan });
                              }}
                              placeholder="SL"
                              className="w-20 px-3 py-2 border rounded-lg text-sm"
                              min="1"
                            />
                            <button
                              onClick={() => {
                                const newThanhPhan = form.thanh_phan_bo.filter((_, i) => i !== index);
                                setForm({ ...form, thanh_phan_bo: newThanhPhan });
                              }}
                              className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          </div>
                        ))}

                        <button
                          onClick={() => {
                            setForm({
                              ...form,
                              thanh_phan_bo: [...form.thanh_phan_bo, { thanh_pham_id: '', ma_hang: '', ten: '', so_luong: 1 }]
                            });
                          }}
                          className="w-full px-4 py-2 border-2 border-dashed border-blue-300 text-blue-600 rounded-lg hover:bg-blue-50 flex items-center justify-center gap-2"
                        >
                          <Plus className="w-4 h-4" />Thêm thành phần
                        </button>

                        {form.thanh_phan_bo.length > 0 && (
                          <div className="bg-blue-50 p-3 rounded-lg text-xs">
                            <p className="font-medium text-blue-900 mb-1">💡 Ví dụ: Đơn hàng 1000 bộ</p>
                            <div className="space-y-1 text-blue-700">
                              {form.thanh_phan_bo.map((tp, idx) => (
                                tp.ten && (
                                  <div key={idx}>
                                    • {tp.ten}: 1000 × {tp.so_luong} = {1000 * tp.so_luong} cái
                                  </div>
                                )
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                )}
              </div>

              {/* Kích thước - Chỉ hiện khi là Đơn lẻ */}
              {form.loai === 'don' && (
                <div className="border rounded-lg p-4 bg-blue-50">
                <h3 className="font-bold mb-4">Kích thước</h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Kích thước PO (Khách hàng) *</label>
                    <div className="grid grid-cols-5 gap-2">
                      <input
                        type="number"
                        value={form.po_dai}
                        onChange={(e) => setForm({ ...form, po_dai: e.target.value })}
                        placeholder="Dài"
                        className="px-3 py-2 border rounded-lg"
                      />
                      <input
                        type="number"
                        value={form.po_rong}
                        onChange={(e) => setForm({ ...form, po_rong: e.target.value })}
                        placeholder="Rộng"
                        className="px-3 py-2 border rounded-lg"
                      />
                      <input
                        type="number"
                        value={form.po_cao}
                        onChange={(e) => setForm({ ...form, po_cao: e.target.value })}
                        placeholder="Cao"
                        className="px-3 py-2 border rounded-lg"
                      />
                      <select
                        value={form.song}
                        onChange={(e) => setForm({ ...form, song: e.target.value })}
                        className="px-3 py-2 border rounded-lg"
                      >
                        <option value="B">B</option>
                        <option value="C">C</option>
                        <option value="E">E</option>
                      </select>
                      <TextInput
                        value={form.dvt}
                        onChange={(value) => setForm({ ...form, dvt: value })}
                        placeholder="ĐVT"
                        className="px-3 py-2 border rounded-lg"
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-2">
                      Kích thước SX (Để trống nếu giống PO)
                    </label>
                    <div className="grid grid-cols-3 gap-2">
                      <input
                        type="number"
                        value={form.sx_dai}
                        onChange={(e) => setForm({ ...form, sx_dai: e.target.value })}
                        placeholder="Dài"
                        className="px-3 py-2 border rounded-lg"
                      />
                      <input
                        type="number"
                        value={form.sx_rong}
                        onChange={(e) => setForm({ ...form, sx_rong: e.target.value })}
                        placeholder="Rộng"
                        className="px-3 py-2 border rounded-lg"
                      />
                      <input
                        type="number"
                        value={form.sx_cao}
                        onChange={(e) => setForm({ ...form, sx_cao: e.target.value })}
                        placeholder="Cao"
                        className="px-3 py-2 border rounded-lg"
                      />
                    </div>
                    {(form.po_dai && form.po_rong && form.po_cao) && (
                      <p className="text-xs text-blue-600 mt-2">
                        💡 KTSX thực tế: {form.sx_dai || form.po_dai} × {form.sx_rong || form.po_rong} × {form.sx_cao || form.po_cao}
                      </p>
                    )}
                  </div>
                </div>
              </div>
              )}
              {/* Giá & Hoa hồng */}
              <div className="border rounded-lg p-4 bg-green-50">
                <h3 className="font-bold mb-4">Giá & Hoa hồng</h3>
                <div className="grid grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Đơn giá</label>
                    <input
                      type="number"
                      value={form.don_gia}
                      onChange={(e) => setForm({ ...form, don_gia: e.target.value })}
                      placeholder="0"
                      className="w-full px-4 py-2 border rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">HH cố định (đ/cái)</label>
                    <input
                      type="number"
                      value={form.hoa_hong_co_dinh}
                      onChange={(e) => setForm({ ...form, hoa_hong_co_dinh: e.target.value })}
                      placeholder="0"
                      className="w-full px-4 py-2 border rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">HH % (%)</label>
                    <input
                      type="number"
                      value={form.hoa_hong_phan_tram}
                      onChange={(e) => setForm({ ...form, hoa_hong_phan_tram: e.target.value })}
                      placeholder="0"
                      className="w-full px-4 py-2 border rounded-lg"
                    />
                  </div>
                </div>
              </div>

              {/* Giao đủ */}
              <div className="border rounded-lg p-4 bg-orange-50">
                <h3 className="font-bold mb-4">Điều kiện giao hàng (Mặc định)</h3>
                <div className="space-y-3">
                  <div className="flex items-center gap-2">
                    <input
                      type="checkbox"
                      id="cho_phep_sai_lech"
                      checked={form.cho_phep_sai_lech}
                      onChange={(e) => setForm({ ...form, cho_phep_sai_lech: e.target.checked })}
                      className="w-4 h-4"
                    />
                    <label htmlFor="cho_phep_sai_lech" className="text-sm font-medium">
                      Cho phép sai lệch
                    </label>
                  </div>
                  {form.cho_phep_sai_lech && (
                    <div className="grid grid-cols-2 gap-4 ml-6">
                      <div>
                        <label className="block text-sm mb-2">Sai lệch số lượng (±cái)</label>
                        <input
                          type="number"
                          value={form.sai_lech_so_luong}
                          onChange={(e) => setForm({ ...form, sai_lech_so_luong: e.target.value })}
                          placeholder="VD: 50"
                          className="w-full px-3 py-2 border rounded-lg"
                        />
                      </div>
                      <div>
                        <label className="block text-sm mb-2">Sai lệch phần trăm (±%)</label>
                        <input
                          type="number"
                          value={form.sai_lech_phan_tram}
                          onChange={(e) => setForm({ ...form, sai_lech_phan_tram: e.target.value })}
                          placeholder="VD: 5"
                          className="w-full px-3 py-2 border rounded-lg"
                        />
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Công đoạn */}
              <div className="border rounded-lg p-4 bg-purple-50">
                <h3 className="font-bold mb-4">Công đoạn sản xuất</h3>
                <div className="grid grid-cols-2 gap-4">
                  {CONG_DOAN_MAC_DINH.map(cd => (
                    <div key={cd.ma} className="flex items-center gap-2">
                      <label className="w-28 text-sm font-medium">{cd.ten}:</label>
                      {cd.type === 'text' ? (
                        <TextInput
                          value={form.cong_doan[cd.ma] || ''}
                          onChange={(value) => setForm({
                            ...form,
                            cong_doan: { ...form.cong_doan, [cd.ma]: value }
                          })}
                          placeholder={cd.placeholder}
                          className="flex-1 px-3 py-2 border rounded-lg text-sm"
                        />
                      ) : (
                        <input
                          type="number"
                          value={form.cong_doan[cd.ma] || ''}
                          onChange={(e) => setForm({
                            ...form,
                            cong_doan: { ...form.cong_doan, [cd.ma]: e.target.value }
                          })}
                          placeholder={cd.placeholder}
                          className="flex-1 px-3 py-2 border rounded-lg text-sm"
                        />
                      )}
                    </div>
                  ))}
                </div>
                <p className="text-xs text-gray-600 mt-3">
                  💡 <strong>Mã phim:</strong> Mã hóa phim in sản phẩm (nếu có) • 
                  <strong> Số màu:</strong> Số màu in lên sản phẩm • 
                  <strong> Mã khuôn:</strong> Mã khuôn bế (nếu có) • 
                  <strong> Định mức:</strong> Số lượng sản xuất/giờ (cái/h)
                </p>
              </div>

              {/* Khác */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Kiểu</label>
                  <TextInput
                    value={form.kieu}
                    onChange={(value) => setForm({ ...form, kieu: value })}
                    placeholder="VD: A5"
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Ghi chú</label>
                  <TextArea
                    value={form.ghi_chu}
                    onChange={(value) => setForm({ ...form, ghi_chu: value })}
                    placeholder="Ghi chú thêm..."
                    className="w-full px-4 py-2 border rounded-lg"
                    rows="1"
                  />
                </div>
              </div>
            </div>
            <div className="border-t px-6 py-4 flex justify-end gap-2 sticky bottom-0 bg-white">
              <button
                onClick={() => {
                  setShowModal(false);
                  setEditingProduct(null);
                  resetForm();
                }}
                className="px-4 py-2 border rounded-lg hover:bg-gray-50"
              >
                Hủy
              </button>
              <button
                onClick={handleSubmit}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
              >
                {editingProduct ? 'Cập nhật' : 'Thêm'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Detail Modal */}
      {showDetailModal && selectedProduct && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10">
              <h2 className="text-xl font-bold">Chi tiết thành phẩm</h2>
            </div>
            <div className="p-6 space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-600">Mã hàng KH</p>
                  <p className="font-bold text-blue-600">{selectedProduct.ma_hang}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Tên sản phẩm</p>
                  <p className="font-medium">{selectedProduct.ten_san_pham}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Khách hàng</p>
                  <p className="font-medium">{getCustomerName(selectedProduct.khach_hang_id)}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">KTSX thực tế</p>
                  <p className="font-medium">
                    {(() => {
                      const ktsx = getKTSXThucTe(selectedProduct);
                      return `${ktsx.dai} × ${ktsx.rong} × ${ktsx.cao}`;
                    })()}
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Đơn giá</p>
                  <p className="font-bold text-green-600">{selectedProduct.don_gia.toLocaleString('vi-VN')} đ</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Hoa hồng</p>
                  <p className="font-medium">
                    {selectedProduct.hoa_hong_co_dinh > 0 && `${selectedProduct.hoa_hong_co_dinh.toLocaleString('vi-VN')} đ/cái`}
                    {selectedProduct.hoa_hong_co_dinh > 0 && selectedProduct.hoa_hong_phan_tram > 0 && ' / '}
                    {selectedProduct.hoa_hong_phan_tram > 0 && `${selectedProduct.hoa_hong_phan_tram}%`}
                  </p>
                </div>
              </div>

              {selectedProduct.cho_phep_sai_lech && (
                <div className="border-t pt-4">
                  <p className="text-sm font-medium mb-2">Điều kiện giao hàng</p>
                  <p className="text-sm">
                    Cho phép sai lệch: ±{selectedProduct.sai_lech_so_luong || 0} cái hoặc ±{selectedProduct.sai_lech_phan_tram || 0}%
                  </p>
                </div>
              )}

              {Object.keys(selectedProduct.cong_doan || {}).length > 0 && (
                <div className="border-t pt-4">
                  <p className="text-sm font-medium mb-2">Công đoạn sản xuất</p>
                  <div className="grid grid-cols-2 gap-2">
                    {CONG_DOAN_MAC_DINH.map(cd => {
                      const value = selectedProduct.cong_doan[cd.ma];
                      if (!value) return null;
                      return (
                        <div key={cd.ma} className="text-sm">
                          <span className="font-medium">{cd.ten}:</span> {value}
                        </div>
                      );
                    })}
                  </div>
                </div>
              )}
            </div>
            <div className="border-t px-6 py-4 flex justify-end">
              <button
                onClick={() => {
                  setShowDetailModal(false);
                  setSelectedProduct(null);
                }}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
              >
                Đóng
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Product;
