import React, { useState, useEffect } from 'react';
import { Save, Edit, Trash2, Plus, X, ChevronDown, ChevronUp } from 'lucide-react';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';

// ============================================================================
// CONSTANTS - 13 CÃ”NG ÄOáº N (Xáº¢ LÃ€ Äáº¦U TIÃŠN!)
// ============================================================================

const CONG_DOAN_MAC_DINH = {
  xa: 0,                    // â­ Xáº¢ - CÃ´ng Ä‘oáº¡n Ä‘áº§u tiÃªn
  ma_phim: '',
  ma_phim_file: null,       // ðŸ”® Upload file (tÆ°Æ¡ng lai)
  ma_phim_file_name: null,
  ma_phim_file_type: null,
  so_mau: 0,
  in: 0,
  boi: 0,
  can_mang: 0,             // âš ï¸ CÃ¡n mÃ ng (KHÃ”NG pháº£i "Cáº¡n")
  ma_khuon: '',
  ma_khuon_file: null,      // ðŸ”® Upload file (tÆ°Æ¡ng lai)
  ma_khuon_file_name: null,
  ma_khuon_file_type: null,
  be: 0,
  chap: 0,
  dong: 0,                  // âš ï¸ ÄÃ³ng (KHÃ”NG pháº£i "DÃ³ng")
  dan: 0,
  khac: 0,
  khac_ghi_chu: ''          // MÃ´ táº£ cÃ´ng viá»‡c "KhÃ¡c"
};

const DVT_OPTIONS = ['CÃ¡i', 'Bá»™', 'Táº¥m', 'Cuá»™n', 'Kg'];
const SONG_OPTIONS = ['E', 'B', 'C', 'BC', 'EB', 'AC', 'AA'];
const KIEU_OPTIONS = ['A5', 'A6', 'B1', 'B2', 'C1', 'C2', 'K1', 'K2'];

// ============================================================================
// MAIN COMPONENT
// ============================================================================

function Product() {
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [expandedProductIds, setExpandedProductIds] = useState(new Set());

  // Form state
  const [form, setForm] = useState({
    ma_hang: '',
    ten_san_pham: '',
    khach_hang_id: '',

    // KÃ­ch thÆ°á»›c PO
    po_dai: '',
    po_rong: '',
    po_cao: '',

    // KÃ­ch thÆ°á»›c SX (null = giá»‘ng PO)
    sx_dai: '',
    sx_rong: '',
    sx_cao: '',

    song: 'B',
    dvt: 'CÃ¡i',

    // GiÃ¡ & Hoa há»“ng
    don_gia: '',
    hoa_hong_co_dinh: '',
    hoa_hong_phan_tram: '',

    // Giao Ä‘á»§ (máº·c Ä‘á»‹nh)
    cho_phep_sai_lech: false,
    sai_lech_so_luong: '',
    sai_lech_phan_tram: '',

    // 13 cÃ´ng Ä‘oáº¡n
    cong_doan: { ...CONG_DOAN_MAC_DINH },

    kieu: 'A5',
    ghi_chu: '',

    // ThÃ nh pháº§n con
    co_thanh_phan_con: false,
    thanh_phan_con: []
  });

  // ============================================================================
  // LOAD DATA
  // ============================================================================

  useEffect(() => {
    loadProducts();
    loadCustomers();
  }, []);

  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    if (saved) {
      setProducts(JSON.parse(saved));
    }
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    if (saved) {
      setCustomers(JSON.parse(saved));
    }
  };

  const saveProducts = (data) => {
    localStorage.setItem('products', JSON.stringify(data));
    setProducts(data);
  };

  // ============================================================================
  // FORM HANDLERS
  // ============================================================================

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation
    if (!form.ma_hang.trim()) {
      alert('Vui lÃ²ng nháº­p MÃ£ hÃ ng');
      return;
    }
    if (!form.ten_san_pham.trim()) {
      alert('Vui lÃ²ng nháº­p TÃªn sáº£n pháº©m');
      return;
    }
    if (!form.khach_hang_id) {
      alert('Vui lÃ²ng chá»n KhÃ¡ch hÃ ng');
      return;
    }

    const productData = {
      ...form,
      id: editingId || Date.now(),

      // Convert to numbers
      po_dai: parseFloat(form.po_dai) || 0,
      po_rong: parseFloat(form.po_rong) || 0,
      po_cao: parseFloat(form.po_cao) || 0,

      sx_dai: form.sx_dai ? parseFloat(form.sx_dai) : null,
      sx_rong: form.sx_rong ? parseFloat(form.sx_rong) : null,
      sx_cao: form.sx_cao ? parseFloat(form.sx_cao) : null,

      don_gia: parseFloat(form.don_gia) || 0,
      hoa_hong_co_dinh: parseFloat(form.hoa_hong_co_dinh) || 0,
      hoa_hong_phan_tram: parseFloat(form.hoa_hong_phan_tram) || 0,

      sai_lech_so_luong: parseFloat(form.sai_lech_so_luong) || 0,
      sai_lech_phan_tram: parseFloat(form.sai_lech_phan_tram) || 0,

      // CÃ´ng Ä‘oáº¡n
      cong_doan: {
        xa: parseFloat(form.cong_doan.xa) || 0,
        ma_phim: form.cong_doan.ma_phim,
        ma_phim_file: form.cong_doan.ma_phim_file,
        ma_phim_file_name: form.cong_doan.ma_phim_file_name,
        ma_phim_file_type: form.cong_doan.ma_phim_file_type,
        so_mau: parseFloat(form.cong_doan.so_mau) || 0,
        in: parseFloat(form.cong_doan.in) || 0,
        boi: parseFloat(form.cong_doan.boi) || 0,
        can_mang: parseFloat(form.cong_doan.can_mang) || 0,
        ma_khuon: form.cong_doan.ma_khuon,
        ma_khuon_file: form.cong_doan.ma_khuon_file,
        ma_khuon_file_name: form.cong_doan.ma_khuon_file_name,
        ma_khuon_file_type: form.cong_doan.ma_khuon_file_type,
        be: parseFloat(form.cong_doan.be) || 0,
        chap: parseFloat(form.cong_doan.chap) || 0,
        dong: parseFloat(form.cong_doan.dong) || 0,
        dan: parseFloat(form.cong_doan.dan) || 0,
        khac: parseFloat(form.cong_doan.khac) || 0,
        khac_ghi_chu: form.cong_doan.khac_ghi_chu
      }
    };

    if (editingId) {
      saveProducts(products.map(p => p.id === editingId ? productData : p));
    } else {
      saveProducts([...products, productData]);
    }

    handleCloseForm();
  };

  const handleEdit = (product) => {
    setForm({
      ...product,
      po_dai: product.po_dai.toString(),
      po_rong: product.po_rong.toString(),
      po_cao: product.po_cao.toString(),
      sx_dai: product.sx_dai ? product.sx_dai.toString() : '',
      sx_rong: product.sx_rong ? product.sx_rong.toString() : '',
      sx_cao: product.sx_cao ? product.sx_cao.toString() : '',
      don_gia: product.don_gia.toString(),
      hoa_hong_co_dinh: product.hoa_hong_co_dinh.toString(),
      hoa_hong_phan_tram: product.hoa_hong_phan_tram.toString(),
      sai_lech_so_luong: product.sai_lech_so_luong.toString(),
      sai_lech_phan_tram: product.sai_lech_phan_tram.toString(),
      cong_doan: {
        ...product.cong_doan,
        xa: product.cong_doan.xa.toString(),
        so_mau: product.cong_doan.so_mau.toString(),
        in: product.cong_doan.in.toString(),
        boi: product.cong_doan.boi.toString(),
        can_mang: product.cong_doan.can_mang.toString(),
        be: product.cong_doan.be.toString(),
        chap: product.cong_doan.chap.toString(),
        dong: product.cong_doan.dong.toString(),
        dan: product.cong_doan.dan.toString(),
        khac: product.cong_doan.khac.toString()
      }
    });
    setEditingId(product.id);
    setIsFormOpen(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a sáº£n pháº©m nÃ y?')) {
      saveProducts(products.filter(p => p.id !== id));
    }
  };

  const handleCloseForm = () => {
    setForm({
      ma_hang: '',
      ten_san_pham: '',
      khach_hang_id: '',
      po_dai: '',
      po_rong: '',
      po_cao: '',
      sx_dai: '',
      sx_rong: '',
      sx_cao: '',
      song: 'B',
      dvt: 'CÃ¡i',
      don_gia: '',
      hoa_hong_co_dinh: '',
      hoa_hong_phan_tram: '',
      cho_phep_sai_lech: false,
      sai_lech_so_luong: '',
      sai_lech_phan_tram: '',
      cong_doan: { ...CONG_DOAN_MAC_DINH },
      kieu: 'A5',
