import React, { useState, useEffect, useRef } from 'react';
import { Plus, Search, Edit, Trash2, Eye, Download, ShoppingCart, Package, X } from 'lucide-react';
import * as XLSX from 'xlsx';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';
import LinkedField from '../components/LinkedField';
import CustomerDetailModal from '../components/CustomerDetailModal';
import ProductDetailModal from '../components/ProductDetailModal';
import FilterBox from '../components/FilterBox';
import ExcelActions from '../components/ExcelActions';

const removeVietnameseTones = (str) => {
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/Ä‘/g, 'd')
    .replace(/Ä/g, 'D');
};

function SalesOrder() {
  const loadOrders = () => {
    const saved = localStorage.getItem('salesOrders');
    return saved ? JSON.parse(saved) : [];
  };

  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    return saved ? JSON.parse(saved) : [];
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    const parsed = saved ? JSON.parse(saved) : [];
    console.log('ðŸ“¦ Load customers:', parsed.length, 'items');
    return parsed;
  };

  const [orders, setOrders] = useState(loadOrders);
  const [products, setProducts] = useState(loadProducts);
  const [customers, setCustomers] = useState(loadCustomers);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [editingOrder, setEditingOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  const [showCustomerModal, setShowCustomerModal] = useState(false);
  const [selectedCustomerId, setSelectedCustomerId] = useState(null);
  const [showProductModal, setShowProductModal] = useState(false);
  const [selectedProductIdModal, setSelectedProductIdModal] = useState(null);

  const [form, setForm] = useState({
    so_don_hang: '',
    khach_hang_id: '',
    ngay_dat: new Date().toISOString().split('T')[0],
    ngay_giao_du_kien: '',
    chi_tiet: [],
    ghi_chu: ''
  });

  const [selectedProductIdForAdd, setSelectedProductIdForAdd] = useState('');
  const firstQuantityInputRef = useRef(null);

  // Reload customers khi má»Ÿ modal
  useEffect(() => {
    if (showModal) {
      const reloaded = loadCustomers();
      setCustomers(reloaded);
    }
  }, [showModal]);

  useEffect(() => {
    localStorage.setItem('salesOrders', JSON.stringify(orders));
  }, [orders]);

  const getCustomerName = (khach_hang_id) => {
    const customer = customers.find(c => c.id === khach_hang_id);
    return customer?.ten_cong_ty || customer?.ten || 'N/A';
  };

  const getCustomerCode = (khach_hang_id) => {
    const customer = customers.find(c => c.id === khach_hang_id);
    return customer?.ma_khach_hang || 'N/A';
  };

  const generateOrderNumber = () => {
    const today = new Date();
    const year = today.getFullYear().toString().slice(-2);
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const existingOrders = orders.filter(o => 
      o.so_don_hang.startsWith(`DH${year}${month}`)
    );
    const nextNum = String(existingOrders.length + 1).padStart(3, '0');
    return `DH${year}${month}${nextNum}`;
  };

  const handleAddProduct = () => {
    if (!selectedProductIdForAdd) {
      alert('Vui lÃ²ng chá»n thÃ nh pháº©m!');
      return;
    }

    const product = products.find(p => p.id === parseInt(selectedProductIdForAdd));
    if (!product) return;

    const newItems = [];

    // TÃ­nh KTSX
    const ktsx = product.sx_dai || product.sx_rong || product.sx_cao
      ? `${product.sx_dai || product.po_dai}Ã—${product.sx_rong || product.po_rong}${(product.sx_cao || product.po_cao) ? `Ã—${product.sx_cao || product.po_cao}` : ''}`
      : `${product.po_dai}Ã—${product.po_rong}${product.po_cao ? `Ã—${product.po_cao}` : ''}`;

    // TÃ­nh KTPO
    const ktpo = `${product.po_dai}Ã—${product.po_rong}${product.po_cao ? `Ã—${product.po_cao}` : ''}`;

    // Sáº£n pháº©m máº¹ - Láº¤Y Táº¤T Cáº¢ FIELDS
    const mainItem = {
      id: Date.now(),
      thanh_pham_id: product.id,

      // BASIC INFO
      ma_hang: product.ma_hang,
      ten_san_pham: product.ten_san_pham,

      // DIMENSIONS
      ktsx: ktsx,
      ktpo: ktpo,
      po_dai: product.po_dai,
      po_rong: product.po_rong,
      po_cao: product.po_cao,
      sx_dai: product.sx_dai,
      sx_rong: product.sx_rong,
      sx_cao: product.sx_cao,

      // PRODUCT SPECS
      song: product.song,
      dvt: product.dvt,
      kieu: product.kieu,
      so_mau: product.so_mau,

      // PRICING & QUANTITY
      don_gia: product.don_gia || 0,
      so_luong: '',
      thanh_tien: 0,
      so_luong_sai_lech: product.so_luong_sai_lech || 0,

      // PRODUCTION
      ma_phim: product.cong_doan?.ma_phim || '',
      ma_khuon: product.cong_doan?.ma_khuon || '',
      cong_doan: product.cong_doan ? {
        in_khuon: product.cong_doan.in_khuon || '',
        be: product.cong_doan.be || '',
        dan: product.cong_doan.dan || '',
        ghep: product.cong_doan.ghep || '',
        ep_keo: product.cong_doan.ep_keo || '',
        ma_phim: product.cong_doan.ma_phim || '',
        ma_khuon: product.cong_doan.ma_khuon || ''
      } : null,

      // STRUCTURE
      co_thanh_phan_con: product.co_thanh_phan_con || false,
      la_thanh_phan_con: false,
      thanh_pham_me_id: null,

      // OTHER
      ghi_chu: product.ghi_chu || ''
    };

    newItems.push(mainItem);

    // ThÃªm thÃ nh pháº§n con náº¿u cÃ³
    if (product.co_thanh_phan_con && product.thanh_phan_con && product.thanh_phan_con.length > 0) {
      product.thanh_phan_con.forEach((con, idx) => {
        const conKtsx = con.sx_dai || con.sx_rong || con.sx_cao
          ? `${con.sx_dai || con.po_dai}Ã—${con.sx_rong || con.po_rong}${(con.sx_cao || con.po_cao) ? `Ã—${con.sx_cao || con.po_cao}` : ''}`
          : `${con.po_dai}Ã—${con.po_rong}${con.po_cao ? `Ã—${con.po_cao}` : ''}`;

        const conKtpo = `${con.po_dai}Ã—${con.po_rong}${con.po_cao ? `Ã—${con.po_cao}` : ''}`;

        const childItem = {
          id: Date.now() + idx + 1,
          thanh_pham_id: null,

          // BASIC INFO
          ma_hang: con.ma_hang_con,
          ten_san_pham: con.ten,

          // DIMENSIONS
          ktsx: conKtsx,
          ktpo: conKtpo,
          po_dai: con.po_dai,
          po_rong: con.po_rong,
          po_cao: con.po_cao,
          sx_dai: con.sx_dai,
          sx_rong: con.sx_rong,
          sx_cao: con.sx_cao,

          // PRODUCT SPECS
          song: product.song,
          dvt: product.dvt,
          kieu: con.kieu,
          so_mau: con.so_mau,

          // PRICING & QUANTITY
          don_gia: con.don_gia || 0,
          so_luong: '',
          so_luong_per_bo: con.so_luong,
          thanh_tien: 0,
          so_luong_sai_lech: con.so_luong_sai_lech || 0,

          // PRODUCTION
          ma_phim: con.cong_doan?.ma_phim || '',
          ma_khuon: con.cong_doan?.ma_khuon || '',
          cong_doan: con.cong_doan ? {
            in_khuon: con.cong_doan.in_khuon || '',
            be: con.cong_doan.be || '',
            dan: con.cong_doan.dan || '',
            ghep: con.cong_doan.ghep || '',
            ep_keo: con.cong_doan.ep_keo || '',
            ma_phim: con.cong_doan.ma_phim || '',
            ma_khuon: con.cong_doan.ma_khuon || ''
          } : null,

          // STRUCTURE
          la_thanh_phan_con: true,
          thanh_pham_me_id: mainItem.id,

          // OTHER
          ghi_chu: con.ghi_chu || ''
        };

        newItems.push(childItem);
      });
    }

    setForm({
      ...form,
      chi_tiet: [...form.chi_tiet, ...newItems]
    });

    setSelectedProductIdForAdd('');

    setTimeout(() => {
      if (firstQuantityInputRef.current) {
        firstQuantityInputRef.current.focus();
        firstQuantityInputRef.current.select();
      }
    }, 100);
  };

  const handleRemoveItem = (itemId) => {
    const item = form.chi_tiet.find(i => i.id === itemId);
    let idsToRemove = [itemId];

    if (!item.la_thanh_phan_con) {
      const children = form.chi_tiet.filter(i => i.thanh_pham_me_id === itemId);
      idsToRemove = [...idsToRemove, ...children.map(c => c.id)];
    }

    setForm({
      ...form,
      chi_tiet: form.chi_tiet.filter(i => !idsToRemove.includes(i.id))
    });
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    const qty = newQuantity === '' ? '' : parseInt(newQuantity) || 0;

    const updatedItems = form.chi_tiet.map(item => {
      if (item.id === itemId) {
        const thanh_tien = (qty || 0) * item.don_gia;
        return { ...item, so_luong: qty, thanh_tien };
      }

      if (item.la_thanh_phan_con && item.thanh_pham_me_id === itemId) {
        const childQty = (qty || 0) * (item.so_luong_per_bo || 1);
        const thanh_tien = childQty * item.don_gia;
        return { ...item, so_luong: childQty, thanh_tien };
      }

      return item;
    });

    setForm({ ...form, chi_tiet: updatedItems });
  };

  const calculateTotal = () => {
    return form.chi_tiet.reduce((sum, item) => {
      if (!item.la_thanh_phan_con) {
        return sum + item.thanh_tien;
      }
      return sum;
    }, 0);
  };

  const resetForm = () => {
    setForm({
      so_don_hang: '',
      khach_hang_id: '',
      ngay_dat: new Date().toISOString().split('T')[0],
      ngay_giao_du_kien: '',
      chi_tiet: [],
      ghi_chu: ''
    });
    setSelectedProductIdForAdd('');
  };

  const handleSubmit = () => {
    if (!form.so_don_hang || !form.khach_hang_id) {
      alert('Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ thÃ´ng tin!');
      return;
    }

    if (form.chi_tiet.length === 0) {
      alert('Vui lÃ²ng thÃªm Ã­t nháº¥t 1 sáº£n pháº©m!');
      return;
    }

    const newOrder = {
      ...form,
      id: editingOrder ? editingOrder.id : Date.now(),
      tong_tien: calculateTotal()
    };

    if (editingOrder) {
      setOrders(orders.map(o => o.id === editingOrder.id ? newOrder : o));
    } else {
      setOrders([...orders, newOrder]);
    }

    setShowModal(false);
    setEditingOrder(null);
    resetForm();
  };

  const handleEdit = (order) => {
    setEditingOrder(order);
    setForm({
      so_don_hang: order.so_don_hang,
      khach_hang_id: order.khach_hang_id,
      ngay_dat: order.ngay_dat,
      ngay_giao_du_kien: order.ngay_giao_du_kien || '',
      chi_tiet: order.chi_tiet,
      ghi_chu: order.ghi_chu || ''
    });
    setShowModal(true);
    setShowDetailModal(false);
  };

  const handleDelete = (id) => {
    if (window.confirm('XÃ¡c nháº­n xÃ³a Ä‘Æ¡n hÃ ng nÃ y?')) {
      setOrders(orders.filter(o => o.id !== id));
    }
  };

  const filterColumns = [
    { key: 'so_don_hang', label: 'Sá»‘ Ä‘Æ¡n hÃ ng', type: 'text' },
    { key: 'ngay_dat', label: 'NgÃ y Ä‘áº·t', type: 'date' },
    { key: 'tong_tien', label: 'Tá»•ng tiá»n', type: 'range' }
  ];

  const handleFilter = (newFilters) => {
    setFilters(newFilters);
  };

  const excelColumns = [
    { key: 'so_don_hang', label: 'Sá»‘ Ä‘Æ¡n hÃ ng', example: 'DH2512001' },
    { 
      key: 'khach_hang', 
      label: 'MÃ£ khÃ¡ch hÃ ng',
      example: 'ABC',
      formatter: (value, row) => getCustomerCode(row.khach_hang_id)
    },
    { 
      key: 'ngay_dat', 
      label: 'NgÃ y Ä‘áº·t',
      example: '2025-12-25',
      formatter: (value) => new Date(value).toLocaleDateString('vi-VN')
    },
    { 
      key: 'tong_tien', 
      label: 'Tá»•ng tiá»n',
      example: '3219000',
      formatter: (value) => value.toLocaleString('vi-VN')
    }
  ];

  const handleImportOrders = (importedData) => {
    const validated = importedData.filter(row => {
      if (!row.so_don_hang) return false;
      return true;
    });

    const newOrders = validated.map(row => ({
      id: Date.now() + Math.random(),
      ...row,
      chi_tiet: []
    }));

    setOrders([...orders, ...newOrders]);
    alert(`ÄÃ£ import ${newOrders.length} Ä‘Æ¡n hÃ ng!`);
  };

  const filteredOrders = orders.filter(order => {
    if (searchTerm) {
      const search = removeVietnameseTones(searchTerm.toLowerCase());
      const matchOrder = removeVietnameseTones(order.so_don_hang.toLowerCase()).includes(search);
      const matchCustomer = removeVietnameseTones(getCustomerName(order.khach_hang_id).toLowerCase()).includes(search);
      const matchCode = removeVietnameseTones(getCustomerCode(order.khach_hang_id).toLowerCase()).includes(search);
      if (!matchOrder && !matchCustomer && !matchCode) return false;
    }

    if (filters.so_don_hang && !order.so_don_hang.toLowerCase().includes(filters.so_don_hang.toLowerCase())) {
      return false;
    }

    if (filters.ngay_dat && order.ngay_dat !== filters.ngay_dat) {
      return false;
    }

    if (filters.tong_tien_min && order.tong_tien < parseFloat(filters.tong_tien_min)) {
      return false;
    }

    if (filters.tong_tien_max && order.tong_tien > parseFloat(filters.tong_tien_max)) {
      return false;
    }

    return true;
  });


  return (
    <div className="p-6">
      <div className="flex justify-between items-start mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
            <ShoppingCart className="w-8 h-8 text-blue-600" />
            ÄÆ¡n hÃ ng xuáº¥t ({filteredOrders.length})
          </h1>
          <p className="text-gray-600">Quáº£n lÃ½ Ä‘Æ¡n hÃ ng bÃ¡n cho khÃ¡ch hÃ ng</p>
        </div>
        <div className="flex gap-2">
          <FilterBox columns={filterColumns} onFilter={handleFilter} />
          <ExcelActions
            data={filteredOrders}
            columns={excelColumns}
            filename="Don-hang-xuat"
            onImport={handleImportOrders}
            showTemplate={true}
          />
          <button
            onClick={() => {
              const reloaded = loadCustomers();
              setCustomers(reloaded);
              setShowModal(true);
              setEditingOrder(null);
              setForm({
                so_don_hang: generateOrderNumber(),
                khach_hang_id: '',
                ngay_dat: new Date().toISOString().split('T')[0],
                ngay_giao_du_kien: '',
                chi_tiet: [],
                ghi_chu: ''
              });
            }}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
          >
            <Plus className="w-5 h-5" />
            ThÃªm Ä‘Æ¡n hÃ ng
          </button>
        </div>
      </div>

      <div className="mb-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
          <input
            type="text"
            placeholder="TÃ¬m kiáº¿m theo sá»‘ Ä‘Æ¡n hÃ ng, khÃ¡ch hÃ ng..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg"
          />
        </div>
      </div>

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Sá»‘ ÄH</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">MÃ£ KH</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">NgÃ y Ä‘áº·t</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">Tá»•ng tiá»n</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase">Thao tÃ¡c</th>
            </tr>
          </thead>
          <tbody className="divide-y">
            {filteredOrders.map(order => (
              <tr key={order.id} className="hover:bg-gray-50">
                <td className="px-6 py-4">
                  <span className="font-bold text-blue-600">{order.so_don_hang}</span>
                </td>
                <td className="px-6 py-4">
                  <div className="group relative inline-block">
                    <LinkedField
                      type="customer"
                      value={getCustomerCode(order.khach_hang_id)}
                      id={order.khach_hang_id}
                      onClick={(id) => {
                        setSelectedCustomerId(id);
                        setShowCustomerModal(true);
                      }}
                    />
                    <div className="absolute left-0 top-full mt-1 bg-gray-900 text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-10">
                      {getCustomerName(order.khach_hang_id)}
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 text-sm">
                  {new Date(order.ngay_dat).toLocaleDateString('vi-VN')}
                </td>
                <td className="px-6 py-4 text-right font-medium text-green-600">
                  {order.tong_tien.toLocaleString('vi-VN')}Ä‘
                </td>
                <td className="px-6 py-4">
                  <div className="flex items-center justify-center gap-2">
                    <button onClick={() => { setSelectedOrder(order); setShowDetailModal(true); }} className="p-2 text-blue-600 hover:bg-blue-50 rounded" title="Xem chi tiáº¿t">
                      <Eye className="w-4 h-4" />
                    </button>
                    <button onClick={() => handleEdit(order)} className="p-2 text-green-600 hover:bg-green-50 rounded" title="Sá»­a">
                      <Edit className="w-4 h-4" />
                    </button>
                    <button onClick={() => handleDelete(order.id)} className="p-2 text-red-600 hover:bg-red-50 rounded" title="XÃ³a">
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredOrders.length === 0 && (
          <div className="text-center py-12 text-gray-500">
            <ShoppingCart className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <p>ChÆ°a cÃ³ Ä‘Æ¡n hÃ ng nÃ o</p>
          </div>
        )}
      </div>


      {/* Add/Edit Modal - 14 Cá»˜T Äáº¦Y Äá»¦ */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[60] p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-[95vw] max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10 rounded-t-xl">
              <h2 className="text-xl font-bold">
