import React, { useState } from 'react';
import { Upload, File, Trash2, Eye, Loader, Image as ImageIcon } from 'lucide-react';

const FileUpload = ({ 
  label, 
  currentFile, 
  onFileChange, 
  onFileRemove,
  uploadEndpoint = '/api/upload',
  accept = '.pdf,.png,.jpg,.jpeg'
}) => {
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleFileSelect = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Validate file type
    const allowedTypes = ['application/pdf', 'image/png', 'image/jpeg', 'image/jpg'];
    if (!allowedTypes.includes(file.type)) {
      alert('Chỉ chấp nhận file PDF, PNG, JPG!');
      return;
    }

    setUploading(true);
    setUploadProgress(0);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const xhr = new XMLHttpRequest();

      // Progress tracking
      xhr.upload.addEventListener('progress', (e) => {
        if (e.lengthComputable) {
          const percentComplete = (e.loaded / e.total) * 100;
          setUploadProgress(Math.round(percentComplete));
        }
      });

      // Success handler
      xhr.addEventListener('load', () => {
        if (xhr.status === 200) {
          const response = JSON.parse(xhr.responseText);
          onFileChange({
            name: file.name,
            url: response.url,
            size: file.size,
            type: file.type,
            uploaded_at: new Date().toISOString()
          });
          setUploading(false);
        } else {
          throw new Error('Upload failed');
        }
      });

      // Error handler
      xhr.addEventListener('error', () => {
        alert('Lỗi khi upload file!');
        setUploading(false);
      });

      xhr.open('POST', uploadEndpoint);
      xhr.send(formData);

    } catch (error) {
      console.error('Upload error:', error);
      alert('Lỗi khi upload file!');
      setUploading(false);
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  const getFileIcon = (type) => {
    if (type?.includes('pdf')) {
      return <File className="w-5 h-5 text-red-500" />;
    }
    if (type?.includes('image')) {
      return <ImageIcon className="w-5 h-5 text-blue-500" />;
    }
    return <File className="w-5 h-5 text-gray-500" />;
  };

  const isImage = (type) => {
    return type?.includes('image');
  };

  return (
    <div className="space-y-2">
      {label && (
        <label className="block text-sm font-medium text-gray-700">
          {label}
        </label>
      )}

      {!currentFile ? (
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center hover:border-blue-400 transition-colors">
          <input
            type="file"
            accept={accept}
            onChange={handleFileSelect}
            className="hidden"
            id={`file-upload-${label}`}
            disabled={uploading}
          />
          <label
            htmlFor={`file-upload-${label}`}
            className="cursor-pointer flex flex-col items-center"
          >
            {uploading ? (
              <>
                <Loader className="w-8 h-8 text-blue-500 animate-spin mb-2" />
                <p className="text-sm text-gray-600">Đang upload... {uploadProgress}%</p>
                <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                  <div 
                    className="bg-blue-600 h-2 rounded-full transition-all"
                    style={{ width: `${uploadProgress}%` }}
                  />
                </div>
              </>
            ) : (
              <>
                <Upload className="w-8 h-8 text-gray-400 mb-2" />
                <p className="text-sm text-gray-600">
                  Click để chọn file
                </p>
                <p className="text-xs text-gray-400 mt-1">
                  PDF, PNG, JPG - Không giới hạn dung lượng
                </p>
              </>
            )}
          </label>
        </div>
      ) : (
        <div className="border border-gray-300 rounded-lg p-3 bg-gray-50">
          <div className="flex items-start gap-3">
            {/* Preview nếu là ảnh */}
            {isImage(currentFile.type) && currentFile.url && (
              <div className="flex-shrink-0">
                <img 
                  src={currentFile.url} 
                  alt="Preview" 
                  className="w-16 h-16 object-cover rounded border"
                />
              </div>
            )}

            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-1">
                {getFileIcon(currentFile.type)}
                <p className="text-sm font-medium text-gray-900 truncate">
                  {currentFile.name}
                </p>
              </div>
              <p className="text-xs text-gray-500">
                {formatFileSize(currentFile.size)}
              </p>
            </div>

            <div className="flex items-center gap-1 flex-shrink-0">
              <a
                href={currentFile.url}
                target="_blank"
                rel="noopener noreferrer"
                className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                title="Xem file"
              >
                <Eye className="w-4 h-4" />
              </a>
              <button
                onClick={onFileRemove}
                className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                title="Xóa file"
                type="button"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
