import React, { useState } from 'react';
import { ExternalLink, Package, ShoppingCart, User, Users, Box, FileText } from 'lucide-react';

/**
 * LinkedField Component - Click để mở detail từ module khác
 * 
 * @param {String} type - Loại link: 'product', 'order', 'customer', 'material', 'supplier'
 * @param {String} value - Giá trị hiển thị (VD: 'NN01', 'DH001')
 * @param {String|Number} id - ID để mở (optional, nếu khác value)
 * @param {Object} data - Dữ liệu đầy đủ (optional, để hiển thị tooltip)
 * @param {Function} onClick - Custom onClick handler (optional)
 * @param {String} className - Custom className
 * @param {Boolean} showIcon - Hiển thị icon
 * @param {String} mode - 'navigate' hoặc 'modal'
 */

const LinkedField = ({ 
  type = 'product',
  value,
  id,
  data,
  onClick,
  className = '',
  showIcon = true,
  mode = 'modal'
}) => {
  const [showTooltip, setShowTooltip] = useState(false);

  // Icon theo loại
  const getIcon = () => {
    switch (type) {
      case 'product':
        return <Package className="w-3 h-3" />;
      case 'order':
        return <ShoppingCart className="w-3 h-3" />;
      case 'customer':
        return <User className="w-3 h-3" />;
      case 'supplier':
        return <Users className="w-3 h-3" />;
      case 'material':
        return <Box className="w-3 h-3" />;
      case 'document':
        return <FileText className="w-3 h-3" />;
      default:
        return <ExternalLink className="w-3 h-3" />;
    }
  };

  // Màu theo loại
  const getColor = () => {
    switch (type) {
      case 'product':
        return 'text-blue-600 hover:text-blue-800 hover:bg-blue-50';
      case 'order':
        return 'text-green-600 hover:text-green-800 hover:bg-green-50';
      case 'customer':
        return 'text-purple-600 hover:text-purple-800 hover:bg-purple-50';
      case 'supplier':
        return 'text-orange-600 hover:text-orange-800 hover:bg-orange-50';
      case 'material':
        return 'text-teal-600 hover:text-teal-800 hover:bg-teal-50';
      case 'document':
        return 'text-gray-600 hover:text-gray-800 hover:bg-gray-50';
      default:
        return 'text-blue-600 hover:text-blue-800 hover:bg-blue-50';
    }
  };

  // Label theo loại
  const getTypeLabel = () => {
    switch (type) {
      case 'product':
        return 'Thành phẩm';
      case 'order':
        return 'Đơn hàng';
      case 'customer':
        return 'Khách hàng';
      case 'supplier':
        return 'Nhà cung cấp';
      case 'material':
        return 'Nguyên liệu';
      case 'document':
        return 'Tài liệu';
      default:
        return 'Chi tiết';
    }
  };

  // Handle click
  const handleClick = (e) => {
    e.stopPropagation();

    if (onClick) {
      onClick(id || value, data);
    } else {
      // Default behavior - Show alert (sẽ thay bằng modal sau)
      alert(`Mở ${getTypeLabel()}: ${value}\nID: ${id || value}\n\n(Sẽ hiển thị modal chi tiết ở đây)`);
    }
  };

  // Tooltip content
  const renderTooltip = () => {
    if (!data) return null;

    return (
      <div className="absolute z-50 bottom-full left-0 mb-2 p-3 bg-gray-900 text-white text-xs rounded-lg shadow-xl min-w-64 max-w-sm">
        <div className="space-y-1">
          <p className="font-bold text-sm">{getTypeLabel()}: {value}</p>
          {Object.entries(data).slice(0, 5).map(([key, val]) => (
            <p key={key} className="text-gray-300">
              <span className="font-medium">{key}:</span> {String(val).substring(0, 50)}
            </p>
          ))}
        </div>
        {/* Arrow */}
        <div className="absolute top-full left-4 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900" />
      </div>
    );
  };

  if (!value) return <span className="text-gray-400">-</span>;

  return (
    <div className="relative inline-block">
      <button
        onClick={handleClick}
        onMouseEnter={() => data && setShowTooltip(true)}
        onMouseLeave={() => setShowTooltip(false)}
        className={`inline-flex items-center gap-1 font-medium underline decoration-dotted cursor-pointer transition-all rounded px-1 py-0.5 ${getColor()} ${className}`}
        title={`Click để xem ${getTypeLabel()}: ${value}`}
      >
        {showIcon && getIcon()}
        <span>{value}</span>
        <ExternalLink className="w-3 h-3 opacity-50" />
      </button>

      {/* Tooltip */}
      {showTooltip && renderTooltip()}
    </div>
  );
};

export default LinkedField;

/**
 * USAGE EXAMPLES:
 * 
 * 1. Trong KHSX - Link đến Thành phẩm:
 * <LinkedField 
 *   type="product" 
 *   value="NN01" 
 *   id={product.id}
 *   data={product}
 * />
 * 
 * 2. Trong Giao hàng - Link đến Đơn hàng:
 * <LinkedField 
 *   type="order" 
 *   value="DH2512001" 
 *   id={order.id}
 *   onClick={(id) => navigate(`/orders/${id}`)}
 * />
 * 
 * 3. Trong Sổ quỹ - Link đến Khách hàng:
 * <LinkedField 
 *   type="customer" 
 *   value="KH001" 
 *   id={customer.id}
 *   data={customer}
 * />
 */
