import React, { useState, useEffect, useRef } from 'react';
import { Plus, Search, Edit, Trash2, Eye, Download, ShoppingCart, Package, X } from 'lucide-react';
import * as XLSX from 'xlsx';
import TextInput from '../components/TextInput';
import TextArea from '../components/TextArea';
import LinkedField from '../components/LinkedField';
import CustomerDetailModal from '../components/CustomerDetailModal';
import ProductDetailModal from '../components/ProductDetailModal';
import FilterBox from '../components/FilterBox';
import ExcelActions from '../components/ExcelActions';

const removeVietnameseTones = (str) => {
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/đ/g, 'd')
    .replace(/Đ/g, 'D');
};

function SalesOrder() {
  const loadOrders = () => {
    const saved = localStorage.getItem('salesOrders');
    return saved ? JSON.parse(saved) : [];
  };

  const loadProducts = () => {
    const saved = localStorage.getItem('products');
    return saved ? JSON.parse(saved) : [];
  };

  const loadCustomers = () => {
    const saved = localStorage.getItem('customers');
    return saved ? JSON.parse(saved) : [];
  };

  const [orders, setOrders] = useState(loadOrders);
  const [products, setProducts] = useState(loadProducts);
  const [customers, setCustomers] = useState(loadCustomers);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [editingOrder, setEditingOrder] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  // Modal states
  const [showCustomerModal, setShowCustomerModal] = useState(false);
  const [selectedCustomerId, setSelectedCustomerId] = useState(null);
  const [showProductModal, setShowProductModal] = useState(false);
  const [selectedProductIdModal, setSelectedProductIdModal] = useState(null);

  const [form, setForm] = useState({
    so_don_hang: '',
    khach_hang_id: '',
    ngay_dat: new Date().toISOString().split('T')[0],
    ngay_giao_du_kien: '',
    chi_tiet: [],
    ghi_chu: ''
  });

  const [selectedProductIdForAdd, setSelectedProductIdForAdd] = useState('');
  const firstQuantityInputRef = useRef(null);

  // ✅ RELOAD customers khi mở modal
  useEffect(() => {
    if (showModal) {
      const updatedCustomers = loadCustomers();
      setCustomers(updatedCustomers);
    }
  }, [showModal]);

  useEffect(() => {
    localStorage.setItem('salesOrders', JSON.stringify(orders));
  }, [orders]);

  // Helper functions
  const getCustomerName = (khach_hang_id) => {
    const customer = customers.find(c => c.id === khach_hang_id);
    return customer?.ten_cong_ty || 'N/A';
  };

  const getCustomerCode = (khach_hang_id) => {
    const customer = customers.find(c => c.id === khach_hang_id);
    return customer?.ma_khach_hang || 'N/A';
  };

  const generateOrderNumber = () => {
    const today = new Date();
    const year = today.getFullYear().toString().slice(-2);
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const existingOrders = orders.filter(o => 
      o.so_don_hang.startsWith(`DH${year}${month}`)
    );
    const nextNum = String(existingOrders.length + 1).padStart(3, '0');
    return `DH${year}${month}${nextNum}`;
  };

  const handleAddProduct = () => {
    if (!selectedProductIdForAdd) {
      alert('Vui lòng chọn thành phẩm!');
      return;
    }

    const product = products.find(p => p.id === parseInt(selectedProductIdForAdd));
    if (!product) return;

    const newItems = [];
    const ktsx = product.sx_dai || product.sx_rong || product.sx_cao
      ? `${product.sx_dai || product.po_dai}×${product.sx_rong || product.po_rong}${(product.sx_cao || product.po_cao) ? `×${product.sx_cao || product.po_cao}` : ''}`
      : `${product.po_dai}×${product.po_rong}${product.po_cao ? `×${product.po_cao}` : ''}`;

    const mainItem = {
      id: Date.now(),
      thanh_pham_id: product.id,
      ma_hang: product.ma_hang,
      ten_san_pham: product.ten_san_pham,
      ktsx: ktsx,
      song: product.song,
      dvt: product.dvt,
      so_luong: '',
      don_gia: product.don_gia || 0,
      thanh_tien: 0,
      ma_phim: product.cong_doan?.ma_phim || '',
      ma_khuon: product.cong_doan?.ma_khuon || '',
      so_luong_sai_lech: product.so_luong_sai_lech || 0,
      kieu: product.kieu || '',
      so_mau: product.so_mau || '',
      cong_doan: { ...product.cong_doan },
      la_thanh_phan_con: false,
      thanh_pham_me_id: null
    };

    newItems.push(mainItem);

    if (product.co_thanh_phan_con && product.thanh_phan_con && product.thanh_phan_con.length > 0) {
      product.thanh_phan_con.forEach((con, idx) => {
        const conKtsx = con.sx_dai || con.sx_rong || con.sx_cao
          ? `${con.sx_dai || con.po_dai}×${con.sx_rong || con.po_rong}${(con.sx_cao || con.po_cao) ? `×${con.sx_cao || con.po_cao}` : ''}`
          : `${con.po_dai}×${con.po_rong}${con.po_cao ? `×${con.po_cao}` : ''}`;

        const childItem = {
          id: Date.now() + idx + 1,
          thanh_pham_id: null,
          ma_hang: con.ma_hang_con,
          ten_san_pham: con.ten,
          ktsx: conKtsx,
          song: product.song,
          dvt: product.dvt,
          so_luong: '',
          so_luong_per_bo: con.so_luong,
          don_gia: con.don_gia || 0,
          thanh_tien: 0,
          ma_phim: con.cong_doan?.ma_phim || '',
          ma_khuon: con.cong_doan?.ma_khuon || '',
          so_luong_sai_lech: con.so_luong_sai_lech || 0,
          kieu: con.kieu || '',
          so_mau: con.so_mau || '',
          cong_doan: { ...con.cong_doan },
          la_thanh_phan_con: true,
          thanh_pham_me_id: mainItem.id
        };

        newItems.push(childItem);
      });
    }

    setForm({
      ...form,
      chi_tiet: [...form.chi_tiet, ...newItems]
    });

    setSelectedProductIdForAdd('');

    setTimeout(() => {
      if (firstQuantityInputRef.current) {
        firstQuantityInputRef.current.focus();
        firstQuantityInputRef.current.select();
      }
    }, 100);
  };

  const handleRemoveItem = (itemId) => {
    const item = form.chi_tiet.find(i => i.id === itemId);
    let idsToRemove = [itemId];

    if (!item.la_thanh_phan_con) {
      const children = form.chi_tiet.filter(i => i.thanh_pham_me_id === itemId);
      idsToRemove = [...idsToRemove, ...children.map(c => c.id)];
    }

    setForm({
      ...form,
      chi_tiet: form.chi_tiet.filter(i => !idsToRemove.includes(i.id))
    });
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    const qty = newQuantity === '' ? '' : parseInt(newQuantity) || 0;

    const updatedItems = form.chi_tiet.map(item => {
      if (item.id === itemId) {
        const thanh_tien = (qty || 0) * item.don_gia;
        return { ...item, so_luong: qty, thanh_tien };
      }

      if (item.la_thanh_phan_con && item.thanh_pham_me_id === itemId) {
        const childQty = (qty || 0) * (item.so_luong_per_bo || 1);
        const thanh_tien = childQty * item.don_gia;
        return { ...item, so_luong: childQty, thanh_tien };
      }

      return item;
    });

    setForm({ ...form, chi_tiet: updatedItems });
  };

  const calculateTotal = () => {
    return form.chi_tiet.reduce((sum, item) => {
      if (!item.la_thanh_phan_con) {
        return sum + item.thanh_tien;
      }
      return sum;
    }, 0);
  };

  const resetForm = () => {
    setForm({
      so_don_hang: '',
      khach_hang_id: '',
      ngay_dat: new Date().toISOString().split('T')[0],
      ngay_giao_du_kien: '',
      chi_tiet: [],
      ghi_chu: ''
    });
    setSelectedProductIdForAdd('');
  };

  const handleSubmit = () => {
    if (!form.so_don_hang || !form.khach_hang_id) {
      alert('Vui lòng nhập đầy đủ thông tin!');
      return;
    }

    if (form.chi_tiet.length === 0) {
      alert('Vui lòng thêm ít nhất 1 sản phẩm!');
      return;
    }

    const newOrder = {
      ...form,
      id: editingOrder ? editingOrder.id : Date.now(),
      tong_tien: calculateTotal()
    };

    if (editingOrder) {
      setOrders(orders.map(o => o.id === editingOrder.id ? newOrder : o));
    } else {
      setOrders([...orders, newOrder]);
    }

    setShowModal(false);
    setEditingOrder(null);
    resetForm();
  };

  const handleEdit = (order) => {
    setEditingOrder(order);
    setForm({
      so_don_hang: order.so_don_hang,
      khach_hang_id: order.khach_hang_id,
      ngay_dat: order.ngay_dat,
      ngay_giao_du_kien: order.ngay_giao_du_kien || '',
      chi_tiet: order.chi_tiet,
      ghi_chu: order.ghi_chu || ''
    });
    setShowModal(true);
    setShowDetailModal(false);
  };

  const handleDelete = (id) => {
    if (window.confirm('Xác nhận xóa đơn hàng này?')) {
      setOrders(orders.filter(o => o.id !== id));
    }
  };

  const filterColumns = [
    { key: 'so_don_hang', label: 'Số đơn hàng', type: 'text' },
    { key: 'ngay_dat', label: 'Ngày đặt', type: 'date' },
    { key: 'tong_tien', label: 'Tổng tiền', type: 'range' }
  ];

  const handleFilter = (newFilters) => {
    setFilters(newFilters);
  };

  const excelColumns = [
    { key: 'so_don_hang', label: 'Số đơn hàng', example: 'DH2512001' },
    { 
      key: 'khach_hang', 
      label: 'Mã khách hàng',
      example: 'ABC',
      formatter: (value, row) => getCustomerCode(row.khach_hang_id)
    },
    { 
      key: 'ngay_dat', 
      label: 'Ngày đặt',
      example: '2025-12-25',
      formatter: (value) => new Date(value).toLocaleDateString('vi-VN')
    },
    { 
      key: 'tong_tien', 
      label: 'Tổng tiền',
      example: '3219000',
      formatter: (value) => value.toLocaleString('vi-VN')
    }
  ];

  const handleImportOrders = (importedData) => {
    const validated = importedData.filter(row => {
      if (!row.so_don_hang) return false;
      return true;
    });

    const newOrders = validated.map(row => ({
      id: Date.now() + Math.random(),
      ...row,
      chi_tiet: []
    }));

    setOrders([...orders, ...newOrders]);
    alert(`Đã import ${newOrders.length} đơn hàng!`);
  };

  const filteredOrders = orders.filter(order => {
    if (searchTerm) {
      const search = removeVietnameseTones(searchTerm.toLowerCase());
      const matchOrder = removeVietnameseTones(order.so_don_hang.toLowerCase()).includes(search);
      const matchCustomer = removeVietnameseTones(getCustomerName(order.khach_hang_id).toLowerCase()).includes(search);
      const matchCode = removeVietnameseTones(getCustomerCode(order.khach_hang_id).toLowerCase()).includes(search);
      if (!matchOrder && !matchCustomer && !matchCode) return false;
    }

    if (filters.so_don_hang && !order.so_don_hang.toLowerCase().includes(filters.so_don_hang.toLowerCase())) {
      return false;
    }

    if (filters.ngay_dat && order.ngay_dat !== filters.ngay_dat) {
      return false;
    }

    if (filters.tong_tien_min && order.tong_tien < parseFloat(filters.tong_tien_min)) {
      return false;
    }

    if (filters.tong_tien_max && order.tong_tien > parseFloat(filters.tong_tien_max)) {
      return false;
    }

    return true;
  });


  return (
    <div className="p-6">
      <div className="flex justify-between items-start mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
            <ShoppingCart className="w-8 h-8 text-blue-600" />
            Đơn hàng xuất ({filteredOrders.length})
          </h1>
          <p className="text-gray-600">Quản lý đơn hàng bán cho khách hàng</p>
        </div>
        <div className="flex gap-2">
          <FilterBox columns={filterColumns} onFilter={handleFilter} />
          <ExcelActions
            data={filteredOrders}
            columns={excelColumns}
            filename="Don-hang-xuat"
            onImport={handleImportOrders}
            showTemplate={true}
          />
          <button
            onClick={() => {
              // ✅ RELOAD customers khi mở modal
              setCustomers(loadCustomers());
              setShowModal(true);
              setEditingOrder(null);
              setForm({
                ...form,
                so_don_hang: generateOrderNumber()
              });
            }}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
          >
            <Plus className="w-5 h-5" />
            Thêm đơn hàng
          </button>
        </div>
      </div>

      <div className="mb-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
          <input
            type="text"
            placeholder="Tìm kiếm theo số đơn hàng, khách hàng..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg"
          />
        </div>
      </div>

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Số ĐH</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Mã KH</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Ngày đặt</th>
              <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase">Tổng tiền</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase">Thao tác</th>
            </tr>
          </thead>
          <tbody className="divide-y">
            {filteredOrders.map(order => (
              <tr key={order.id} className="hover:bg-gray-50">
                <td className="px-6 py-4">
                  <span className="font-bold text-blue-600">{order.so_don_hang}</span>
                </td>
                <td className="px-6 py-4">
                  <div className="group relative inline-block">
                    <LinkedField
                      type="customer"
                      value={getCustomerCode(order.khach_hang_id)}
                      id={order.khach_hang_id}
                      onClick={(id) => {
                        setSelectedCustomerId(id);
                        setShowCustomerModal(true);
                      }}
                    />
                    <div className="absolute left-0 top-full mt-1 bg-gray-900 text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-10">
                      {getCustomerName(order.khach_hang_id)}
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 text-sm">
                  {new Date(order.ngay_dat).toLocaleDateString('vi-VN')}
                </td>
                <td className="px-6 py-4 text-right font-medium text-green-600">
                  {order.tong_tien.toLocaleString('vi-VN')}đ
                </td>
                <td className="px-6 py-4">
                  <div className="flex items-center justify-center gap-2">
                    <button onClick={() => { setSelectedOrder(order); setShowDetailModal(true); }} className="p-2 text-blue-600 hover:bg-blue-50 rounded" title="Xem chi tiết">
                      <Eye className="w-4 h-4" />
                    </button>
                    <button onClick={() => handleEdit(order)} className="p-2 text-green-600 hover:bg-green-50 rounded" title="Sửa">
                      <Edit className="w-4 h-4" />
                    </button>
                    <button onClick={() => handleDelete(order.id)} className="p-2 text-red-600 hover:bg-red-50 rounded" title="Xóa">
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredOrders.length === 0 && (
          <div className="text-center py-12 text-gray-500">
            <ShoppingCart className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <p>Chưa có đơn hàng nào</p>
          </div>
        )}
      </div>


      {/* Add/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[60] p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-6xl max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10 rounded-t-xl">
              <h2 className="text-xl font-bold">
                {editingOrder ? 'Sửa đơn hàng' : 'Thêm đơn hàng mới'}
              </h2>
            </div>

            <div className="p-6 space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Số đơn hàng *</label>
                  <TextInput
                    value={form.so_don_hang}
                    onChange={(value) => setForm({ ...form, so_don_hang: value.toUpperCase() })}
                    placeholder="DH2512001"
                    className="uppercase"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Mã khách hàng *</label>
                  <select
                    value={form.khach_hang_id}
                    onChange={(e) => setForm({ ...form, khach_hang_id: parseInt(e.target.value) })}
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">Chọn khách hàng</option>
                    {customers.map(c => (
                      <option key={c.id} value={c.id}>
                        {c.ma_khach_hang}
                      </option>
                    ))}
                  </select>
                  {form.khach_hang_id && (
                    <p className="text-sm text-gray-600 mt-1 flex items-center gap-1">
                      <span className="text-green-600">✓</span>
                      {getCustomerName(form.khach_hang_id)}
                    </p>
                  )}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Ngày đặt *</label>
                  <input
                    type="date"
                    value={form.ngay_dat}
                    onChange={(e) => setForm({ ...form, ngay_dat: e.target.value })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Ngày giao dự kiến</label>
                  <input
                    type="date"
                    value={form.ngay_giao_du_kien}
                    onChange={(e) => setForm({ ...form, ngay_giao_du_kien: e.target.value })}
                    className="w-full px-4 py-2 border rounded-lg"
                  />
                </div>
              </div>

              <div className="border-t pt-4">
                <label className="block text-sm font-medium mb-2">Chọn thành phẩm để thêm</label>
                <div className="flex gap-2">
                  <select
                    value={selectedProductIdForAdd}
                    onChange={(e) => setSelectedProductIdForAdd(e.target.value)}
                    className="flex-1 px-4 py-2 border rounded-lg"
                  >
                    <option value="">Chọn thành phẩm để thêm...</option>
                    {products.map(p => (
                      <option key={p.id} value={p.id}>
                        {p.ma_hang} - {p.ten_san_pham}
                      </option>
                    ))}
                  </select>
                  <button
                    onClick={handleAddProduct}
                    className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2"
                  >
                    <Plus className="w-4 h-4" />
                    Thêm
                  </button>
                </div>
              </div>

              <div className="border-t pt-4">
                <h3 className="font-bold mb-3">Chi tiết đơn hàng</h3>
                {form.chi_tiet.length > 0 ? (
                  <div className="border rounded-lg overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-3 py-2 text-left text-xs font-medium text-gray-500">Mã hàng</th>
                          <th className="px-3 py-2 text-left text-xs font-medium text-gray-500">Tên SP</th>
                          <th className="px-3 py-2 text-center text-xs font-medium text-gray-500">KTSX</th>
                          <th className="px-3 py-2 text-right text-xs font-medium text-gray-500">SL</th>
                          <th className="px-3 py-2 text-right text-xs font-medium text-gray-500">Đơn giá</th>
                          <th className="px-3 py-2 text-right text-xs font-medium text-gray-500">Thành tiền</th>
                          <th className="px-3 py-2 text-center text-xs font-medium text-gray-500"></th>
                        </tr>
                      </thead>
                      <tbody className="divide-y">
                        {form.chi_tiet.map((item, index) => (
                          <tr key={item.id} className={item.la_thanh_phan_con ? 'bg-purple-50' : ''}>
                            <td className="px-3 py-2 text-sm">
                              <span className={`font-bold ${item.la_thanh_phan_con ? 'text-purple-600 pl-4' : 'text-blue-600'}`}>
                                {item.la_thanh_phan_con && '↳ '}
                                {item.ma_hang}
                              </span>
                            </td>
                            <td className="px-3 py-2 text-sm">{item.ten_san_pham}</td>
                            <td className="px-3 py-2 text-xs text-center">
                              <span className="bg-gray-100 px-2 py-1 rounded">{item.ktsx}</span>
                            </td>
                            <td className="px-3 py-2">
                              {!item.la_thanh_phan_con ? (
                                <input
                                  ref={index === 0 ? firstQuantityInputRef : null}
                                  type="number"
                                  value={item.so_luong}
                                  onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                                  placeholder="0"
                                  className="w-20 px-2 py-1 border rounded text-right text-sm"
                                />
                              ) : (
                                <span className="text-sm text-right block pr-2 text-purple-600">
                                  {item.so_luong || '-'}
                                </span>
                              )}
                            </td>
                            <td className="px-3 py-2 text-right text-sm">{item.don_gia.toLocaleString('vi-VN')}</td>
                            <td className="px-3 py-2 text-right text-sm font-medium text-green-600">
                              {item.thanh_tien.toLocaleString('vi-VN')}đ
                            </td>
                            <td className="px-3 py-2 text-center">
                              {!item.la_thanh_phan_con && (
                                <button onClick={() => handleRemoveItem(item.id)} className="p-1 text-red-600 hover:bg-red-50 rounded">
                                  <Trash2 className="w-4 h-4" />
                                </button>
                              )}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                      <tfoot className="bg-gray-50">
                        <tr>
                          <td colSpan="5" className="px-3 py-3 text-right font-bold text-gray-900">TỔNG TIỀN:</td>
                          <td className="px-3 py-3 text-right text-lg font-bold text-green-600">
                            {calculateTotal().toLocaleString('vi-VN')}đ
                          </td>
                          <td></td>
                        </tr>
                      </tfoot>
                    </table>
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500 bg-white rounded-lg border border-dashed">
                    <Package className="w-12 h-12 mx-auto mb-2 text-gray-400" />
                    <p>Chưa có sản phẩm nào. Chọn thành phẩm bên trên để thêm.</p>
                  </div>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Ghi chú</label>
                <TextArea
                  value={form.ghi_chu}
                  onChange={(value) => setForm({ ...form, ghi_chu: value })}
                  placeholder="Ghi chú đơn hàng..."
                  rows="2"
                />
              </div>
            </div>

            <div className="border-t px-6 py-4 flex justify-end gap-2 sticky bottom-0 bg-white rounded-b-xl">
              <button onClick={() => { setShowModal(false); setEditingOrder(null); resetForm(); }} className="px-4 py-2 border rounded-lg hover:bg-gray-50">
                Hủy
              </button>
              <button onClick={handleSubmit} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                {editingOrder ? 'Cập nhật' : 'Thêm'}
              </button>
            </div>
          </div>
        </div>
      )}


      {/* Detail Modal - HIỂN THỊ ĐẦY ĐỦ */}
      {showDetailModal && selectedOrder && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-7xl max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b px-6 py-4 z-10 rounded-t-xl flex items-center justify-between">
              <h2 className="text-xl font-bold">Chi tiết đơn hàng: {selectedOrder.so_don_hang}</h2>
              <button onClick={() => { setShowDetailModal(false); setSelectedOrder(null); }} className="p-2 hover:bg-gray-100 rounded-lg">
                <X className="w-5 h-5" />
              </button>
            </div>
            <div className="p-6 space-y-6">
              <div className="grid grid-cols-3 gap-4">
                <div>
                  <p className="text-sm text-gray-600">Số đơn hàng</p>
                  <p className="font-bold text-blue-600">{selectedOrder.so_don_hang}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Mã khách hàng</p>
                  <LinkedField
                    type="customer"
                    value={getCustomerCode(selectedOrder.khach_hang_id)}
                    id={selectedOrder.khach_hang_id}
                    onClick={(id) => {
                      setSelectedCustomerId(id);
                      setShowCustomerModal(true);
                    }}
                    showIcon={false}
                  />
                  <p className="text-xs text-gray-600 mt-1">{getCustomerName(selectedOrder.khach_hang_id)}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Ngày đặt</p>
                  <p className="font-medium">{new Date(selectedOrder.ngay_dat).toLocaleDateString('vi-VN')}</p>
                </div>
              </div>

              <div>
                <h3 className="font-bold mb-3">Chi tiết sản phẩm</h3>
                <div className="border rounded-lg overflow-x-auto">
                  <table className="w-full text-sm">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-3 py-2 text-left text-xs font-medium text-gray-500">Mã hàng</th>
                        <th className="px-3 py-2 text-left text-xs font-medium text-gray-500">Tên SP</th>
                        <th className="px-3 py-2 text-center text-xs font-medium text-gray-500">KTSX</th>
                        <th className="px-3 py-2 text-center text-xs font-medium text-gray-500">Sóng</th>
                        <th className="px-3 py-2 text-right text-xs font-medium text-gray-500">SL</th>
                        <th className="px-3 py-2 text-right text-xs font-medium text-gray-500">Đơn giá</th>
                        <th className="px-3 py-2 text-right text-xs font-medium text-gray-500">Thành tiền</th>
                        <th className="px-3 py-2 text-center text-xs font-medium text-gray-500">Mã phim</th>
                        <th className="px-3 py-2 text-center text-xs font-medium text-gray-500">Mã khuôn</th>
                        <th className="px-3 py-2 text-center text-xs font-medium text-gray-500">Sai lệch</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y">
                      {selectedOrder.chi_tiet.map(item => (
                        <tr key={item.id} className={item.la_thanh_phan_con ? 'bg-purple-50' : ''}>
                          <td className="px-3 py-2">
                            <LinkedField
                              type="product"
                              value={item.ma_hang}
                              id={item.thanh_pham_id}
                              onClick={(id) => {
                                setSelectedProductIdModal(id || item.ma_hang);
                                setShowProductModal(true);
                              }}
                              className={item.la_thanh_phan_con ? 'text-purple-600' : 'text-blue-600'}
                            >
                              {item.la_thanh_phan_con && '↳ '}
                              {item.ma_hang}
                            </LinkedField>
                          </td>
                          <td className="px-3 py-2">{item.ten_san_pham}</td>
                          <td className="px-3 py-2 text-center">
                            <span className="bg-gray-100 px-2 py-1 rounded text-xs">{item.ktsx}</span>
                          </td>
                          <td className="px-3 py-2 text-center">
                            <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded text-xs font-medium">
                              {item.song || '-'}
                            </span>
                          </td>
                          <td className="px-3 py-2 text-right font-medium">{item.so_luong || 0}</td>
                          <td className="px-3 py-2 text-right">{item.don_gia.toLocaleString('vi-VN')}</td>
                          <td className="px-3 py-2 text-right font-medium text-green-600">
                            {item.thanh_tien.toLocaleString('vi-VN')}đ
                          </td>
                          <td className="px-3 py-2 text-center">
                            {item.ma_phim ? (
                              <span className="bg-purple-100 text-purple-700 px-2 py-1 rounded text-xs">
                                {item.ma_phim}
                              </span>
                            ) : '-'}
                          </td>
                          <td className="px-3 py-2 text-center">
                            {item.ma_khuon ? (
                              <span className="bg-indigo-100 text-indigo-700 px-2 py-1 rounded text-xs">
                                {item.ma_khuon}
                              </span>
                            ) : '-'}
                          </td>
                          <td className="px-3 py-2 text-center">
                            {item.so_luong_sai_lech ? (
                              <span className="bg-orange-100 text-orange-700 px-2 py-1 rounded text-xs font-medium">
                                ±{item.so_luong_sai_lech}%
                              </span>
                            ) : '-'}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                    <tfoot className="bg-gray-50">
                      <tr>
                        <td colSpan="6" className="px-3 py-3 text-right font-bold">TỔNG TIỀN:</td>
                        <td colSpan="4" className="px-3 py-3 text-right text-lg font-bold text-green-600">
                          {selectedOrder.tong_tien.toLocaleString('vi-VN')}đ
                        </td>
                      </tr>
                    </tfoot>
                  </table>
                </div>
              </div>

              {selectedOrder.ghi_chu && (
                <div>
                  <p className="text-sm text-gray-600">Ghi chú</p>
                  <p className="font-medium">{selectedOrder.ghi_chu}</p>
                </div>
              )}
            </div>
            <div className="border-t px-6 py-4 flex justify-end gap-2">
              <button onClick={() => handleEdit(selectedOrder)} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2">
                <Edit className="w-4 h-4" />Sửa
              </button>
              <button onClick={() => { setShowDetailModal(false); setSelectedOrder(null); }} className="px-4 py-2 border rounded-lg hover:bg-gray-50">
                Đóng
              </button>
            </div>
          </div>
        </div>
      )}

      <CustomerDetailModal
        customerId={selectedCustomerId}
        isOpen={showCustomerModal}
        onClose={() => {
          setShowCustomerModal(false);
          setSelectedCustomerId(null);
        }}
        onSave={() => {
          window.location.reload();
        }}
      />

      <ProductDetailModal
        productId={selectedProductIdModal}
        isOpen={showProductModal}
        onClose={() => {
          setShowProductModal(false);
          setSelectedProductIdModal(null);
        }}
        onSave={() => {
          window.location.reload();
        }}
      />

    </div>
  );
}

export default SalesOrder;
